CREATE DEFINER=`root`@`localhost` FUNCTION `FC_CM_GET_LICV2_CD_NM`(`_CD_ID` VARCHAR(30)) RETURNS varchar(256) CHARSET utf8mb3 COLLATE utf8mb3_general_ci
BEGIN
    DECLARE _CD_NM VARCHAR(256);

    IF LENGTH(_CD_ID) = 1 THEN
        CASE _CD_ID
            WHEN 'M' THEN SELECT CD_NM INTO _CD_NM FROM CD_INFO WHERE CD_GROUP_ID = 2098 AND CD_ID = 'MCH';
            WHEN 'A' THEN SELECT CD_NM INTO _CD_NM FROM CD_INFO WHERE CD_GROUP_ID = 2098 AND CD_ID = 'APC';
            WHEN 'I' THEN SELECT CD_NM INTO _CD_NM FROM CD_INFO WHERE CD_GROUP_ID = 2098 AND CD_ID = 'IPT';
            WHEN 'S' THEN SELECT CD_NM INTO _CD_NM FROM CD_INFO WHERE CD_GROUP_ID = 2098 AND CD_ID = 'SMS';
            WHEN 'F' THEN SELECT CD_NM INTO _CD_NM FROM CD_INFO WHERE CD_GROUP_ID = 2098 AND CD_ID = 'FMS';
            WHEN 'T' THEN SELECT CD_NM INTO _CD_NM FROM CD_INFO WHERE CD_GROUP_ID = 2098 AND CD_ID = 'TMS';
            WHEN 'X' THEN SELECT CD_NM INTO _CD_NM FROM CD_INFO WHERE CD_GROUP_ID = 2098 AND CD_ID = 'AI';
            WHEN 'C' THEN SET _CD_NM = 'CCTV';
            ELSE SET _CD_NM = NULL;
        END CASE;
    ELSE
        SET _CD_NM = _CD_ID;
    END IF;

    IF LENGTH(_CD_ID) > 1 THEN
        CASE _CD_ID
            WHEN 'MCH' THEN SET _CD_NM = 'M';
            WHEN 'AP'  THEN SET _CD_NM = 'A';
            WHEN 'IPT' THEN SET _CD_NM = 'I';
            WHEN 'CCTV' THEN SET _CD_NM = 'C';
            WHEN 'SMS' THEN SET _CD_NM = 'S';
            WHEN 'FMS' THEN SET _CD_NM = 'F';
            WHEN 'TMS' THEN SET _CD_NM = 'T';
            WHEN 'AI' THEN SET _CD_NM = 'X';
            ELSE SET _CD_NM = NULL;
        END CASE;
    END IF;

    RETURN _CD_NM; 
END ;;
