CREATE DEFINER=`root`@`localhost` FUNCTION `FC_NMS_GET_EVENT_TIME`(_FIRST_OCCUR_DT DATETIME,
_days VARCHAR(30),
_hours VARCHAR(30),
_minutes VARCHAR(30),
_seconds VARCHAR(30)
) RETURNS varchar(40) CHARSET utf8mb3 COLLATE utf8mb3_general_ci
    DETERMINISTIC
BEGIN
    DECLARE _OCCUR_DT VARCHAR(40);
    DECLARE days INT;
    DECLARE hours INT;
    DECLARE minutes INT;
    DECLARE seconds INT;
    
    SET _OCCUR_DT := '';
    
    SELECT TIMESTAMPDIFF(DAY, _FIRST_OCCUR_DT,NOW()) INTO days;
    IF days <> 0 THEN
       SET _OCCUR_DT = CONCAT(days, _days);
    END IF;
    
    SELECT TIMESTAMPDIFF(hour, _FIRST_OCCUR_DT, NOW()) mod 24       INTO hours;
    IF hours <> 0 THEN
       SET _OCCUR_DT = CONCAT(_OCCUR_DT, ' ', hours, _hours);
    END IF;
    
    SELECT TIMESTAMPDIFF(minute, _FIRST_OCCUR_DT, NOW()) mod 60       INTO minutes;
    IF minutes <> 0 THEN
       SET _OCCUR_DT = CONCAT(_OCCUR_DT, ' ', minutes, _minutes);
    END IF;
    
    SELECT TIMESTAMPDIFF(second, _FIRST_OCCUR_DT, NOW()) mod 60       INTO seconds;
    IF seconds <> 0 THEN
       SET _OCCUR_DT = CONCAT(_OCCUR_DT, ' ', seconds, _seconds);
    END IF;
    
RETURN _OCCUR_DT;
END ;;
