CREATE DEFINER=`root`@`localhost` FUNCTION `FC_NMS_GET_NETMASK`(_IP VARCHAR(50),  
_NETMASK VARCHAR(50)  
) RETURNS varchar(50) CHARSET utf8mb3 COLLATE utf8mb3_general_ci
    DETERMINISTIC
BEGIN
  
    
  SET @DEST_IP = _IP;
  SET @NET_MASK = _NETMASK;
  SET @BIT = '';
  SET @RESULT = '';

  SET @IP1 = CAST(SUBSTRING_INDEX(@DEST_IP, '.', 1) AS SIGNED);
  SET @IP2 = CAST(SUBSTRING_INDEX(SUBSTRING_INDEX(@DEST_IP, '.', 2), '.', -1) AS SIGNED);
  SET @IP3 = CAST(SUBSTRING_INDEX(SUBSTRING_INDEX(@DEST_IP, '.', 3), '.', -1) AS SIGNED);
  SET @IP4 = CAST(SUBSTRING_INDEX(SUBSTRING_INDEX(@DEST_IP, '.', 4), '.', -1) AS SIGNED);

  SET @M1 = CAST(SUBSTRING_INDEX(@NET_MASK, '.', 1) AS SIGNED);
  SET @M2 = CAST(SUBSTRING_INDEX(SUBSTRING_INDEX(@NET_MASK, '.', 2), '.', -1) AS SIGNED);
  SET @M3 = CAST(SUBSTRING_INDEX(SUBSTRING_INDEX(@NET_MASK, '.', 3), '.', -1) AS SIGNED);
  SET @M4 = CAST(SUBSTRING_INDEX(SUBSTRING_INDEX(@NET_MASK, '.', 4), '.', -1) AS SIGNED);
  
  
  IF @M1 = 255 AND @M2 = 255 AND @M3 = 255 THEN

    IF @M4 = 0 THEN SET @BIT = '/24';
    ELSEIF @M4 = 128 THEN SET @BIT = '/25';
    ELSEIF @M4 = 192 THEN SET @BIT = '/26';
    ELSEIF @M4 = 224 THEN SET @BIT = '/27';
    ELSEIF @M4 = 240 THEN SET @BIT = '/28';
    ELSEIF @M4 = 248 THEN SET @BIT = '/29';
    ELSEIF @M4 = 252 THEN SET @BIT = '/30';
    ELSEIF @M4 = 254 THEN SET @BIT = '/31';
    ELSEIF @M4 = 255 THEN SET @BIT = '/32';
    END IF;
    
    IF @IP4 < @M4 THEN SET @IP4 = 0; ELSE SET @IP4 = @M4;  END IF;
 
 ELSEIF @M1 = 255 AND @M2 = 255 THEN
    
    IF @M3 = 0 AND @M4 = 0 THEN SET @BIT = '/16';
    ELSEIF @M3 = 128 AND @M4 = 0 THEN SET @BIT = '/17';
    ELSEIF @M3 = 192 AND @M4 = 0 THEN SET @BIT = '/18';
    ELSEIF @M3 = 224 AND @M4 = 0 THEN SET @BIT = '/19';
    ELSEIF @M3 = 240 AND @M4 = 0 THEN SET @BIT = '/20';
    ELSEIF @M3 = 248 AND @M4 = 0 THEN SET @BIT = '/21';
    ELSEIF @M3 = 252 AND @M4 = 0 THEN SET @BIT = '/22';
    ELSEIF @M3 = 254 AND @M4 = 0 THEN SET @BIT = '/23';
    END IF;
    
    SET @IP4 = 0;    
    
    IF @IP3 < @M3 THEN SET @IP3 = 0; ELSE SET @IP3 = @M3;  END IF;
 
 ELSEIF @M1 = 255 THEN
 
    IF @M2 = 0 AND @M3 = 0 THEN SET @BIT = '/8';
    ELSEIF @M2 = 128 AND @M3 = 0 THEN SET @BIT = '/9';
    ELSEIF @M2 = 192 AND @M3 = 0 THEN SET @BIT = '/10';
    ELSEIF @M2 = 224 AND @M3 = 0 THEN SET @BIT = '/11';
    ELSEIF @M2 = 240 AND @M3 = 0 THEN SET @BIT = '/12';
    ELSEIF @M2 = 248 AND @M3 = 0 THEN SET @BIT = '/13';
    ELSEIF @M2 = 252 AND @M3 = 0 THEN SET @BIT = '/14';
    ELSEIF @M2 = 254 AND @M3 = 0 THEN SET @BIT = '/15';
    END IF;
    
    SET @IP3 = 0;
    SET @IP4 = 0;
    
    IF @IP2 < @M2 THEN SET @IP2 = 0; ELSE SET @IP2 = @M2;  END IF;
 END IF;

 IF @BIT <> '' THEN
  SET @RESULT = CONCAT(@IP1, '.', @IP2, '.', @IP3, '.', @IP4, @BIT);
 END IF;
  
  RETURN @RESULT;
END ;;
DELIMITER ;
