CREATE DEFINER=`root`@`localhost` FUNCTION `FC_NMS_GET_NOTI_TIME`(_TMPL_ID INT
) RETURNS varchar(256) CHARSET utf8mb3 COLLATE utf8mb3_general_ci
    DETERMINISTIC
BEGIN






    DECLARE _RESULT VARCHAR(256) DEFAULT '';
    
    SELECT SCH_HOUR, SCH_MIN, SCH_SEC INTO @SCH_HOUR, @SCH_MIN, @SCH_SEC
    FROM TIME_SETUP WHERE TMPL_ID = _TMPL_ID AND SCH_WEEK IS NOT NULL 
    ORDER BY SCH_HOUR, SCH_MIN, SCH_SEC LIMIT 1;
    
    SELECT SCH_HOUR, SCH_MIN, SCH_SEC, SCH_PERIOD INTO @END_HOUR, @END_MIN, @END_SEC, @SCH_PERIOD
    FROM TIME_SETUP WHERE TMPL_ID = _TMPL_ID AND SCH_WEEK IS NOT NULL 
    ORDER BY SCH_HOUR * 3600 + SCH_MIN * 60 + SCH_SEC + SCH_PERIOD DESC LIMIT 1;
    
    
    SET @S_SEC = (@END_HOUR*60*60)+(@END_MIN*60)+(@END_SEC);
    
    
    SET @TEMP_SEC = UNIX_TIMESTAMP('2000-01-01 00:00:00');
    
    
    SET @E_DATE = FROM_UNIXTIME( @TEMP_SEC + @S_SEC + @SCH_PERIOD ); 
    
    SET _RESULT = CONCAT(_RESULT, LPAD(@SCH_HOUR, 2, '0'));
    SET _RESULT = CONCAT(_RESULT, ':');
    SET _RESULT = CONCAT(_RESULT, LPAD(@SCH_MIN, 2, '0'));
    SET _RESULT = CONCAT(_RESULT, ':');
    SET _RESULT = CONCAT(_RESULT, LPAD(@SCH_SEC, 2, '0'));
    SET _RESULT = CONCAT(_RESULT, '~');
    SET _RESULT = CONCAT(_RESULT, LPAD(HOUR(@E_DATE), 2, '0'));
    SET _RESULT = CONCAT(_RESULT, ':');
    SET _RESULT = CONCAT(_RESULT, LPAD(MINUTE(@E_DATE), 2, '0'));
    SET _RESULT = CONCAT(_RESULT, ':');
    SET _RESULT = CONCAT(_RESULT, LPAD(SECOND(@E_DATE), 2, '0'));
        
RETURN _RESULT;
END ;;
