CREATE DEFINER=`root`@`localhost` FUNCTION `FC_NMS_GET_SORT_STR`(_IF_NM VARCHAR(256)
) RETURNS varchar(256) CHARSET utf8mb3 COLLATE utf8mb3_general_ci
    DETERMINISTIC
BEGIN
    DECLARE _RESULT VARCHAR(256)  DEFAULT '';
    DECLARE _TEMP   VARCHAR(256);
    DECLARE iteratorS INT          DEFAULT 1;
    DECLARE iteratorE INT          DEFAULT 1;
    DECLARE len       INT;
    DECLARE firstDigit BOOL;
    
    SET len = LENGTH(_IF_NM) + 1;
    LOOP_WHILE:LOOP
      IF iteratorS > len OR iteratorE > len OR LENGTH(_RESULT) > 250 THEN
        LEAVE LOOP_WHILE;
      END IF;
      
      SET firstDigit = FALSE;
      WHILE ORD(SUBSTRING(_IF_NM, iteratorE, 1)) BETWEEN 48 AND 57 AND iteratorE < len DO
        IF firstDigit = FALSE THEN
          SET iteratorS = iteratorE;
          SET firstDigit = TRUE;
        END IF;
        SET iteratorE = iteratorE + 1;
      END WHILE;
      
      IF firstDigit = TRUE THEN
        SET _TEMP = SUBSTRING(_IF_NM, iteratorS, iteratorE - iteratorS);
        IF LENGTH(_TEMP) < 6 THEN
          SET _RESULT = CONCAT(_RESULT, LPAD(CAST(_TEMP AS SIGNED), 6, '0'));  
        ELSE
          SET _RESULT = CONCAT(_RESULT, _TEMP);  
        END IF;
        
      END IF;
      
      SET _RESULT = CONCAT(_RESULT, SUBSTRING(_IF_NM, iteratorE, 1));
      SET iteratorE = iteratorE + 1;
      
    END LOOP;

  RETURN _RESULT;
END ;;
