    SET @TEMP = TIMESTAMPDIFF(MINUTE, _SRC_DT, _DEST_DT) % 60;
    IF @TEMP > 0 THEN
      SET _RESULT = CONCAT(_RESULT, @TEMP, FC_CM_GET_CD_NM(1008, 'mi'), ' ');
    END IF;
    SET @TEMP = TIMESTAMPDIFF(SECOND, _SRC_DT, _DEST_DT) % 60;
    IF @TEMP > 0 THEN
      SET _RESULT = CONCAT(_RESULT, @TEMP, FC_CM_GET_CD_NM(1008, 'ss'), ' ');
    END IF;
RETURN _RESULT;
END ;;
