CREATE DEFINER=`root`@`localhost` FUNCTION `FC_NMS_OFFSET_TO_TIME`(_GMT_OFFSET DOUBLE) RETURNS varchar(10) CHARSET utf8mb3 COLLATE utf8mb3_general_ci
    DETERMINISTIC
BEGIN
    DECLARE _RESULT VARCHAR(10);
    SET _RESULT = '';
    SET @SIGN = '+';
    SET @OFFSET_TO_MIN = (_GMT_OFFSET * 3600.0);

    IF( _GMT_OFFSET < 0 ) THEN
      SET _GMT_OFFSET = (_GMT_OFFSET * -1);
      SET @SIGN = '-';
    END IF;

    SET @HOUR = IF(@SIGN = '+', ABS(FLOOR(@OFFSET_TO_MIN / 3600)), ABS(CEIL(@OFFSET_TO_MIN / 3600)));
	  SET @MINUTE = IF(@SIGN = '+', ABS(FLOOR(MOD(@OFFSET_TO_MIN, 3600) / 60)), ABS(CEIL(MOD(@OFFSET_TO_MIN, 3600) / 60)));

    SET @hDisplay = IF(@HOUR >= 10, CONCAT(@HOUR, ':'), CONCAT('0', @HOUR, ':'));
	  SET @mDisplay = IF(@MINUTE >= 10, @MINUTE, CONCAT('0', @MINUTE));

    SET _RESULT = CONCAT(@SIGN, @hDisplay, @mDisplay);

    RETURN _RESULT;
END ;;
DELIMITER ;
