CREATE DEFINER=`root`@`localhost` PROCEDURE `SP_EXTIME_INFO_ADD`(
)
BEGIN






  
  
  DECLARE v_mchID int;
  DECLARE endOfRow BOOLEAN DEFAULT FALSE; 
  DECLARE chkCnt int;
  DECLARE isExtimeMch BOOLEAN DEFAULT FALSE; 
      
  
  
  DECLARE MCH_CUR CURSOR FOR
  
  
  
  SELECT MCH_ID FROM NMS_DB.MCH_INFO M WHERE MCH_ID IN (SELECT DISTINCT MCH_ID FROM (
               SELECT DISTINCT
              MCH_ID
            FROM EXTIME_MCH_SETUP IC  
            INNER JOIN TIME_TMPL ET ON ET.TMPL_ID = IC.TMPL_ID AND ET.EACH_YN = 'N' AND ET.TMPL_GB = 'E' AND ET.USE_YN ='Y'
            WHERE IC.USE_YN='Y'
          UNION ALL
            SELECT DISTINCT
              MCH_ID
            FROM EXTIME_IF_SETUP IC  
            INNER JOIN TIME_TMPL ET ON ET.TMPL_ID = IC.TMPL_ID AND ET.EACH_YN = 'N' AND ET.TMPL_GB = 'E' AND ET.USE_YN ='Y'
            WHERE IC.USE_YN='Y'
          UNION ALL
             SELECT UE.MCH_ID
             FROM EVENT_UACT_EXTIME UE 
             INNER JOIN EVENT_UACT_INFO UI ON UE.UACT_ID = UI.UACT_ID
             WHERE UE.EXTIME_DT > NOW() AND UE.DEL_YN='N'
          UNION ALL
             SELECT MU.MCH_ID
             FROM MCH_UACT_EXTIME MU
             INNER JOIN USER_INFO U ON MU.USER_ID = U.USER_ID
             WHERE MU.END_EXTIME > NOW() AND MU.DEL_YN='N'
             ) A);

  
  DECLARE CONTINUE HANDLER FOR NOT FOUND SET endOfRow = TRUE;
  
  SET @nowDateTime = now();
  
  SET @JOB_DATE = NOW();
  SET @SP_CALL = 'SP_EXTIME_INFO_ADD';
  SET @SEC = SECOND(@JOB_DATE);
  SET @END_DATE = DATE_ADD(@JOB_DATE, INTERVAL -@SEC SECOND);
  
  
  
  SET @ST_DATE  = @END_DATE;
  
  IF NOT EXISTS 
      (SELECT * FROM NMS_DB.NMS_WATCHDOG_QUERY_HIST 
      WHERE SP_CALL = @SP_CALL AND ST_DT = @ST_DATE) THEN
      
    INSERT INTO NMS_DB.NMS_WATCHDOG_QUERY_HIST ( JOB_DT, ST_DT, END_DT, SP_CALL, UPD_DT, STATE ) 
    VALUES (@JOB_DATE, @ST_DATE, @END_DATE, @SP_CALL, NOW(), 0);
  
    
    OPEN MCH_CUR;
    mch_loop: LOOP
      FETCH MCH_CUR INTO v_mchID;
      
      IF endOfRow THEN
      	LEAVE mch_loop;
      END IF;
      
      
      SET isExtimeMch = true; 
      SET chkCnt = 0;
      BLOCK1: BEGIN 
        DECLARE v_extimeGB VARCHAR(40);
        DECLARE v_timeStr VARCHAR(1024);
        DECLARE v_extimeCD CHAR(1);
        DECLARE v_tmplYN CHAR(1);
        DECLARE v_tmplNM VARCHAR(256);
        DECLARE v_tmplDesc VARCHAR(1024);
        DECLARE endOfRow_extime BOOLEAN DEFAULT FALSE; 
        
        DECLARE runNow BOOLEAN DEFAULT FALSE; 
        DECLARE runLessThan1Hour BOOLEAN DEFAULT FALSE; 
        DECLARE runAfter1Hour BOOLEAN DEFAULT FALSE;  
        DECLARE runNextDay BOOLEAN DEFAULT FALSE;  
        
        DECLARE mchTmplCnt INT DEFAULT 0; 
        DECLARE ifTmplCnt INT DEFAULT 0; 
        DECLARE eventTmplCnt INT DEFAULT 0; 
        DECLARE mchUactCnt INT DEFAULT 0; 
              
        
        
        DECLARE EXTIME_CUR CURSOR FOR
        SELECT EXTIME_GB, TIME_STR, EXTIME_GB_CD, TMPL_YN, TMPL_NM, TMPL_DESC FROM (
          SELECT 
               FC_CM_GET_CD_NM(2099, '1') as EXTIME_GB,
                FC_NMS_GET_TIME_SET_SUM_STR2(ET.TMPL_ID, 1) AS TIME_STR,
               '1' as EXTIME_GB_CD, 'Y' AS TMPL_YN, ET.TMPL_NM, ET.TMPL_DESC
             FROM EXTIME_MCH_SETUP CP
             INNER JOIN TIME_TMPL ET ON ET.TMPL_ID = CP.TMPL_ID AND ET.EACH_YN = 'N' AND ET.TMPL_GB = 'E' AND ET.USE_YN ='Y'
             WHERE MCH_ID = v_mchID
             AND CP.USE_YN ='Y'
          UNION
            SELECT
              FC_CM_GET_CD_NM(2099, '2') as EXTIME_GB,
              FC_NMS_GET_TIME_SET_SUM_STR2(ET.TMPL_ID, 1) AS TIME_STR,
              '2' as EXTIME_GB_CD, 'Y' AS TMPL_YN, ET.TMPL_NM, CONCAT(ET.TMPL_DESC, ' (I/F Count: ', IC.IF_CNT, ')') AS TMPL_DESC
            FROM 
            (SELECT TMPL_ID, COUNT(TMPL_ID) AS IF_CNT
            FROM EXTIME_IF_SETUP 
            WHERE MCH_ID = v_mchID
            AND USE_YN ='Y'
            GROUP BY TMPL_ID) IC
            INNER JOIN TIME_TMPL ET ON ET.TMPL_ID = IC.TMPL_ID AND ET.EACH_YN = 'N' AND ET.TMPL_GB = 'E' AND ET.USE_YN ='Y'
          UNION
             SELECT
               FC_CM_GET_CD_NM(2099, '3') as EXTIME_GB,
               CONCAT(FC_CM_GET_CD_NM(2015, '1'), '(~ ', UE.EXTIME_DT, ')') AS TIME_STR,
               '3' as EXTIME_GB_CD, 'N' AS TMPL_YN, '-' AS TMPL_NM, CONCAT(UI.MEMO_DESC, FC_NMS_GET_EVENT_CD_NM(EVENT_CD)) AS TMPL_DESC
             FROM EVENT_UACT_EXTIME UE 
             INNER JOIN EVENT_UACT_INFO UI ON UE.UACT_ID = UI.UACT_ID
             WHERE UE.MCH_ID = v_mchID AND UE.EXTIME_DT > NOW() AND UE.DEL_YN='N'
          UNION
             SELECT 
                FC_CM_GET_CD_NM(2099, '4') as EXTIME_GB,
                CONCAT(FC_CM_GET_CD_NM(2015, '1'), '(', MU.ST_EXTIME, ' ~ ', MU.END_EXTIME, ')') AS TIME_STR,
               '4' as EXTIME_GB_CD, 'N' AS TMPL_YN, '-' AS TMPL_NM, U.USER_NM AS TMPL_DESC
             FROM MCH_UACT_EXTIME MU
             INNER JOIN USER_INFO U ON MU.USER_ID = U.USER_ID
             WHERE MU.MCH_ID = v_mchID AND MU.END_EXTIME > NOW() AND MU.DEL_YN='N'
        ) A;
           
        
        DECLARE CONTINUE HANDLER FOR NOT FOUND SET endOfRow_extime = TRUE;
        
        
        OPEN EXTIME_CUR;
        extime_loop: LOOP
          FETCH EXTIME_CUR INTO v_extimeGB, v_timeStr, v_extimeCD, v_tmplYN, v_tmplNM, v_tmplDesc;
          
          IF endOfRow_extime THEN
          	LEAVE extime_loop;
          END IF;
          
          
          
          
          CASE v_extimeCD 
            WHEN '1' THEN
              SET mchTmplCnt = mchTmplCnt + 1;
            WHEN '2' THEN
              SET @ifCnt = SUBSTRING_INDEX(SUBSTRING_INDEX(v_tmplDesc, 'Count: ', -1), ')', 1);
              SET ifTmplCnt = ifTmplCnt + @ifCnt;
            WHEN '3' THEN
              SET eventTmplCnt = eventTmplCnt + 1;
            WHEN '4' THEN
              SET mchUactCnt = mchUactCnt + 1;
          END CASE;
          
          
          SET @spliti =1; 
   
          WHILE @spliti != 0 DO
          	SET @tempresult = (SELECT FC_NMS_SPLIT_STR(v_timeStr, ',' ,@spliti));				

          	IF @tempresult != ''
          	THEN
              
              
              SET @sIdx = INSTR(@tempresult, '(');
              SET @eIdx = INSTR(@tempresult, ')');
              SET @schTime = SUBSTRING(@tempresult, @sIdx+1, @eIdx-@sIdx-1);
              

              
              SET @schType = SUBSTRING(@tempresult, 1, @sIdx-1);
              
              
              IF @schType = '한번' THEN 
                SET @isChkTime = TRUE;
                SET @runType = '';
                SET @findIdx = INSTR(@schTime, '~');
                
                SET @sDate = TRIM(SUBSTRING(@schTime, 1, @findIdx -1));
                SET @eDate = TRIM(SUBSTRING(@schTime, @findIdx+1, length(@schTime) -1));
                
                IF @findIdx < 21 THEN 
                  SET @sDate = @nowDateTime;
                END IF;
                
                          
              ELSEIF @schType = '반복' THEN
                SET @tmpData = @schTime; 
                
                SET @runType = SUBSTRING_INDEX(@tmpData, ' ', 1);
                SET @tmp_sDate = SUBSTRING_INDEX(SUBSTRING_INDEX(@tmpData, ' ', -3), ' ', 1);
                SET @tmp_eDate = SUBSTRING_INDEX(@tmpData, ' ', -1);
                          
                SET @isChkTime = FALSE;
                                    
                IF @runType = '매일' THEN
                  SET @isChkTime = TRUE;
                  
                  
                  SET runNextDay = TRUE;
                ELSEIF @runType = '매주' THEN
                  
                  SET @runSch = SUBSTRING_INDEX(SUBSTRING_INDEX(@tmpData, ' ', 2), ' ', -1);
                  SET @nowDayOfWeek = CONCAT(SUBSTR(_UTF8'일월화수목금토', DAYOFWEEK(@nowdateTime) ,1), '요일');
                  SET @nextDayOfWeek = CONCAT(SUBSTR(_UTF8'일월화수목금토', (DAYOFWEEK(@nowdateTime)+1)%7, 1), '요일');
                  
                  
                  IF @runSch = @nextDayOfWeek THEN
                    SET runNextDay = TRUE;
                  END IF;
                  
                  IF @runSch = @nowDayOfWeek THEN
                    SET @isChkTime = TRUE;
                  END IF;
                ELSEIF @runType = '매월' THEN
                  
                  SET @runSch = SUBSTRING_INDEX(SUBSTRING_INDEX(@tmpData, ' ', 2), ' ', -1);
                  SET @nowDayOfMonth = CONCAT(dayofmonth(@nowdateTime), '일');
                  SET @nextDayOfMonth = CONCAT(dayofmonth(date_add(@nowdateTime, interval +1 day)), '일');
                  
                  IF @runSch = @nextDayOfMonth THEN
                    SET @runNextDay = TRUE;
                  END IF;
                  
                  IF @runSch = @nowDayOfMonth THEN
                    SET @isChkTime = TRUE;
                  END IF;
                  
                END IF;
                
                
                
              END IF; 
              
                     
              IF @isChkTime = TRUE THEN
                IF @schType = '반복' THEN
                  SET @sDate = CONCAT(date_format(@nowdateTime, '%Y-%m-%d '), @tmp_sDate);
                  SET @eDate = CONCAT(date_format(@nowdateTime, '%Y-%m-%d '), @tmp_eDate);
                END IF;
                
                    
                SET @diffStartDate = DATEDIFF(date_format(@nowDateTime, '%Y-%m-%d %H:%i:%s'), date_format(@sDate, '%Y-%m-%d  %H:%i:%s'));  
                SET @diffEndDate = DATEDIFF(date_format(@nowDateTime, '%Y-%m-%d %H:%i:%s'), date_format(@eDate, '%Y-%m-%d  %H:%i:%s'));  
                SET @diffStartDateTime =  TIMESTAMPDIFF(second, date_format(@nowDateTime, '%Y-%m-%d %H:%i:%s'), date_format(@sDate, '%Y-%m-%d %H:%i:%s'));
                SET @diffEndDateTime =  TIMESTAMPDIFF(second, date_format(@nowDateTime, '%Y-%m-%d %H:%i:%s'), date_format(@eDate, '%Y-%m-%d %H:%i:%s'));
                
                IF @diffStartDate < 0 THEN 
                  SET runNextDay = TRUE;
                END IF;  
                  
                IF @diffStartDate >= 0 THEN 
                  
                  IF @diffEndDate < 0 THEN 
                    SET runNextDay = TRUE;
                  END IF;
                  
                  IF @diffStartDate = 0 THEN 
                    
                    IF @diffStartDateTime < 0 THEN 
                      SET runNow = TRUE; 
                      
                      IF @diffEndDateTime >= 60*60  THEN 
                        
                        SET runLessThan1Hour = TRUE;
                        SET runAfter1Hour = TRUE;
                      ELSEIF @diffEndDateTime < 0 THEN 
                        SET runNow = FALSE;
                      ELSEIF @diffEndDateTime < 60*60 THEN 
                        
                        set runLessThan1Hour = TRUE;
                      END IF;
                    ELSEIF @diffStartDateTime < 60*60 THEN 
                      
                      SET runLessThan1Hour = TRUE;
                      
                      IF @diffEndDateTime >= 60*60 THEN 
                        SET runAfter1Hour = TRUE;
                      END IF;
                    ELSEIF @diffStartDateTime >= 60*60 THEN 
                      
                      SET runAfter1Hour = TRUE;
                    END IF; 
                  ELSE 
                    
                    
                    IF @diffEndDate <= 0 and @diffEndDateTime > 0 THEN 
                      SET runNow = TRUE; 
                      
                      IF @diffEndDateTime < 60*60  THEN 
                        
                        SET runLessThan1Hour = TRUE;
                      ELSE
                        
                        set runLessThan1Hour = TRUE;
                        SET runAfter1Hour = TRUE;
                      END IF;
                    END IF;
                  END IF;
                  
                  
                END IF;
                

              END IF;
              
          		SET @spliti = @spliti+1;
          	ELSE
          		SET @spliti = 0;
          	END IF;
          END WHILE;          
          
          
          
        END LOOP extime_loop;
        CLOSE EXTIME_CUR;
        
        
        
        IF mchTmplCnt = 0 AND ifTmplCnt = 0 AND eventTmplCnt = 0 AND mchUactCnt = 0  THEN
          SET isExtimeMch = false;
        END IF;
        
        SELECT count(*) into chkCnt FROM  NMS_DB.EXTIME_INFO where MCH_ID=v_mchID;
        
        
        IF chkCnt = 0 THEN
          IF isExtimeMch = true THEN 
            INSERT INTO NMS_DB.EXTIME_INFO(MCH_ID, RUN_NOW, RUN_LESS_THAN_1_HR, RUN_AFTER_1_HR, RUN_NEXTDAY, MCH_TMPL_CNT, IF_TMPL_CNT, EVENT_TMPL_CNT, MCH_UACT_CNT)
              VALUES(v_mchID, runNow, runLessThan1Hour, runAfter1Hour, runNextDay, mchTmplCnt, ifTmplCnt, eventTmplCnt, mchUactCnt);
          END IF;
        ELSE 
          IF isExtimeMch = false THEN
            SET @delYN = 'Y';
          ELSE 
            SET @delYN = 'N';
          END IF;
          
          
          UPDATE NMS_DB.EXTIME_INFO 
          SET RUN_NOW = runNow,
              RUN_LESS_THAN_1_HR = runLessThan1Hour,
              RUN_AFTER_1_HR = runAfter1Hour,
              RUN_NEXTDAY = runNextDay,
              MCH_TMPL_CNT = mchTmplCnt,
              IF_TMPL_CNT = ifTmplCnt,
              EVENT_TMPL_CNT = eventTmplCnt,
              MCH_UACT_CNT = mchUactCnt,
              UPD_DT = @nowDateTime,
              DEL_YN = @delYN
          WHERE MCH_ID = v_mchID;
        END IF;
        
      END BLOCK1;    
     
    END LOOP mch_loop;
    CLOSE MCH_CUR;
    
    
    UPDATE NMS_DB.EXTIME_INFO SET DEL_YN='Y', UPD_DT = NOW() WHERE DEL_YN='N' AND UPD_DT < @nowDateTime;
    
    UPDATE NMS_DB.NMS_WATCHDOG_QUERY_HIST SET UPD_DT = NOW(), STATE = 1
    WHERE SP_CALL = @SP_CALL AND ST_DT = @ST_DATE;
    
  END IF;
END ;;
