  SET @WEEK = DAYOFWEEK(@B_ST_DATE);
    
  
  SELECT SCH_HOUR, SCH_MIN, SCH_PERIOD INTO @ST_HOUR, @SCH_MIN, @SCH_PERIOD 
    FROM NMS_DB.TIME_SETUP TS 
    INNER JOIN NMS_DB.TIME_TMPL T ON TS.TMPL_ID = T.TMPL_ID
    WHERE T.TMPL_GB = 'B' AND SCH_WEEK = @WEEK;
  
  
  SET @SCH_DATE = DATE_ADD(@B_ST_DATE, INTERVAL @ST_HOUR HOUR);
  SET @SCH_DATE = DATE_ADD(@SCH_DATE, INTERVAL @SCH_MIN MINUTE);
  SET @SCH_ST_DATE = @SCH_DATE;
  SET @SCH_END_DATE  = DATE_ADD(@SCH_DATE, INTERVAL @SCH_PERIOD SECOND);
  SET @B_END_HOUR = HOUR(@SCH_END_DATE);
    
  
  IF @SCH_PERIOD = 0 OR @HOUR < @ST_HOUR  THEN
    SET @B_ST_DATE  = CONVERT('1700-01-01 00:00:00', DATETIME);
    SET @B_END_DATE = CONVERT('1700-01-01 00:00:00', DATETIME);
  ELSE 
    IF @HOUR < @B_END_HOUR THEN
      SET @B_ST_DATE = @SCH_ST_DATE;
      SET @B_END_DATE  = @ORG_DT; 
    ELSE
      SET @B_ST_DATE = @SCH_ST_DATE;
      SET @B_END_DATE  = @SCH_END_DATE; 
    END IF;
  END IF;
  
  SET _START_DATE = @B_ST_DATE;
  SET _END_DATE = @B_END_DATE;
  
END ;;
