CREATE DEFINER=`root`@`localhost` PROCEDURE `SP_MYSQL_TABLE_DATA_INSERT_GEN`(
IN _TABLE_NM VARCHAR(100),    
IN _TABLE_SCHEMA VARCHAR(20), 
IN _AI_INCLUDE BOOL           
)
BEGIN






  DECLARE v_sql VARCHAR(8000) DEFAULT '';
  DECLARE v_sql_val VARCHAR(8000) DEFAULT '';
  DECLARE v_column_nm VARCHAR(64);
  DECLARE v_data_type VARCHAR(64);
  DECLARE v_extra VARCHAR(27);
  DECLARE v_finish INT DEFAULT 0;
  
  DECLARE CUR_JOB CURSOR FOR
    SELECT COLUMN_NAME, DATA_TYPE, EXTRA 
    FROM INFORMATION_SCHEMA.COLUMNS WHERE TABLE_NAME=_TABLE_NM AND TABLE_SCHEMA=_TABLE_SCHEMA;
      
  DECLARE CONTINUE HANDLER FOR NOT FOUND SET v_finish = 1;
  
  OPEN CUR_JOB;
  
  LOOP_JOB : LOOP
  
    FETCH CUR_JOB INTO v_column_nm, v_data_type, v_extra;
    
    IF v_finish = 1 THEN
      LEAVE LOOP_JOB;
    END IF;
    
    
    IF _AI_INCLUDE = FALSE AND v_extra = 'auto_increment' THEN
      ITERATE LOOP_JOB;
    END IF;
    
