CREATE DEFINER=`root`@`localhost` PROCEDURE `SP_NMS_CHECK_TABLE_PARTITION_COUNT`()
BEGIN






    DECLARE v_table_nm1, v_table_nm2, v_table_nm3 varchar(64);
    DECLARE v_index_count int;
    DECLARE v_done boolean;
    DECLARE cursor_nms CURSOR FOR
        select distinct TABLE_NAME from information_schema.partitions where TABLE_SCHEMA='NMS_DB' AND PARTITION_NAME IS NOT NULL;
        
    DECLARE cursor_nmc CURSOR FOR
        select distinct TABLE_NAME from information_schema.partitions where TABLE_SCHEMA='NMC_DB' AND PARTITION_NAME IS NOT NULL;
        
    DECLARE cursor_cm CURSOR FOR
        select distinct TABLE_NAME from information_schema.partitions where TABLE_SCHEMA='CM_DB' AND PARTITION_NAME IS NOT NULL;
        
    DECLARE CONTINUE HANDLER FOR NOT FOUND SET v_done = true;

    CREATE TEMPORARY TABLE IF NOT EXISTS NMS_TABLE_PARTITION(
        IDX int NOT NULL,
        DB_SCHEMA varchar(64) NOT NULL,
        TABLE_NM varchar(64) NOT NULL,
        PARTITION_COUNT int NOT NULL
    );

    SET v_index_count = 0;
    OPEN cursor_nms;
      nms_loop:LOOP

          FETCH cursor_nms
              INTO v_table_nm1;
              
          IF v_done THEN
              SET v_done = false;
              LEAVE nms_loop;              
          END IF;

          SET v_index_count = v_index_count + 1;
          INSERT INTO NMS_TABLE_PARTITION (select v_index_count, 'NMS_DB', v_table_nm1, COUNT(*) from information_schema.partitions where TABLE_NAME=v_table_nm1);
          

      END LOOP nms_loop;    

    CLOSE cursor_nms;
    
    OPEN cursor_nmc;
      nmc_loop:LOOP

          FETCH cursor_nmc
              INTO v_table_nm2;
              
          IF v_done THEN
              SET v_done = false;
              LEAVE nmc_loop;              
          END IF;

          SET v_index_count = v_index_count + 1;
          INSERT INTO NMS_TABLE_PARTITION (select v_index_count, 'NMC_DB', v_table_nm2, COUNT(*) from information_schema.partitions where TABLE_NAME=v_table_nm2);
          

      END LOOP nmc_loop;    

    CLOSE cursor_nmc;
    
    OPEN cursor_cm;
      cm_loop:LOOP

          FETCH cursor_cm
              INTO v_table_nm3;
              
          IF v_done THEN
              SET v_done = false; 
              LEAVE cm_loop;              
          END IF;

          SET v_index_count = v_index_count + 1;
          INSERT INTO NMS_TABLE_PARTITION (select v_index_count, 'CM_DB', v_table_nm3, COUNT(*) from information_schema.partitions where TABLE_NAME=v_table_nm3);
          

      END LOOP cm_loop;    

    CLOSE cursor_cm;

    select * from NMS_DB.NMS_TABLE_PARTITION;

    DROP TEMPORARY TABLE IF EXISTS NMS_TABLE_PARTITION;

END ;;
