  SET @SEC = SECOND(@JOB_DATE);
  SET @END_DATE = DATE_ADD(@JOB_DATE, INTERVAL -@SEC SECOND);
  
  SELECT @ST_DATE := IFNULL(MAX(END_DT), DATE_ADD(@JOB_DATE, INTERVAL -1 HOUR)) FROM NMS_DB.NMS_WATCHDOG_QUERY_HIST
  WHERE SP_CALL = @SP_CALL AND ST_DT > DATE_ADD(@END_DATE, INTERVAL -1 DAY);
  SET @ST_DATE = DATE_ADD(@ST_DATE, INTERVAL -1 HOUR);



    
  IF NOT EXISTS 
      (SELECT * FROM NMS_DB.NMS_WATCHDOG_QUERY_HIST 
      WHERE SP_CALL = @SP_CALL AND ST_DT = @ST_DATE) THEN
      

    
  	START TRANSACTION;
    
    
    INSERT INTO NMS_DB.NMS_WATCHDOG_QUERY_HIST ( JOB_DT, ST_DT, END_DT, SP_CALL, UPD_DT, STATE ) 
    VALUES (NOW(), @ST_DATE, @END_DATE, @SP_CALL, NOW(), 0);

    SET @REQ_ID = 0;
    
    INSERT INTO NMS_DB.CONF_REQ (SVR_ID, REQ_CHANNEL_GB, MCH_IP_RANGE, SNMP_TMPL_ID, FAIL_REG_YN, MODEL_CHG_YN, USER_ID, USER_IP)
    SELECT _SVR_ID, 'S', 'IF_CHG_MCH', 0, 'N', 'N', 'schedule', '127.0.0.1';

    
    SELECT LAST_INSERT_ID() INTO @REQ_ID;

    INSERT INTO NMS_DB.CONF_MCH_REQ (REQ_ID, REQ_CHANNEL_GB, SVR_ID, REG_MCH_ID, MCH_IP, MCH_NM, SNMP_TMPL_ID, REQ_STATE, RESULT_STATE, FAIL_REG_YN, MODEL_CHG_YN)
    SELECT @REQ_ID, 'S', CP.NMS_SVR_ID, CP.MCH_ID, M.MCH_IP, T.TARGET_NM, MA.SNMP_TMPL_ID, 0, 0, 'N', 'N'
    FROM NMS_DB.MCH_COLLECT_POLICY CP 
    INNER JOIN NMS_DB.MCH_INFO M ON CP.MCH_ID = M.MCH_ID
    INNER JOIN NMS_DB.TARGET_INFO T ON CP.MCH_ID = T.TARGET_ID
    INNER JOIN NMS_DB.MCH_ACCOUNT MA ON CP.MCH_ID = MA.MCH_ID
    WHERE CP.NMS_SVR_ID = _SVR_ID AND CP.MNG_YN ='Y' AND CP.CHG_MNG_YN = 'Y' AND M.MODEL_ID > 1
              AND CP.MCH_ID IN (SELECT DISTINCT MCH_ID FROM MCH_IF_STATE WHERE UPD_DT >= @ST_DATE AND UPD_DT < @END_DATE);

    
    UPDATE NMS_DB.NMS_WATCHDOG_QUERY_HIST SET UPD_DT=NOW(), STATE=1
    WHERE SP_CALL = @SP_CALL AND ST_DT = @ST_DATE;
    
    
    COMMIT;
    
  END IF;
  
END ;;
