  WHERE IF_ID = _IF_ID;
  
  
  DELETE FROM NMS_DB.IF_COLLECT_POLICY WHERE IF_ID = _IF_ID;
  
  
  DELETE FROM NMS_DB.MCH_IF_STATE WHERE IF_ID = _IF_ID;
  
  
  DELETE FROM NMS_DB.MCH_IF_MIB WHERE IF_ID = _IF_ID;
  
  
