IN _MCH_IP VARCHAR(50),
IN _MCH_NM VARCHAR(256),
IN _MODEL_ID INT,
IN _SYS_OBJECTID VARCHAR(50),
IN _SYS_NAME VARCHAR(256),
IN _SYS_DESC VARCHAR(600),
IN _SNMP_TMPL_ID INT,
IN _ICMP_RESULT INT,
IN _ICMP_RESULT_PREV INT,
IN _MODEL_CHG INT
)
ERR_TRAN: BEGIN










  
  DECLARE EXIT HANDLER FOR SQLEXCEPTION
	  BEGIN
		  ROLLBACK;
      
      GET DIAGNOSTICS CONDITION 1 @P1 = MYSQL_ERRNO, @P2 = MESSAGE_TEXT;        
      INSERT INTO NMS_DB.ERROR_DB(ERR_TYPE,ERR_DEST,ERR_CD,ERR_MSG) 
      VALUES('SP','SP_NMS_CONFMON_MCH_CHG',@P1,@P2);
	END;
  
  
	START TRANSACTION;
  
  
  SELECT MODEL_ID INTO @MODEL_ID_OLD FROM NMS_DB.MCH_INFO WHERE MCH_ID = _MCH_ID;

  
  UPDATE NMS_DB.MCH_INFO SET MCH_IP=_MCH_IP, MODEL_ID=_MODEL_ID, SYS_OBJECTID=_SYS_OBJECTID, 
  SYS_NAME=_SYS_NAME, SYS_DESC=_SYS_DESC, UPD_DT=NOW()
  WHERE MCH_ID=_MCH_ID;
  
  
  UPDATE NMS_DB.MCH_ACCOUNT SET SNMP_TMPL_ID=_SNMP_TMPL_ID, UPD_DT=NOW()
  WHERE MCH_ID=_MCH_ID;
  
  
  
  DELETE FROM CONF_MCH_CHG_MODEL WHERE MCH_ID = _MCH_ID;

  
  IF _MODEL_CHG = 1 THEN 
  
    
    DELETE FROM SMCH_COLLECT_POLICY WHERE MCH_ID = _MCH_ID;
  
    
    UPDATE NMS_DB.TARGET_INFO SET TARGET_NM=_MCH_NM, UPD_DT=NOW()
    WHERE TARGET_ID=_MCH_ID;
  
    IF _MODEL_ID > 1 THEN 
      SET @PERF_COLLECT_YN = 'Y';
      
      
      SELECT R.PERF_POLL_INTERVAL, R.ICMP_TMPL_ID, R.MCH_PERF_TMPL_ID, R.MCH_MNG_LEVEL, MT.IMG_CD, MT.MCH_TYPE_CD, MT.MCH_TYPE_NM, MG.MCH_GB_NM
      INTO @PERF_POLL_INTERVAL, @ICMP_TMPL_ID, @PERF_TMPL_ID, @MNG_LEVEL, @IMG_CD, @MCH_TYPE_CD, @MCH_TYPE_NM, @MCH_GB_NM
      FROM NMS_DB.REG_POLICY R
      JOIN NMS_DB.MCH_TYPE_INFO MT ON MT.POLICY_ID = R.POLICY_ID
      JOIN NMS_DB.MCH_GB_INFO MG ON MG.MCH_GB_CD = MT.MCH_GB_CD
      JOIN NMS_DB.MCH_MODEL_INFO MM ON MM.MCH_TYPE_CD = MT.MCH_TYPE_CD
      WHERE MM.MODEL_ID = _MODEL_ID;

      
      SELECT ICMP_TMPL_ID, PERF_TMPL_ID INTO @REG_ICMP_TMPL_ID, @REG_PERF_TMPL_ID
      FROM NMS_DB.MCH_COLLECT_POLICY WHERE MCH_ID = _MCH_ID;

      IF @REG_ICMP_TMPL_ID > 0 THEN
        SET @ICMP_TMPL_ID = @REG_ICMP_TMPL_ID;
      END IF;

      IF @REG_PERF_TMPL_ID > 0 THEN
        SET @PERF_TMPL_ID = @REG_PERF_TMPL_ID;
      END IF;
                
      
      
      INSERT INTO NMS_DB.SMCH_COLLECT_POLICY (MCH_ID, SMCH_GB, BASE_POLICY_ID, PERF_POLL_INTERVAL)
      SELECT _MCH_ID, SMCH_GB, BASE_POLICY_ID, PERF_POLL_INTERVAL FROM NMS_DB.SMCH_MODEL_SETUP WHERE MODEL_ID = _MODEL_ID AND BASE_POLICY_ID > 0;
    
      
      SELECT SERIAL_NO INTO @SERIAL_NO FROM CONF_MCH_REQ WHERE UNIT_ID = _UNIT_ID;

      
      SELECT MCH_TYPE_CD INTO @MCH_TYPE_CD_OLD FROM NMS_DB.MCH_UI_INFO WHERE MCH_ID = _MCH_ID;

      
      IF @MODEL_ID_OLD = 1 AND @MCH_TYPE_CD_OLD != 'TMP_00' THEN
        UPDATE MCH_UI_INFO SET UI_SERIAL_NO = @SERIAL_NO
        WHERE MCH_ID = _MCH_ID;
      ELSE
        UPDATE MCH_UI_INFO SET IMG_CD = @IMG_CD, MCH_TYPE_CD = @MCH_TYPE_CD, UI_SERIAL_NO = @SERIAL_NO
        WHERE MCH_ID = _MCH_ID;        
      END IF;
      
      
      SET @INVEN_MCH_ID = 0;
      SELECT INVEN_MCH_ID INTO @INVEN_MCH_ID FROM INVEN_MCH_INFO WHERE MCH_IP = _MCH_IP AND INVEN_DEL_YN='N';
      SELECT MODEL_NM, VENDOR_CD INTO @MODEL_NM, @VENDOR_CD FROM MCH_MODEL_INFO WHERE MODEL_ID=_MODEL_ID;
      
      IF @INVEN_MCH_ID > 0 THEN
        UPDATE INVEN_MCH_INFO 
        SET MCH_ID=_MCH_ID, UPD_DT=NOW()
          , MCH_GB_NM=@MCH_GB_NM
          , MCH_TYPE_NM=@MCH_TYPE_NM
          , VENDOR_NM=FC_CM_GET_CD_NM('2031',@VENDOR_CD)
          , MODEL_NM=@MODEL_NM
          , MCH_DEL_YN='N'
          , INVEN_DEL_YN='N'
          , SERIAL_NO=@SERIAL_NO
        WHERE INVEN_MCH_ID = @INVEN_MCH_ID;
      END IF;

    ELSE
      
      SET @PERF_COLLECT_YN = 'N';
      
      
      SET @REG_POLICY_ID = 0;
      SELECT R.POLICY_ID, R.PERF_POLL_INTERVAL, R.ICMP_TMPL_ID, R.MCH_PERF_TMPL_ID, R.MCH_MNG_LEVEL, MT.IMG_CD 
      INTO @REG_POLICY_ID, @PERF_POLL_INTERVAL, @ICMP_TMPL_ID, @PERF_TMPL_ID, @MNG_LEVEL, @IMG_CD
      FROM NMS_DB.REG_POLICY R
      JOIN NMS_DB.MCH_TYPE_INFO MT ON MT.POLICY_ID = R.POLICY_ID
      JOIN NMS_DB.MCH_MODEL_INFO MM ON MM.MCH_TYPE_CD = MT.MCH_TYPE_CD
      WHERE MM.MODEL_ID = 1;
      
      
      IF @REG_POLICY_ID <= 0 THEN
        SET @PERF_POLL_INTERVAL = 300;  
        SET @ICMP_TMPL_ID = 1;         
        SET @PERF_TMPL_ID = 0;         
        SET @MNG_LEVEL = 5;            
        SET @IMG_CD = '';              
        
        
        SELECT IMG_CD INTO @IMG_CD FROM IMG_INFO WHERE IMG_TYPE_GB = '2' AND IMG_CD LIKE 'NM%' LIMIT 1;  
      END IF;
      
    END IF;

    
    IF _ICMP_RESULT = 1 AND @ICMP_TMPL_ID > 0 THEN  
      SET @FAULT_COLLECT_YN = 'Y';    
    ELSE
      SET @FAULT_COLLECT_YN = 'N';    
    END IF;
    
    
    UPDATE NMS_DB.MCH_COLLECT_POLICY SET ICMP_TMPL_ID=@ICMP_TMPL_ID, PERF_TMPL_ID=@PERF_TMPL_ID,
    NMS_SVR_ID=_NMS_SVR_ID, PERF_COLLECT_YN=@PERF_COLLECT_YN, PERF_POLL_INTERVAL=@PERF_POLL_INTERVAL,
    FAULT_COLLECT_YN=@FAULT_COLLECT_YN, UPD_DT=NOW()
    WHERE MCH_ID=_MCH_ID;
    







  
  END IF;

  
  COMMIT;
  
END ;;
DELIMITER ;
