IN _SMCH_GB VARCHAR (10),         
IN _BASEPOLICY_CD VARCHAR(20),    
IN _POLLING_INTERVAL INT,         
IN _HOST_MCH_ID INT,              
IN _ACCOUNT_TMPL_ID INT,
IN _GUEST_KEY VARCHAR(20),        
IN _SERIAL_NO VARCHAR(50),        
IN _ICMP_RESULT INT,              
OUT _NEW_ID INT                  
)
ERR_TRAN: BEGIN
  

   DECLARE EXIT HANDLER FOR SQLEXCEPTION
   BEGIN
      ROLLBACK;
      SET _NEW_ID = 0;

      
      GET DIAGNOSTICS CONDITION 1 @P1 = MYSQL_ERRNO, @P2 = MESSAGE_TEXT;        
      INSERT INTO NMS_DB.ERROR_DB(ERR_TYPE,ERR_DEST,ERR_CD,ERR_MSG) 
      VALUES('SP','SP_NMS_CONF_VMM_ADD',@P1,@P2);
   END;
  
  
   START TRANSACTION;


    SET @GROUP_ID = 0;                 
    SET @NW_GRP_ID = 0;                
    SET @SYS_DESC = '';                
    SET @SYS_LOCATION = '';            
    SET @SYS_CONTACT = '';             
    SET @SNMP_TMPL_ID = 0;             
    SET @SNMP_VER = 0;                 
    SET @SNMP_CMNT_RO = '';            
    SET @TERMINAL_TMPL_ID = 0;         
    SET @SYS_OBJECTID = '1';           
    SET @SYS_UPTIME = '';             
    SET @NEW_ID = 0;
    SET @ALREADY = 0;    

    SELECT COUNT(*) INTO @ALREADY FROM NMS_DB.MCH_INFO WHERE MCH_IP=_GUEST_IP;
    
    IF @ALREADY = 0 THEN

      SET @MODEL_ID = ( SELECT MODEL_ID FROM NMS_DB.MCH_MODEL_INFO WHERE SYS_OBJECTID = '1.9999.2.8.5.3' ); 

      CALL NMS_DB.SP_NMS_CONFMON_MCH_ADD ( 0, _NMS_SVR_ID, _GUEST_IP, _SVR_NM, @MODEL_ID, @GROUP_ID, @NW_GRP_ID,
        @SYS_OBJECTID, _SVR_NM, @SYS_DESC, @SYS_UPTIME, @SYS_LOCATION, @SYS_CONTACT, 
        _SERIAL_NO, @SNMP_TMPL_ID, @SNMP_VER, @SNMP_CMNT_RO, _ICMP_RESULT, @TERMINAL_TMPL_ID, @NEW_ID );  
      
      SET _NEW_ID = @NEW_ID;    
      
      
      IF _NEW_ID != 0 THEN        
        UPDATE NMS_DB.SMCH_COLLECT_POLICY SET ACCOUNT_TMPL_ID = _ACCOUNT_TMPL_ID WHERE MCH_ID = @NEW_ID AND SMCH_GB='H';
        
        INSERT INTO NMS_DB.VM_HG_MAPPING (HOST_MCH_ID, GUEST_MCH_ID, GUEST_KEY) VALUE (_HOST_MCH_ID, _NEW_ID, _GUEST_KEY);
        INSERT INTO NMS_DB.MCH_STATE (MCH_ID) VALUES( _NEW_ID );        
        
      END IF;
    ELSE
      SET _NEW_ID = -1;
    END IF;
    
    COMMIT;
    SELECT _NEW_ID FROM DUAL; 
    
END ;;
