CREATE DEFINER=`root`@`localhost` PROCEDURE `SP_NMS_CREATE_BRANCH_OFFICE_WORKHOUR`(
IN _TMPL_NM VARCHAR(256),
IN _TMPL_DESC VARCHAR(600),
IN _BRANCH_OFFICE_ID INT(11),
IN _SCH_WEEK INT(11),
IN _START_HOUR INT(11),
IN _START_MIN INT(11),
IN _END_HOUR INT(11),
IN _END_MIN INT(11),
IN _START_HMS VARCHAR(50),
IN _END_HMS VARCHAR(50),
OUT _RESULT CHAR(1),
OUT _ERROR_CD VARCHAR(256)
)
ERR_TRAN: BEGIN





   DECLARE EXIT HANDLER FOR SQLEXCEPTION
   BEGIN
      ROLLBACK;
      SET _RESULT = 'N';
      SET _ERROR_CD = 'NM20001';

      
      GET DIAGNOSTICS CONDITION 1 @P1 = MYSQL_ERRNO, @P2 = MESSAGE_TEXT;        
      INSERT INTO NMS_DB.ERROR_DB(ERR_TYPE,ERR_DEST,ERR_CD,ERR_MSG) 
      VALUES('SP','SP_NMS_CREATE_BRANCH_OFFICE_WORKHOUR',@P1,@P2);
   END;
  
    
   

    
  
  START TRANSACTION;

  INSERT INTO TIME_TMPL (TMPL_NM, TMPL_GB, EACH_YN, TMPL_DESC, USE_YN, REG_DT, UPD_DT) 
  (SELECT _TMPL_NM,'F','N',_TMPL_DESC,'Y',NOW(),NOW()
  FROM DUAL WHERE NOT EXISTS
  ( SELECT * FROM TIME_TMPL WHERE TMPL_NM= _TMPL_NM AND TMPL_GB='F'));

  SET @TMPL_ID = (SELECT TMPL_ID FROM TIME_TMPL WHERE TMPL_NM = _TMPL_NM AND TMPL_GB = 'F');

  INSERT INTO TIME_SETUP (TMPL_ID,RUN_GB,RUN_TYPE,SCH_HOUR,SCH_MIN,SCH_SEC, SCH_DAY,SCH_WEEK ,SCH_PERIOD)
  VALUES (@TMPL_ID, '1', '3', _START_HOUR, _START_MIN, 0, 0, _SCH_WEEK, IF( _END_HOUR=0 AND _END_MIN=0, 0, TIME_TO_SEC(CONCAT(_END_HMS,':59')) - TIME_TO_SEC(CONCAT(_START_HMS,':00')) + 1 ));

  UPDATE BRANCH_OFFICE_TMPL
  SET TMPL_ID = @TMPL_ID, 
      UPD_DT = NOW()
  WHERE BRANCH_OFFICE_ID = _BRANCH_OFFICE_ID;
          
 
  COMMIT;
    
  SET _RESULT = 'Y';
  SET _ERROR_CD = '';
  
END ;;
