IN  _WGD_ID INT,
IN  _WGD_GB_CD INT,
OUT _RESULT CHAR(1),
OUT _ERROR_CD VARCHAR(256)
)
ERR_TRAN : BEGIN

	
    DECLARE EXIT HANDLER FOR SQLEXCEPTION
    BEGIN
        ROLLBACK;
        SET _RESULT = 'N';
        SET _ERROR_CD = 'NM20001';

        
        GET DIAGNOSTICS CONDITION 1 @P1 = MYSQL_ERRNO, @P2 = MESSAGE_TEXT;        
        INSERT INTO NMS_DB.ERROR_DB(ERR_TYPE,ERR_DEST,ERR_CD,ERR_MSG) 
        VALUES('SP','SP_NMS_DASHBOARD_COPY',@P1,@P2);
    END;

	
	START TRANSACTION;

    INSERT INTO WGD_SETUP (USER_ID, WGD_NM, WGD_DESC, WGD_GB_CD, TITLE_NM, FONT_COLOR, CI_IMG_CD, THEMA_CD, TAB_INTERVAL, DEFAULT_YN, TAB_HIDING_YN)
    SELECT USER_ID, CONCAT('(COPY)',WGD_NM) AS WGD_NM, CONCAT('(COPY)',WGD_DESC) AS WGD_DESC, WGD_GB_CD, TITLE_NM, FONT_COLOR, CI_IMG_CD, THEMA_CD, TAB_INTERVAL, DEFAULT_YN, TAB_HIDING_YN  FROM WGD_SETUP WHERE WGD_GB_CD = _WGD_GB_CD AND WGD_ID = _WGD_ID;

    INSERT INTO WGD_TAB_SETUP (WGD_ID, TAB_NM, TAB_NM_EN, TAB_ORDER) 
    SELECT (SELECT WGD_ID FROM WGD_SETUP WHERE WGD_GB_CD = _WGD_GB_CD ORDER BY WGD_ID DESC LIMIT 1), TAB_NM, TAB_NM_EN, TAB_ORDER FROM WGD_TAB_SETUP WHERE WGD_ID =_WGD_ID;

    INSERT INTO WG_SETUP (WGD_TAB_ID, WGD_ID, WG_CD, USE_YN, TITLE_NM, TITLE_VIEW_YN, VIEW_INTR, VIEW_PERIOD, TOPN_CNT, DEST_ALL_YN, POS_X, POS_Y, SIZE_X, SIZE_Y, SETUP, MCH_SETTING, SETTING_TYPE)
    SELECT A.WGD_TAB_ID, A.WGD_ID, B.WG_CD, B.USE_YN, B.TITLE_NM, B.TITLE_VIEW_YN, B.VIEW_INTR, B.VIEW_PERIOD, B.TOPN_CNT, B.DEST_ALL_YN, B.POS_X, B.POS_Y, B.SIZE_X, B.SIZE_Y, B.SETUP, B.MCH_SETTING, B.SETTING_TYPE
    FROM (SELECT A.*, B.WGD_TAB_ID AS TAB_ID, B.WGD_ID AS ID FROM (
    SELECT * FROM WGD_TAB_SETUP WHERE WGD_ID = (SELECT WGD_ID FROM WGD_SETUP WHERE WGD_GB_CD = _WGD_GB_CD ORDER BY WGD_ID DESC LIMIT 1)) A, (SELECT * FROM WGD_TAB_SETUP WHERE WGD_ID = _WGD_ID) B WHERE A.TAB_NM = B.TAB_NM AND A.TAB_ORDER = B.TAB_ORDER
    GROUP BY A.WGD_TAB_ID, A.WGD_ID, A.TAB_NM) A, WG_SETUP B WHERE A.ID = B.WGD_ID AND A.TAB_ID = B.WGD_TAB_ID;

    INSERT INTO WG_SETUP_DEST (WG_ID, DEST_ID, DEST_CD) 
    SELECT B.WG_ID, A.DEST_ID, A.DEST_CD  FROM (
          SELECT B.*, A.WG_CD, A.POS_X, A.POS_Y, A.SIZE_X, A.SIZE_Y
          FROM WG_SETUP A, WG_SETUP_DEST B
          WHERE A.WG_ID = B.WG_ID AND A.WGD_ID = _WGD_ID
    ) A
    LEFT OUTER JOIN WG_SETUP B ON A.WG_CD = B.WG_CD AND A.POS_X = B.POS_X AND A.POS_Y = B.POS_Y AND A.SIZE_X = B.SIZE_X AND A.SIZE_Y = B.SIZE_Y
    WHERE B.WGD_ID = (SELECT WGD_ID FROM WGD_SETUP WHERE WGD_GB_CD = _WGD_GB_CD ORDER BY WGD_ID DESC LIMIT 1);

    SET _RESULT = 'Y';
    SET _ERROR_CD = 'NM00000';

    COMMIT;

END ;;
DELIMITER ;
