ERR_TRAN: BEGIN





  
	DECLARE EXIT HANDLER FOR SQLEXCEPTION
	BEGIN
		ROLLBACK;

    
    GET DIAGNOSTICS CONDITION 1 @P1 = MYSQL_ERRNO, @P2 = MESSAGE_TEXT;        
    INSERT INTO NMS_DB.ERROR_DB(ERR_TYPE,ERR_DEST,ERR_CD,ERR_MSG) 
    VALUES('SP','SP_NMS_DB_CLEAN_SCH',@P1,@P2);
	END;
    
  SET @JOB_DATE = NOW();
  SET @SP_CALL = 'SP_NMS_DB_CLEAN_SCH';
  SET @SEC = SECOND(@JOB_DATE);
  SET @END_DATE = DATE_ADD(@JOB_DATE, INTERVAL -@SEC SECOND);
  SET @MIN = MINUTE(@END_DATE);
  SET @END_DATE = DATE_ADD(@END_DATE, INTERVAL -@MIN MINUTE);
  SET @ST_DATE  = @END_DATE;
  
    
  IF NOT EXISTS 
      (SELECT * FROM NMS_DB.NMS_WATCHDOG_QUERY_HIST 
      WHERE SP_CALL = @SP_CALL AND ST_DT = @ST_DATE) THEN
    
    
    INSERT INTO NMS_DB.NMS_WATCHDOG_QUERY_HIST ( JOB_DT, ST_DT, END_DT, SP_CALL, UPD_DT, STATE ) 
    VALUES (@JOB_DATE, @ST_DATE, @END_DATE, @SP_CALL, NOW(), 0);
    
    
    START TRANSACTION;
    
    
    TRUNCATE TABLE SMS_PROCS_UI;
    
    
    
    
    DELETE FROM NMC_DB.NOTI_SMS_SEND_HIST WHERE EVENT_ID IN (SELECT sub.EVENT_ID FROM NMC_DB.NOTI_SMS_SEND_HIST sub WHERE sub.EVENT_GB=3) OR REG_DT < DATE_ADD(NOW(), INTERVAL -30 DAY);
    DELETE FROM NOTI_MAIL_SEND_HIST WHERE REG_DT < DATE_ADD(NOW(), INTERVAL -1 DAY);
      
    
    DELETE FROM CLI_TASK_REQ WHERE REG_DT < DATE_ADD(NOW(), INTERVAL -1 DAY) AND TASK_GB != 'W';
    
    
    DELETE FROM IF_SNMP_REQ WHERE REG_DT < DATE_ADD(NOW(), INTERVAL -1 DAY);
    
    
    DELETE FROM RT_SNMP_UI WHERE REG_DT < DATE_ADD(NOW(), INTERVAL -1 DAY);
    
    
    DELETE FROM SMS_PROCS_MNG WHERE USE_YN = 'N' AND REG_DT < DATE_ADD(NOW(), INTERVAL -3 MONTH);
    
    
    DELETE FROM MCH_COLLECT_TRANS WHERE REG_DT < DATE_ADD(NOW(), INTERVAL -3 MONTH);
    
    
    DELETE FROM ISSUE_HIST WHERE REG_DT < DATE_ADD(NOW(), INTERVAL -6 MONTH);
    
    
    DELETE FROM NMS_WATCHDOG_QUERY_HIST WHERE UPD_DT < DATE_ADD(NOW(), INTERVAL -6 MONTH);
    
    
    DELETE FROM NMS_BACKUP_HIST WHERE END_DT < DATE_ADD(NOW(), INTERVAL -12 MONTH);
    
    
    DELETE FROM CONF_MCH_REQ WHERE UPD_DT < DATE_ADD(NOW(), INTERVAL -1 MONTH);
    IF NOT EXISTS( SELECT * FROM CONF_MCH_REQ WHERE RESULT_STATE = 0 ) THEN
      TRUNCATE TABLE CONF_MCH_REQ;
      TRUNCATE TABLE CONF_REQ;
    END IF;
    DELETE FROM CONF_MCH_RESULT WHERE UPD_DT < DATE_ADD(NOW(), INTERVAL -3 MONTH);
    DELETE FROM CONF_IF_RESULT WHERE UPD_DT < DATE_ADD(NOW(), INTERVAL -3 MONTH);
          
    
    DELETE FROM ERROR_DB WHERE REG_DT < DATE_ADD(NOW(), INTERVAL -2 MONTH);

    
    COMMIT;
    
    
    UPDATE NMS_DB.NMS_WATCHDOG_QUERY_HIST SET UPD_DT = NOW(), STATE = 1
    WHERE SP_CALL = @SP_CALL AND ST_DT = @ST_DATE;
    
  END IF;
	
  
END ;;
