CREATE DEFINER=`root`@`localhost` PROCEDURE `SP_NMS_DB_COLLECT_SVR_CHK_LIST`(
IN _PERIOD INT  
)
BEGIN





    
  DECLARE v_svr_id INT;
  DECLARE v_grp_id INT;
  DECLARE v_mch_cnt INT;
  DECLARE v_finish INT DEFAULT 0;
  
  DECLARE CUR_JOB CURSOR FOR
    SELECT S.SVR_ID, G.SVR_GRP_ID, (SELECT COUNT(*) FROM MCH_COLLECT_POLICY WHERE NMS_SVR_ID = S.SVR_ID) AS CNT 
    FROM NMS_SVR_INFO S
    INNER JOIN NMS_SVR_GRP_OBJ G ON S.SVR_ID = G.SVR_ID AND G.APPLY_PC > 0
    WHERE S.ST_UPD_DT < DATE_ADD(NOW(), INTERVAL -_PERIOD MINUTE);
      
  DECLARE CONTINUE HANDLER FOR NOT FOUND SET v_finish = 1;
  
  SET @UPD_ROW = 0;
  
  OPEN CUR_JOB;
  
  LOOP_JOB : LOOP
  
    FETCH CUR_JOB INTO v_svr_id, v_grp_id, v_mch_cnt;
    
    IF v_finish = 1 THEN
      LEAVE LOOP_JOB;
    END IF;
    
    SELECT COUNT(*) INTO @OBJ_CNT FROM NMS_SVR_GRP_OBJ WHERE SVR_GRP_ID = v_grp_id;

    
    IF v_mch_cnt > 0 AND v_svr_id > 0 AND v_grp_id > 0 AND @OBJ_CNT > 1 THEN
      
      CALL SP_NMS_DB_COLLECT_SVR_SNAPSHOT_CREATE(v_svr_id, 2, '이슈 발생전 자동 백업(스케줄)', @RESULT, @ERROR_CD);
      CALL SP_NMS_DB_COLLECT_SVR_CHK_TRANSFER( v_svr_id, v_grp_id, _PERIOD );
    END IF;    
            
  END LOOP LOOP_JOB;
  
  CLOSE CUR_JOB;
        
END ;;
