  SELECT @ROW := @ROW + 1 AS RNUM, MCH_ID, NMS_SVR_ID FROM MCH_COLLECT_POLICY 
  WHERE NMS_SVR_ID = _SVR_ID;
    
  
  
  
  
  
  DELETE FROM TMP_SVR_CHK 
