    SELECT NS.MCH_ID FROM NMS_SVR_INFO NS
    INNER JOIN MCH_COLLECT_POLICY MC ON NS.MCH_ID = MC.MCH_ID
    WHERE MC.PERF_COLLECT_YN = 'N');
  
  
  SELECT COUNT(*) INTO @TOT_MCH_CNT FROM TMP_SVR_CHK;
  
  
  SELECT SUM(APPLY_PC) INTO @SUM_APPLY_PC FROM NMS_SVR_GRP_OBJ 
  WHERE SVR_GRP_ID = _SVR_GRP_ID AND SVR_ID != _SVR_ID;
  
  
  SELECT COUNT(*) INTO @SVR_GRP_CNT FROM NMS_SVR_GRP_OBJ 
  WHERE SVR_GRP_ID = _SVR_GRP_ID AND SVR_ID != _SVR_ID;
      
  
  CALL SP_NMS_DB_COLLECT_SVR_CHK_TRANSFER_CALC( _SVR_ID, _SVR_GRP_ID, @SVR_GRP_CNT, @TOT_MCH_CNT, @SUM_APPLY_PC, _PERIOD );
      
END ;;
