CREATE DEFINER=`root`@`localhost` PROCEDURE `SP_NMS_DB_COLLECT_SVR_CHK_TRANSFER_CALC`(
IN _SVR_ID INT,         
IN _SVR_GRP_ID INT,     
IN _SVR_GRP_CNT INT,    
IN _TOTAL_MCH_CNT INT,  
IN _SUM_APPLY_PC INT,   
IN _PERIOD INT          
)
BEGIN





    
  DECLARE v_svr_id INT;
  DECLARE v_apply_pc INT;
  DECLARE v_finish INT DEFAULT 0;
  
  DECLARE CUR_JOB CURSOR FOR
    SELECT O.SVR_ID, O.APPLY_PC FROM NMS_SVR_GRP_OBJ O
    INNER JOIN NMS_SVR_INFO S ON O.SVR_ID = S.SVR_ID AND S.ST_UPD_DT > DATE_ADD(NOW(), INTERVAL -_PERIOD MINUTE)
    WHERE O.SVR_GRP_ID = _SVR_GRP_ID AND O.SVR_ID != _SVR_ID;
      
  DECLARE CONTINUE HANDLER FOR NOT FOUND SET v_finish = 1;
  
  SET @UPD_ROW = 0;
  SET @LAST_SVR_ID = 0;
  
  OPEN CUR_JOB;
  
  LOOP_JOB : LOOP
  
    FETCH CUR_JOB INTO v_svr_id, v_apply_pc;
    
    IF v_finish = 1 THEN
      LEAVE LOOP_JOB;
    END IF;
    
    SET @LAST_SVR_ID = v_svr_id;
    IF _SUM_APPLY_PC > 0 THEN
      SET @APPLY_NUM = ( v_apply_pc / _SUM_APPLY_PC * _TOTAL_MCH_CNT );
    ELSE
      SET @APPLY_NUM = ( 1 / _SVR_GRP_CNT * _TOTAL_MCH_CNT );
    END IF;
  
    UPDATE TMP_SVR_CHK SET NMS_SVR_ID = v_svr_id
    WHERE RNUM > @UPD_ROW AND RNUM <= ( @UPD_ROW + @APPLY_NUM );
    
    SET @UPD_ROW = @UPD_ROW + @APPLY_NUM;
        
  END LOOP LOOP_JOB;
  
  CLOSE CUR_JOB;
  
  
  IF @LAST_SVR_ID > 0 THEN
    UPDATE TMP_SVR_CHK SET NMS_SVR_ID = @LAST_SVR_ID
    WHERE NMS_SVR_ID = _SVR_ID;
  END IF;
    
  
  INSERT INTO MCH_COLLECT_TRANS ( SVR_ID, MCH_ID )
  SELECT _SVR_ID, MCH_ID FROM TMP_SVR_CHK WHERE NMS_SVR_ID != _SVR_ID;
  
  
  UPDATE MCH_COLLECT_POLICY S, TMP_SVR_CHK D
  SET S.NMS_SVR_ID = D.NMS_SVR_ID, S.UPD_DT = NOW()
  WHERE S.MCH_ID = D.MCH_ID AND S.NMS_SVR_ID != D.NMS_SVR_ID;  
      
END ;;
