CREATE DEFINER=`root`@`localhost` PROCEDURE `SP_NMS_DEL_EXPIRED_ACCOUNT`()
BEGIN
  DECLARE _DELACCOUNT_DAY INT;
  DECLARE _DELACCOUNT_YN VARCHAR(10);
  DECLARE _DELACCOUNT_CNT INT;
  DECLARE _DELACCOUNT VARCHAR(50);

  SET _DELACCOUNT_DAY = (SELECT DELACCOUNT_DAY FROM LOGIN_POLICY);
  SET _DELACCOUNT_YN = (SELECT DELACCOUNT_YN FROM LOGIN_POLICY);
  SET _DELACCOUNT_CNT = (SELECT COUNT(DISTINCT LH.USER_ID) FROM LOGIN_HIST LH INNER JOIN USER_INFO UI ON LH.USER_ID = UI.USER_ID WHERE LH.LOGIN_DT< DATE_SUB(NOW(), INTERVAL 1 MONTH) AND LOGIN_GB = 1 AND LH.USER_ID != 'm_user');
  

	START TRANSACTION;
  
