		    DELETE FROM MAP_USER_ACCESS_INFO WHERE USER_ID = _DELACCOUNT;
		    DELETE FROM POPUP_USER_FIELD_ORDER_MNG WHERE USER_ID = _DELACCOUNT;
        SET _DELACCOUNT_CNT = _DELACCOUNT_CNT -1 ; 
        
