CREATE DEFINER=`root`@`localhost` PROCEDURE `SP_NMS_EVENT_EVENT_HIST_DEL`(
IN _EVENT_ID BIGINT,
OUT _RESULT CHAR(1),
OUT _ERROR_CD VARCHAR(256)
)
ERR_TRAN: BEGIN






  
	DECLARE EXIT HANDLER FOR SQLEXCEPTION
	BEGIN
		ROLLBACK;
		SET _RESULT = 'N';
    SET _ERROR_CD = 'NM20001';

    
    GET DIAGNOSTICS CONDITION 1 @P1 = MYSQL_ERRNO, @P2 = MESSAGE_TEXT;        
    INSERT INTO NMS_DB.ERROR_DB(ERR_TYPE,ERR_DEST,ERR_CD,ERR_MSG) 
    VALUES('SP','SP_NMS_EVENT_EVENT_HIST_DEL',@P1,@P2);
	END;
  
  
  IF NOT EXISTS (SELECT * FROM NMC_DB.EVENT_HIST WHERE EVENT_ID = _EVENT_ID LIMIT 1) THEN
    SET _RESULT = 'N';
    SET _ERROR_CD = 'NM20006';
    LEAVE ERR_TRAN;
  END IF;
  
  
	START TRANSACTION;
  
  
  DELETE FROM NMS_DB.EVENT_UACT_LINK WHERE EVENT_ID = _EVENT_ID;
  DELETE FROM NMS_DB.EVENT_UACT_EXTIME WHERE EVENT_ID = _EVENT_ID;
  DELETE FROM NMC_DB.EVENT_UACT_HIST WHERE EVENT_ID = _EVENT_ID;
  
  
  DELETE FROM NMC_DB.EVENT_HIST WHERE EVENT_ID = _EVENT_ID;
  
  
  COMMIT;
  
  SET _RESULT = 'Y';
  SET _ERROR_CD = '';
  
END ;;
