CREATE DEFINER=`root`@`localhost` PROCEDURE `SP_NMS_FLOW_HOST_SUMMARY`(
IN _MCH_ID INT,
IN _MCH_IP VARCHAR(24),
IN _FLOW_10M_TOTAL INT,
IN _UPD_DT VARCHAR(24)
)
ERR_TRAN: BEGIN
  
	DECLARE EXIT HANDLER FOR SQLEXCEPTION
	BEGIN
		ROLLBACK;

		GET DIAGNOSTICS CONDITION 1 @P1 = MYSQL_ERRNO, @P2 = MESSAGE_TEXT;        
	    INSERT INTO NMS_DB.ERROR_DB(ERR_TYPE,ERR_DEST,ERR_CD,ERR_MSG) 
	    VALUES('SP','SP_NMS_FLOW_HOST_10M_SUMMARY',@P1,@P2);
	END;

    START TRANSACTION;

    SET @UPD_DT = STR_TO_DATE(_UPD_DT, '%Y-%m-%d %H:%i:%s');
   
    SELECT COUNT(*) INTO @YN_EXIST FROM NMS_DB.FLOW_HOST_LIST WHERE MCH_IP = _MCH_IP;
    IF @YN_EXIST = 0 THEN
    	INSERT INTO NMS_DB.FLOW_HOST_LIST(MCH_IP, MCH_ID, FLOW_10M_UPD_DT, FLOW_10M_TOTAL, FLOW_10M_CALL_CNT, FLOW_10M_AVG)
    	VALUES (_MCH_IP, _MCH_ID, @UPD_DT, _FLOW_10M_TOTAL, 1, _FLOW_10M_TOTAL);
    ELSE
    	SET @NOW_HOUR = HOUR(@UPD_DT);
    	SET @NOW_DAY = DAY(@UPD_DT);
   
		
    	
    	SELECT FLOW_10M_TOTAL, FLOW_10M_CALL_CNT, FLOW_1H_TOTAL, FLOW_1H_CALL_CNT, FLOW_1H_AVG, FLOW_1D_TOTAL, HOUR(FLOW_10M_UPD_DT), DAY(FLOW_1H_UPD_DT)
  		INTO @FLOW_10M_TOTAL, @FLOW_10M_CALL_CNT, @FLOW_1H_TOTAL, @FLOW_1H_CALL_CNT, @FLOW_1H_AVG, @FLOW_1D_TOTAL, @FLOW_HOUR_IN_10M, @FLOW_DAY_IN_1H 
  		FROM NMS_DB.FLOW_HOST_LIST WHERE MCH_IP = _MCH_IP;
	  	

  		SET @NOW_10M_TOTAL = 0;	
    	SET @NOW_10M_CALL_CNT = 0;
        SET @NOW_10M_AVG = 0;
  		SET @NOW_1H_TOTAL = 0;	
    	SET @NOW_1H_CALL_CNT = 0;
        SET @NOW_1H_AVG = 0;
        SET @NOW_1D_TOTAL = 0;

  		
  		
  	    
        IF @NOW_HOUR != @FLOW_HOUR_IN_10M THEN
        	
 	        SET @NOW_1H_TOTAL = @FLOW_1H_TOTAL + @FLOW_10M_TOTAL;	
	    	SET @NOW_1H_CALL_CNT = 1 + @FLOW_1H_CALL_CNT;
	        SET @NOW_1H_AVG = @NOW_1H_TOTAL / @NOW_1H_CALL_CNT;

            
        	SET @NOW_10M_TOTAL = _FLOW_10M_TOTAL;
        	SET @NOW_10M_CALL_CNT = 1;
	        SET @NOW_10M_AVG = @NOW_10M_TOTAL;
        ELSE
            
        	SET @NOW_1H_TOTAL = @FLOW_1H_TOTAL;	
	    	SET @NOW_1H_CALL_CNT = @FLOW_1H_CALL_CNT;
	        SET @NOW_1H_AVG = @FLOW_1H_AVG;
	       
        	
	        SET @NOW_10M_TOTAL = _FLOW_10M_TOTAL + @FLOW_10M_TOTAL;	
	    	SET @NOW_10M_CALL_CNT = 1 + @FLOW_10M_CALL_CNT;
	        SET @NOW_10M_AVG = @NOW_10M_TOTAL / @NOW_10M_CALL_CNT;
        END IF;
        
  	
        
        
        
        IF @NOW_DAY != @FLOW_DAY_IN_1H THEN
        	
	        SET @NOW_1D_TOTAL = @NOW_1H_TOTAL;
	    
            
        	SET @NOW_1H_TOTAL = 0;
        	SET @NOW_1H_CALL_CNT = 0;
	        SET @NOW_1H_AVG = 0;
        END IF;
 		
        
  		INSERT INTO NMS_DB.FLOW_HOST_LIST(MCH_IP, MCH_ID, 
  											FLOW_10M_UPD_DT, FLOW_10M_TOTAL, FLOW_10M_CALL_CNT, FLOW_10M_AVG, 
  											FLOW_1H_UPD_DT, FLOW_1H_TOTAL, FLOW_1H_CALL_CNT, FLOW_1H_AVG, 
  											FLOW_1D_UPD_DT, FLOW_1D_TOTAL ) 
	  		VALUE (_MCH_IP, _MCH_ID, @UPD_DT, @NOW_10M_TOTAL, @NOW_10M_CALL_CNT, @NOW_10M_AVG, 
  									 @UPD_DT, @NOW_1H_TOTAL, @NOW_1H_CALL_CNT, @NOW_1H_AVG, 
  									 @UPD_DT, @NOW_1H_TOTAL ) 
  			ON DUPLICATE KEY UPDATE FLOW_10M_UPD_DT = @UPD_DT, FLOW_10M_TOTAL = @NOW_10M_TOTAL, FLOW_10M_CALL_CNT = @NOW_10M_CALL_CNT, FLOW_10M_AVG = @NOW_10M_AVG, 
  									FLOW_1H_UPD_DT = @UPD_DT, FLOW_1H_TOTAL = @NOW_1H_TOTAL, FLOW_1H_CALL_CNT = @NOW_1H_CALL_CNT, FLOW_1H_AVG = @NOW_1H_AVG, 
  									FLOW_1D_UPD_DT = @UPD_DT, FLOW_1D_TOTAL = @NOW_1D_TOTAL;
  								
    END IF;
   
    COMMIT;

END ;;
DELIMITER ;
