CREATE DEFINER=`root`@`localhost` PROCEDURE `SP_NMS_INVENTORY_GET_SVR_FOR_ADD_MCH`(
IN  _SVR_GRP_ID INT,          
OUT _SVR_ID INT,              
OUT _RESULT CHAR(1),
OUT _ERROR_CD VARCHAR(256)
)
ERR_TRAN: BEGIN







  
	DECLARE EXIT HANDLER FOR SQLEXCEPTION
	BEGIN
		ROLLBACK;
		SET _RESULT = 'N';
    SET _ERROR_CD = 'NM20001';

    
    GET DIAGNOSTICS CONDITION 1 @P1 = MYSQL_ERRNO, @P2 = MESSAGE_TEXT;        
    INSERT INTO NMS_DB.ERROR_DB(ERR_TYPE,ERR_DEST,ERR_CD,ERR_MSG) 
    VALUES('SP','SP_NMS_INVENTORY_GET_SVR_FOR_ADD_MCH',@P1,@P2);
	END;
  
  
  IF NOT EXISTS (SELECT * FROM NMS_SVR_GRP WHERE SVR_GRP_ID = _SVR_GRP_ID) THEN
    SET _RESULT = 'N';
    SET _ERROR_CD = 'NM20016';
    LEAVE ERR_TRAN;
  END IF;
  
  SELECT SUM(O.APPLY_PC) INTO @SUM_PC
  FROM NMS_SVR_GRP_OBJ O
  INNER JOIN NMS_SVR_INFO S ON O.SVR_ID = S.SVR_ID
  WHERE O.SVR_GRP_ID = _SVR_GRP_ID;
  
  SELECT T.SVR_ID, T.APP_PC * T.CNT AS RST_PC
  INTO @SVR_ID, @RST_PC
  FROM
  (
  SELECT O.SVR_ID, 1-(O.APPLY_PC / @SUM_PC) AS APP_PC, 
  (SELECT COUNT(MCH_ID) FROM MCH_COLLECT_POLICY WHERE NMS_SVR_ID = O.SVR_ID AND MNG_YN = 'Y' AND PERF_COLLECT_YN = 'Y') AS CNT
  FROM NMS_SVR_GRP_OBJ O
  INNER JOIN NMS_SVR_INFO S ON O.SVR_ID = S.SVR_ID
  WHERE O.SVR_GRP_ID = _SVR_GRP_ID
  ) T
  ORDER BY RST_PC ASC LIMIT 1; 
  
  IF( @SVR_ID > 0 ) THEN
    SET _SVR_ID = @SVR_ID;
  END IF;
    
  SET _RESULT = 'Y';
  SET _ERROR_CD = '';
  
END ;;
