    SET _RESULT = 'N';
    SET _ERROR_CD = 'NM20003';
    LEAVE ERR_TRAN;
  END IF;
  
  SET @MCH_ID = 0;
  
  
  SET @MCH_ID = '';
  SET @MCH_NM = '';
  SET @MCH_IP = '';
  SET @IF_NM = '';
  SET @UI_ALIAS = '';
  SELECT I.MCH_ID, T.TARGET_NM, M.MCH_IP, I.IF_NM, I.UI_ALIAS
  INTO @MCH_ID, @MCH_NM, @MCH_IP, @IF_NM, @UI_ALIAS 
  FROM MCH_IF_INFO I 
  INNER JOIN TARGET_INFO T ON I.MCH_ID = T.TARGET_ID AND T.TARGET_CD = '01'
  INNER JOIN MCH_INFO M ON I.MCH_ID = M.MCH_ID
  WHERE I.IF_ID = _IF_ID;
  
  
