IN _MCH_ID INT,
IN _MODEL_ID INT,
OUT _RESULT CHAR(1),
OUT _ERROR_CD VARCHAR(256)
)
ERR_TRAN: BEGIN








  
	DECLARE EXIT HANDLER FOR SQLEXCEPTION
	BEGIN
		ROLLBACK;
		SET _RESULT = 'N';
    SET _ERROR_CD = 'NM20001';

    
    GET DIAGNOSTICS CONDITION 1 @P1 = MYSQL_ERRNO, @P2 = MESSAGE_TEXT;        
    INSERT INTO NMS_DB.ERROR_DB(ERR_TYPE,ERR_DEST,ERR_CD,ERR_MSG) 
    VALUES('SP','SP_NMS_INVENTORY_MCH_MODEL_CHG',@P1,@P2);
	END;
  
  
  IF NOT EXISTS (SELECT * FROM MCH_INFO WHERE MCH_ID = _MCH_ID) THEN
    SET _RESULT = 'N';
    SET _ERROR_CD = 'NM20002';
    LEAVE ERR_TRAN;
  END IF;
  
  
  IF NOT EXISTS (SELECT * FROM MCH_MODEL_INFO WHERE MODEL_ID = _MODEL_ID) THEN
    SET _RESULT = 'N';
    SET _ERROR_CD = 'NM20014';
    LEAVE ERR_TRAN;
  END IF;
  
  
	START TRANSACTION;
  
  
  SET @CPU_ID = 0;
  SET @FLAG_W = 0;
  SET @ETC_CNT = 0;
  SET @CPU_RULE = '';
  SELECT CPU_ID, OID_CPU INTO @CPU_ID, @OID_CPU FROM MCH_CPU WHERE MCH_ID = _MCH_ID AND CPU_GB = 'T';
  SELECT COUNT(CPU_ID) INTO @ETC_CNT FROM MCH_CPU WHERE MCH_ID = _MCH_ID AND CPU_GB != 'T'; 
  SELECT OID_CPU, POSITION('.w' IN OID_CPU), CPU_RULE INTO @OID_CPU_CHG, @FLAG_W, @CPU_RULE FROM MCH_MODEL_INFO WHERE MODEL_ID = _MODEL_ID;
    
  
  
  
  IF @CPU_ID > 0 AND LENGTH(@OID_CPU) < 10 AND LENGTH(@OID_CPU_CHG) > 10 AND @FLAG_W = 0 AND @ETC_CNT = 0 THEN
    UPDATE MCH_CPU SET OID_CPU = @OID_CPU_CHG, CPU_RULE = @CPU_RULE, UPD_DT=NOW() WHERE CPU_ID = @CPU_ID;
  END IF;
  
  
  SET @MEMORY_ID = 0;
  SET @FLAG_W = 0;
  SET @MEMORY_RULE = '';
  SET @TOTAL_RULE = '';
  SET @MEMORY_CALC = 1;
  SELECT MEMORY_ID, OID_MEMORY INTO @MEMORY_ID, @OID_MEMORY FROM MCH_MEMORY WHERE MCH_ID = _MCH_ID AND MEMORY_GB = 'M' AND DEL_YN = 'N';
  SELECT OID_MEMORY, POSITION('.w' IN OID_MEMORY), MEMORY_RULE, TOTAL_RULE, MEMORY_CALC INTO @OID_MEMORY_CHG, @FLAG_W, @MEMORY_RULE, @TOTAL_RULE, @MEMORY_CALC
  FROM MCH_MODEL_INFO WHERE MODEL_ID = _MODEL_ID;
    
  
  
  IF @MEMORY_ID > 0 AND LENGTH(@OID_MEMORY) < 10 AND LENGTH(@OID_MEMORY_CHG) > 10 AND @FLAG_W = 0 THEN
    UPDATE MCH_MEMORY SET OID_MEMORY = @OID_MEMORY_CHG, MEMORY_RULE = @MEMORY_RULE, TOTAL_RULE = @TOTAL_RULE, MEMORY_CALC = @MEMORY_CALC, UPD_DT=NOW() 
    WHERE MEMORY_ID = @MEMORY_ID;
  END IF;

  
  DELETE FROM CONF_MCH_CHG_MODEL WHERE MCH_ID = _MCH_ID;  
  
  
  COMMIT;
  
  SET _RESULT = 'Y';
  SET _ERROR_CD = '';
  
END ;;
