CREATE DEFINER=`root`@`localhost` PROCEDURE `SP_NMS_INVENTORY_POLICY_SYNC_CHK`(
IN _MCH_ID INT,
IN _POLICY_ID INT,
IN _USER_ID VARCHAR(50),
IN _BPS_STATE VARCHAR(2),
IN _PPS_STATE VARCHAR(2),
IN _ERROR_STATE VARCHAR(2),
IN _CRC_STATE VARCHAR(2),
IN _DISCARD_STATE VARCHAR(2),
IN _COLLI_STATE VARCHAR(2),
OUT _RESULT CHAR(1),
OUT _ERROR_CD VARCHAR(256)
)
ERR_TRAN: BEGIN









  
  
  
  DECLARE _PERF_POLL_INTERVAL INT;  
  DECLARE _ICMP_TMPL_ID INT;        
  DECLARE _SNMP_TMPL_ID INT;        
  DECLARE _MCH_PERF_TMPL_ID INT;    
  DECLARE _IF_PERF_TMPL_ID INT;     
  DECLARE _MCH_MNG_LEVEL INT;       
  DECLARE _IF_MNG_LEVEL INT;        
  DECLARE _UDI_TMPL_ID INT;         
  DECLARE _CONFIBACK_TMPL_ID INT;   
  DECLARE _NOTI_TMPL_ID INT;        
  DECLARE _IF_PERF_SETUP VARCHAR(200);    

  DECLARE v_finish INT DEFAULT 0;
  
  
  DECLARE _IF_ID INT;             
  DECLARE _SNMP_TMPL_ID_IF INT;
  DECLARE _PERF_TMPL_ID_IF INT;
  DECLARE _UDI_TMPL_ID_IF INT;
  DECLARE _MNG_LEVEL_IF INT;
  DECLARE _PERF_COLLECT_YN VARCHAR(2);
  DECLARE _BPS VARCHAR(2);
  DECLARE _PPS VARCHAR(2);
  DECLARE _ERROR VARCHAR(2);
  DECLARE _CRC VARCHAR(2);
  DECLARE _DISCARD VARCHAR(2);
  DECLARE _COLLI VARCHAR(2);
  DECLARE _INSERT_PERF_COLLECT_YN VARCHAR(2);
  
  
  DECLARE _TOTAL_SYNC_PC FLOAT;   
  DECLARE _MCH_TOTAL_SUM FLOAT;   
  DECLARE _IF_TOTAL_SUM FLOAT;    
  
  
  DECLARE CUR_JOB CURSOR FOR 
  SELECT 
    A.if_id,
    snmp_tmpl_id,
    perf_tmpl_id,
    udi_tmpl_id,
	  perf_collect_yn,        
    mng_level,
    IF(oid_bps_in = '1' AND oid_bps_out = '1' ,'N','Y') AS bps,
    IF(oid_pps_in = '1' AND oid_pps_out = '1' ,'N','Y') AS pps,
    IF(oid_discard_in = '1' AND oid_discard_out = '1' ,'N','Y') AS discard,
    IF(oid_error_in = '1' AND oid_error_out = '1' ,'N','Y') AS error,
    IF(oid_crc = '1','N','Y') AS crc,
	  IF(oid_collision = '1','N','Y') AS collision
  FROM IF_COLLECT_POLICY A 
  INNER JOIN MCH_IF_MIB B ON A.IF_ID = B.IF_ID
  WHERE A.MCH_ID = _MCH_ID AND PERF_COLLECT_YN = 'Y';
      
  DECLARE CONTINUE HANDLER FOR NOT FOUND SET v_finish = 1;
	DECLARE EXIT HANDLER FOR SQLEXCEPTION
	BEGIN
		ROLLBACK;
		SET _RESULT = 'N';
    SET _ERROR_CD = 'NM20001';

    
    GET DIAGNOSTICS CONDITION 1 @P1 = MYSQL_ERRNO, @P2 = MESSAGE_TEXT;        
    INSERT INTO NMS_DB.ERROR_DB(ERR_TYPE,ERR_DEST,ERR_CD,ERR_MSG) 
    VALUES('SP','SP_NMS_INVENTORY_POLICY_SYNC_CHK',@P1,@P2);
	END;
  
  IF (_POLICY_ID = '0' AND _POLICY_ID = NULL) THEN
  
    SET _RESULT = 'N';
    SET _ERROR_CD = 'NM20002';
    LEAVE ERR_TRAN;
    
  ELSE
    SELECT
      PERF_POLL_INTERVAL,ICMP_TMPL_ID,SNMP_TMPL_ID,MCH_PERF_TMPL_ID,IF_PERF_TMPL_ID,MCH_MNG_LEVEL,IF_MNG_LEVEL,UDI_TMPL_ID,CONFIBACK_TMPL_ID,NOTI_TMPL_ID,IF_PERF_SETUP 
  INTO
      _PERF_POLL_INTERVAL,_ICMP_TMPL_ID,_SNMP_TMPL_ID,_MCH_PERF_TMPL_ID,_IF_PERF_TMPL_ID,_MCH_MNG_LEVEL,_IF_MNG_LEVEL,_UDI_TMPL_ID,_CONFIBACK_TMPL_ID,_NOTI_TMPL_ID,_IF_PERF_SETUP
    FROM REG_POLICY
    WHERE POLICY_ID = _POLICY_ID;
        
  END IF;
    
  OPEN CUR_JOB; 

    LOOP_JOB : LOOP
  
    FETCH CUR_JOB INTO _IF_ID,_SNMP_TMPL_ID_IF,_PERF_TMPL_ID_IF,_UDI_TMPL_ID_IF,_PERF_COLLECT_YN,_MNG_LEVEL_IF,_BPS,_PPS,_DISCARD,_ERROR,_CRC,_COLLI; 

    
    IF v_finish = 1 THEN
      LEAVE LOOP_JOB;
    END IF;

    SET _INSERT_PERF_COLLECT_YN = 'Y';
  
    
    IF _BPS_STATE IS NOT NULL THEN     
      SET _INSERT_PERF_COLLECT_YN = 'Y';  
    ELSE 
      IF _BPS = 'Y' THEN
        SET _INSERT_PERF_COLLECT_YN = 'N';    
      END IF;
    END IF;
      
    IF _INSERT_PERF_COLLECT_YN = 'Y' THEN
      IF _PPS_STATE IS NOT NULL THEN
            SET _INSERT_PERF_COLLECT_YN = 'Y';  
      ELSE 
        IF _PPS = 'Y' THEN
          SET _INSERT_PERF_COLLECT_YN = 'N';    
        END IF;
      END IF;
    END IF;

    IF _INSERT_PERF_COLLECT_YN = 'Y' THEN
      IF _ERROR_STATE IS NOT NULL THEN
            SET _INSERT_PERF_COLLECT_YN = 'Y';  
      ELSE 
        IF _ERROR = 'Y' THEN
          SET _INSERT_PERF_COLLECT_YN = 'N';    
        END IF;
      END IF;
    END IF;
    
    IF _INSERT_PERF_COLLECT_YN = 'Y' THEN
      IF _CRC_STATE IS NOT NULL THEN
            SET _INSERT_PERF_COLLECT_YN = 'Y';  
      ELSE 
        IF _CRC = 'Y' THEN
          SET _INSERT_PERF_COLLECT_YN = 'N';    
        END IF;
      END IF;
    END IF;
      
    IF _INSERT_PERF_COLLECT_YN = 'Y' THEN
      IF _DISCARD_STATE IS NOT NULL THEN
            SET _INSERT_PERF_COLLECT_YN = 'Y';  
      ELSE 
        IF _DISCARD = 'Y' THEN
          SET _INSERT_PERF_COLLECT_YN = 'N';    
        END IF;
      END IF;
    END IF;
    
    IF _INSERT_PERF_COLLECT_YN = 'Y' THEN
      IF _COLLI_STATE IS NOT NULL THEN
            SET _INSERT_PERF_COLLECT_YN = 'Y';  
      ELSE 
        IF _COLLI = 'Y' THEN
          SET _INSERT_PERF_COLLECT_YN = 'N';    
        END IF;
      END IF;
    END IF;
        
    
    INSERT INTO NMS_DB.IF_SYNC_POLICY 
    (USER_ID,IF_ID,MCH_ID,SNMP_TMPL_YN,PERF_TMPL_YN,PERF_COLL_YN,MNG_LEVEL_YN,SNMP_TMPL_ID,PERF_TMPL_ID,MNG_LEVEL,UPD_DT)
    VALUES
    (
    _USER_ID,
    _IF_ID,
    _MCH_ID,
    IF(_SNMP_TMPL_ID = _SNMP_TMPL_ID_IF,'Y','N'),
    IF(_IF_PERF_TMPL_ID = _PERF_TMPL_ID_IF,'Y','N'),
    _INSERT_PERF_COLLECT_YN,
    IF(_IF_MNG_LEVEL = _MNG_LEVEL_IF,'Y','N'),
    _SNMP_TMPL_ID_IF,
    _PERF_TMPL_ID_IF,
    _MNG_LEVEL_IF,
    NOW()
    );
  
  END LOOP LOOP_JOB;
  
  CLOSE CUR_JOB;  
  
  
  INSERT INTO MCH_SYNC_POLICY
  (
  USER_ID,MCH_ID,PERF_POLL_INTR_YN,ICMP_TMPL_YN,PERF_TMPL_YN,UDI_TMPL_YN,CONFIBACK_TMPL_YN,NOTI_TMPL_YN,MNG_LEVEL_YN,
  PERF_POLL_INTERVAL,ICMP_TMPL_ID,PERF_TMPL_ID,UDI_TMPL_ID,CONFIBACK_TMPL_ID,NOTI_TMPL_ID,MNG_LEVEL,UPD_DT
  )
  SELECT
  _USER_ID,
  _MCH_ID,
  IF(PERF_POLL_INTERVAL = _PERF_POLL_INTERVAL ,'Y','N'),
  IF(ICMP_TMPL_ID = _ICMP_TMPL_ID ,'Y','N'),
  IF(PERF_TMPL_ID = _MCH_PERF_TMPL_ID ,'Y','N'),
  IF(UDI_TMPL_ID = _UDI_TMPL_ID ,'Y','N'),
  IF(CONFIBACK_TMPL_ID = _CONFIBACK_TMPL_ID ,'Y','N'),
  IF(NOTI_TMPL_ID = _NOTI_TMPL_ID ,'Y','N'),
  IF(MNG_LEVEL = _MCH_MNG_LEVEL ,'Y','N'),
  PERF_POLL_INTERVAL,
  ICMP_TMPL_ID,
  PERF_TMPL_ID,
  UDI_TMPL_ID,
  CONFIBACK_TMPL_ID,
  NOTI_TMPL_ID,
  MNG_LEVEL,
  NOW()
  FROM MCH_COLLECT_POLICY
  WHERE MCH_ID = _MCH_ID;

  
  SELECT
  ROUND((PERF_POLL_INTR_YN+ICMP_TMPL_YN+PERF_TMPL_YN+UDI_TMPL_YN+CONFIBACK_TMPL_YN+NOTI_TMPL_YN+MNG_LEVEL_YN)/7 * 100,1) INTO _MCH_TOTAL_SUM
  FROM
  (SELECT 
  IF(PERF_POLL_INTR_YN = 'Y',1,0) AS PERF_POLL_INTR_YN,
  IF(ICMP_TMPL_YN = 'Y',1,0) AS ICMP_TMPL_YN,
  IF(PERF_TMPL_YN = 'Y',1,0) AS PERF_TMPL_YN,
  IF(UDI_TMPL_YN = 'Y',1,0) AS UDI_TMPL_YN,
  IF(CONFIBACK_TMPL_YN = 'Y',1,0) AS CONFIBACK_TMPL_YN,
  IF(NOTI_TMPL_YN = 'Y',1,0) AS NOTI_TMPL_YN,
  IF(MNG_LEVEL_YN = 'Y',1,0) AS MNG_LEVEL_YN
  FROM MCH_SYNC_POLICY
  WHERE MCH_ID = _MCH_ID
  AND USER_ID = _USER_ID
  ) T;
  
  
  SELECT 
  IFNULL(ROUND(K.IF_TOTALSUM/(4*K.IF_COUNT) * 100,1),0) INTO _IF_TOTAL_SUM
  FROM(
  	SELECT
  	(SNMP_TMPL_YN+PERF_TMPL_YN+PERF_COLL_YN+MNG_LEVEL_YN) AS IF_TOTALSUM,
  	(SELECT COUNT(*) FROM IF_SYNC_POLICY WHERE MCH_ID = _MCH_ID AND USER_ID = _USER_ID) AS IF_COUNT	
  	FROM(
  	SELECT 
  	SUM(IF(SNMP_TMPL_YN = 'Y',1,0)) AS SNMP_TMPL_YN,
  	SUM(IF(PERF_TMPL_YN = 'Y',1,0)) AS PERF_TMPL_YN,
  	SUM(IF(PERF_COLL_YN = 'Y',1,0)) AS PERF_COLL_YN,
  	SUM(IF(MNG_LEVEL_YN = 'Y',1,0)) AS MNG_LEVEL_YN
  	FROM IF_SYNC_POLICY
    WHERE MCH_ID = _MCH_ID
    AND USER_ID = _USER_ID
  	)T
  )K;
 
  
  UPDATE MCH_SYNC_POLICY SET 
  MCH_SYNC_PC = _MCH_TOTAL_SUM,
  IF_SYNC_PC = _IF_TOTAL_SUM,
  TOTAL_SYNC_PC = ROUND((_MCH_TOTAL_SUM + _IF_TOTAL_SUM) / 2,1)
  WHERE MCH_ID = _MCH_ID
  AND USER_ID = _USER_ID;
   
    
  COMMIT;
  
  SET _RESULT = 'Y';
  SET _ERROR_CD = '';
END ;;
DELIMITER ;
