  END IF;
  
  
  UPDATE NMS_DB.IF_COLLECT_POLICY  SET
  SNMP_TMPL_ID = _SNMP_TMPL_ID,
  PERF_TMPL_ID =_IF_PERF_TMPL_ID,  
  MNG_LEVEL = _IF_MNG_LEVEL,
  UPD_DT = NOW()
  WHERE MCH_ID = _MCH_ID AND PERF_COLLECT_YN = 'Y';

  
  IF _BPS_STATE IS NULL THEN  
    UPDATE NMS_DB.MCH_IF_MIB 
    SET OID_BPS_IN = '1', OID_BPS_OUT = '1', UPD_DT=NOW() 
    WHERE MCH_ID = _MCH_ID AND OID_BPS_IN != '1' AND IF_ID IN (SELECT IF_ID FROM NMS_DB.IF_COLLECT_POLICY WHERE PERF_COLLECT_YN = 'Y'); 
  END IF;
  IF _PPS_STATE IS NULL THEN  
    UPDATE NMS_DB.MCH_IF_MIB SET OID_PPS_IN = '1', OID_PPS_OUT = '1', UPD_DT=NOW() 
    WHERE MCH_ID = _MCH_ID AND OID_PPS_IN != '1' AND IF_ID IN (SELECT IF_ID FROM NMS_DB.IF_COLLECT_POLICY WHERE PERF_COLLECT_YN = 'Y'); 
