CREATE DEFINER=`root`@`localhost` PROCEDURE `SP_NMS_ISSUE_INSERT_CONFIBACK_SCH`()
ERR_TRAN: BEGIN







  
	DECLARE EXIT HANDLER FOR SQLEXCEPTION
	BEGIN
		ROLLBACK;

    
    GET DIAGNOSTICS CONDITION 1 @P1 = MYSQL_ERRNO, @P2 = MESSAGE_TEXT;        
    INSERT INTO NMS_DB.ERROR_DB(ERR_TYPE,ERR_DEST,ERR_CD,ERR_MSG) 
    VALUES('SP','SP_NMS_ISSUE_INSERT_CONFIBACK_SCH',@P1,@P2);
	END;
     
  
  START TRANSACTION;
  
  SET @END_DATE = NOW(); 
  SET @SEC = SECOND(@END_DATE);
  SET @END_DATE = DATE_ADD(@END_DATE, INTERVAL -@SEC SECOND);
  SET @MIN = MINUTE(@END_DATE);
  SET @END_DATE = DATE_ADD(@END_DATE, INTERVAL -@MIN MINUTE);
  SET @HOUR = HOUR(@END_DATE);
  SET @END_DATE  = DATE_ADD(@END_DATE, INTERVAL -@HOUR HOUR);
  SET @ST_DATE  = DATE_ADD(@END_DATE, INTERVAL -1 DAY);
  
  SET @WEEK = DAYOFWEEK(@ST_DATE);
  SET @DAY = DAY(@ST_DATE);
    
  SELECT COUNT(MC.MCH_ID) INTO @TOTAL
  FROM NMS_DB.MCH_COLLECT_POLICY MC
  INNER JOIN NMS_DB.CLI_TASK_TMPL CT ON MC.CONFIBACK_TMPL_ID = CT.TMPL_ID AND CT.USE_YN = 'Y'
  INNER JOIN NMS_DB.TIME_SETUP TS ON CT.TIME_TMPL_ID = TS.TMPL_ID
  WHERE MC.CONFIBACK_TMPL_ID > 0 
  AND ((TS.RUN_GB = 1 AND TS.SCH_TIME BETWEEN @ST_DATE AND @END_DATE)
  OR (TS.RUN_GB = 2 AND (TS.RUN_TYPE IN (0, 1) OR (TS.RUN_TYPE = 2 AND TS.SCH_WEEK = @WEEK) OR (TS.RUN_TYPE = 3 AND TS.SCH_DAY = @DAY))));
 
  SELECT COUNT(T.MCH_ID) INTO @SUCC
  FROM (
  SELECT CR.MCH_ID, CR.SUCC_YN
  FROM NMC_DB.CONFIBACK_HIST CR
  INNER JOIN NMS_DB.MCH_COLLECT_POLICY MC ON CR.MCH_ID = MC.MCH_ID
  INNER JOIN NMS_DB.CLI_TASK_TMPL CT ON MC.CONFIBACK_TMPL_ID = CT.TMPL_ID AND CT.USE_YN = 'Y'
  INNER JOIN NMS_DB.TIME_SETUP TS ON CT.TIME_TMPL_ID = TS.TMPL_ID
  WHERE MC.CONFIBACK_TMPL_ID > 0 
  AND ((TS.RUN_GB = 1 AND TS.SCH_TIME BETWEEN @ST_DATE AND @END_DATE)
  OR (TS.RUN_GB = 2 AND (TS.RUN_TYPE IN (0, 1) OR (TS.RUN_TYPE = 2 AND TS.SCH_WEEK = @WEEK) OR (TS.RUN_TYPE = 3 AND TS.SCH_DAY = @DAY))))
  AND CR.REG_DT BETWEEN @ST_DATE AND @END_DATE AND CR.SUCC_YN = 'Y'
  GROUP BY CR.MCH_ID
  ) T;
    
  IF @TOTAL < @SUCC THEN
    SET @TOTAL = @SUCC;
  END IF;  
  
  SET @FAIL = @TOTAL - @SUCC;
  

  
  
  IF @TOTAL > 0 THEN
    SET @MSG_VAL = CONCAT('', @SUCC,',', @FAIL,',', @TOTAL);
    INSERT INTO ISSUE_HIST (REG_DT, MSG_TYPE, MSG_CD, MSG_VAL, COLOR_TABLE) VALUES (NOW(), 'BACKUP', 'ui.label.issue.msg.confiback.summary', @MSG_VAL, '#8495a0');
  END IF;
  
  
  COMMIT;
  
END ;;
DELIMITER ;
