  SELECT COUNT(MCH_ID) INTO @MCH_DEL_CNT FROM NMC_DB.MCH_CHG_HIST 
  WHERE UPD_DT BETWEEN @ST_DATE AND @END_DATE AND CHG_GB = 'M' AND CHG_STATE = 4;
  
  SET @IF_ADD_CNT = 0;
  SET @IF_CHG_CNT = 0;
