CREATE DEFINER=`root`@`localhost` PROCEDURE `SP_NMS_LIC_INFO_UPDATE`()
BEGIN








  DECLARE _L4_CNT INT DEFAULT 0;
  DECLARE _APC_CNT INT DEFAULT 0;
  DECLARE _AP_CNT INT DEFAULT 0;

  
  
  
  SELECT COUNT(*) INTO @MCH_CNT
  FROM MCH_COLLECT_POLICY M
  LEFT OUTER JOIN MCH_ICMP_POLICY_TMPL P ON P.TMPL_ID = M.ICMP_TMPL_ID
    WHERE P.EVENT_USE_YN = 'Y' AND M.FAULT_COLLECT_YN = 'Y' AND M.MNG_YN = 'Y';
  
  

  
  
  SELECT COUNT(*) INTO @IF_CNT FROM IF_COLLECT_POLICY I
  LEFT OUTER JOIN MCH_COLLECT_POLICY M ON I.MCH_ID = M.MCH_ID
  LEFT OUTER JOIN IF_SNMP_POLICY_TMPL P ON P.TMPL_ID = I.SNMP_TMPL_ID
  WHERE P.EVENT_USE_YN = 'Y' AND M.FAULT_COLLECT_YN = 'Y' AND M.MNG_YN = 'Y';
  
  

  
  
  
  SET _L4_CNT   = IFNULL((SELECT COUNT(*) FROM SMCH_COLLECT_POLICY WHERE SMCH_GB = 'L'), 0) * 30;

  
  
  SET _APC_CNT  = IFNULL((SELECT COUNT(*) FROM SMCH_COLLECT_POLICY WHERE SMCH_GB = 'W'), 0) * 10;
  
  SET _AP_CNT  = IFNULL((SELECT COUNT(*) FROM MCH_UI_INFO WHERE MCH_TYPE_CD = 'WA_01'), 0);


  
  
  INSERT INTO NMS_DB.LIC_MODULE_USE(MODULE_TYPE, USE_CNT, UPD_DT)
  VALUES( 'MCH', @MCH_CNT, NOW())
  ON DUPLICATE KEY UPDATE USE_CNT = @MCH_CNT, UPD_DT = NOW();

  
  INSERT INTO NMS_DB.LIC_MODULE_USE(MODULE_TYPE, USE_CNT, UPD_DT)
  VALUES( 'IF', @IF_CNT, NOW())
  ON DUPLICATE KEY UPDATE USE_CNT = @IF_CNT, UPD_DT = NOW();  
  
  INSERT INTO NMS_DB.LIC_MODULE_USE(MODULE_TYPE, USE_CNT, UPD_DT)
  VALUES( 'L4', _L4_CNT, NOW())
  ON DUPLICATE KEY UPDATE USE_CNT = _L4_CNT, UPD_DT = NOW();
  
  INSERT INTO NMS_DB.LIC_MODULE_USE(MODULE_TYPE, USE_CNT, UPD_DT)
  VALUES( 'APC', _APC_CNT + _AP_CNT, NOW())
  ON DUPLICATE KEY UPDATE USE_CNT = _APC_CNT + _AP_CNT, UPD_DT = NOW();
  
  
  IF NOT EXISTS( SELECT * FROM LIC_HIST WHERE REG_DT > DATE_ADD(NOW(), INTERVAL -5 SECOND) ) THEN
    
    
    INSERT INTO LIC_HIST ( REG_DT, LIC_TYPE, LIC_GB, LIC_CNT, USE_CNT)
      SELECT   NOW(), A.LIC_TYPE, '1', A.LIC_CNT, ( SELECT IFNULL(SUM(C.USE_CNT), 0) 
                                                 FROM   LIC_MODULE B JOIN LIC_MODULE_USE C  
                                                 WHERE  B.MODULE_TYPE = C.MODULE_TYPE
                                                  AND    B.LIC_GB = '1' ) AS USE_CNT 
      FROM   LIC_INFO A
      WHERE  A.LIC_GB = '1'
      UNION  ALL
      SELECT NOW(), A.LIC_TYPE, A.LIC_GB, LIC_CNT, IFNULL(USE_CNT, 0) 
      FROM   LIC_INFO A LEFT OUTER JOIN LIC_MODULE_USE B ON A.LIC_TYPE = B.MODULE_TYPE
      WHERE  A.LIC_GB = '2'
      AND    A.USE_TYPE <> '1';
    
  END IF;
    
END ;;
