CREATE DEFINER=`root`@`localhost` PROCEDURE `SP_NMS_LIC_V2_INFO_UPDATE`()
BEGIN

  
  
  

    DECLARE _WA_01 INT DEFAULT 0;
    DECLARE _FAC_00 INT DEFAULT 0;
    DECLARE _IPT_12 INT DEFAULT 0;
    DECLARE _SMS_CNT INT DEFAULT 0;
    DECLARE _FMS_CNT INT DEFAULT 0;
    DECLARE _TMS_CNT INT DEFAULT 0;

    DECLARE WA_CHECK INT DEFAULT 0;
    DECLARE FAC_CHECK INT DEFAULT 0;
    DECLARE IPT_CHECK INT DEFAULT 0;
    DECLARE SMS_CHECK INT DEFAULT 0;
    DECLARE FMS_CHECK INT DEFAULT 0;
    DECLARE TMS_CHECK INT DEFAULT 0;

    SET WA_CHECK =  IFNULL((SELECT COUNT(*) FROM LIC_V2_INFO WHERE MODULE_TYPE = 'A'),0);
    SET FAC_CHECK =  IFNULL((SELECT COUNT(*) FROM LIC_V2_INFO WHERE MODULE_TYPE = 'C'),0);
    SET IPT_CHECK =  IFNULL((SELECT COUNT(*) FROM LIC_V2_INFO WHERE MODULE_TYPE = 'I'),0);
    SET SMS_CHECK =  IFNULL((SELECT COUNT(*) FROM LIC_V2_INFO WHERE MODULE_TYPE = 'S'),0);
    SET FMS_CHECK =  IFNULL((SELECT COUNT(*) FROM LIC_V2_INFO WHERE MODULE_TYPE = 'F'),0);
    SET TMS_CHECK =  IFNULL((SELECT COUNT(*) FROM LIC_V2_INFO WHERE MODULE_TYPE = 'T'),0);


    SET _WA_01 =  IFNULL((SELECT COUNT(*) FROM MCH_INFO MI 
    INNER JOIN MCH_UI_INFO MU ON MI.MCH_ID = MU.MCH_ID 
    INNER JOIN MCH_COLLECT_POLICY MC ON MI.MCH_ID = MC.MCH_ID 
    INNER JOIN MCH_ACCOUNT MA ON MI.MCH_ID = MA.MCH_ID 
    WHERE MC.MNG_YN = 'Y' AND MA.SNMP_TMPL_ID = 0 AND MU.MCH_TYPE_CD = 'WA_01'),0);

    SET _FAC_00 =  IFNULL((SELECT COUNT(*) FROM MCH_INFO MI 
    INNER JOIN MCH_UI_INFO MU ON MI.MCH_ID = MU.MCH_ID 
    INNER JOIN MCH_COLLECT_POLICY MC ON MI.MCH_ID = MC.MCH_ID 
    INNER JOIN MCH_ACCOUNT MA ON MI.MCH_ID = MA.MCH_ID 
    WHERE MC.MNG_YN = 'Y' AND MA.SNMP_TMPL_ID = 0 AND MU.MCH_TYPE_CD = '_FAC_00'),0);

    SET _IPT_12 =  IFNULL((SELECT COUNT(*) FROM MCH_INFO MI 
    INNER JOIN MCH_UI_INFO MU ON MI.MCH_ID = MU.MCH_ID 
    INNER JOIN MCH_COLLECT_POLICY MC ON MI.MCH_ID = MC.MCH_ID 
    INNER JOIN MCH_ACCOUNT MA ON MI.MCH_ID = MA.MCH_ID 
    WHERE MC.MNG_YN = 'Y' AND MA.SNMP_TMPL_ID = 0 AND MU.MCH_TYPE_CD = '_IPT_12'),0);
    
    SET _SMS_CNT =  IFNULL((SELECT COUNT(*) FROM MCH_INFO MI 
    INNER JOIN MCH_COLLECT_POLICY MC ON MI.MCH_ID = MC.MCH_ID 
    INNER JOIN SMCH_COLLECT_POLICY SM ON MI.MCH_ID = SM.MCH_ID AND SMCH_GB = 'S'
    INNER JOIN MCH_ACCOUNT MA ON MI.MCH_ID = MA.MCH_ID 
    WHERE MC.MNG_YN = 'Y' AND MA.SNMP_TMPL_ID > 0 AND MI.MCH_ID NOT IN (SELECT MCH_ID FROM NMS_SVR_INFO)),0);

    SET _FMS_CNT =  IFNULL((SELECT COUNT(*) FROM MCH_INFO MI 
    INNER JOIN MCH_COLLECT_POLICY MC ON MI.MCH_ID = MC.MCH_ID 
    INNER JOIN SMCH_COLLECT_POLICY SM ON MI.MCH_ID = SM.MCH_ID AND SMCH_GB IN ('U', 'C')
    INNER JOIN MCH_ACCOUNT MA ON MI.MCH_ID = MA.MCH_ID 
    WHERE MC.MNG_YN = 'Y'),0);
    
    SET _TMS_CNT =  IFNULL((SELECT COUNT(*) FROM MCH_INFO MI 
    INNER JOIN MCH_COLLECT_POLICY MC ON MI.MCH_ID = MC.MCH_ID 
    INNER JOIN FLOW_COLLECT_POLICY FM ON MI.MCH_ID = FM.MCH_ID
    WHERE MC.MNG_YN = 'Y' AND FM.USE_YN = 'Y'),0);

    IF(WA_CHECK = 0 ) THEN
        SET _WA_01 = 0;
    END IF;

    IF(FAC_CHECK = 0 ) THEN
        SET _FAC_00 = 0;
    END IF;

    IF(IPT_CHECK = 0 ) THEN
        SET _IPT_12 = 0;
    END IF;
    
    IF(SMS_CHECK = 0 ) THEN
        SET _SMS_CNT = 0;
    END IF;
    
    IF(FMS_CHECK = 0 ) THEN
        SET _FMS_CNT = 0;
    END IF;
    
    IF(TMS_CHECK = 0 ) THEN
        SET _TMS_CNT = 0;
    END IF;

    SELECT COUNT(*) INTO @MCH_CNT FROM MCH_INFO MI
    INNER JOIN MCH_UI_INFO MU ON MI.MCH_ID = MU.MCH_ID
    INNER JOIN MCH_COLLECT_POLICY MC ON MI.MCH_ID = MC.MCH_ID
    WHERE MC.MNG_YN = 'Y' AND MI.MCH_ID NOT IN (SELECT MCH_ID FROM NMS_SVR_INFO);

    SET @MCH_CNT = @MCH_CNT - _WA_01 - _FAC_00 - _IPT_12 - _SMS_CNT - _FMS_CNT;

    INSERT INTO NMS_DB.LIC_V2_MODULE_USE(MODULE_TYPE, USE_CNT, UPD_DT)
    VALUES( 'M', @MCH_CNT, NOW())
    ON DUPLICATE KEY UPDATE USE_CNT = @MCH_CNT, UPD_DT = NOW();

    INSERT INTO NMS_DB.LIC_V2_MODULE_USE(MODULE_TYPE, USE_CNT, UPD_DT)
    VALUES( 'A', _WA_01, NOW())
    ON DUPLICATE KEY UPDATE USE_CNT = _WA_01, UPD_DT = NOW();

    INSERT INTO NMS_DB.LIC_V2_MODULE_USE(MODULE_TYPE, USE_CNT, UPD_DT)
    VALUES( 'C', _FAC_00, NOW())
    ON DUPLICATE KEY UPDATE USE_CNT = _FAC_00, UPD_DT = NOW();

    INSERT INTO NMS_DB.LIC_V2_MODULE_USE(MODULE_TYPE, USE_CNT, UPD_DT)
    VALUES( 'I', _IPT_12, NOW())
    ON DUPLICATE KEY UPDATE USE_CNT = _IPT_12, UPD_DT = NOW();
    
