CREATE DEFINER=`root`@`localhost` PROCEDURE `SP_NMS_MCH_INNET_CHK_NH`(
IN _CON_GUBUN CHAR(1),
IN _UPPER_GROUP_ID INT ,
IN _UPPER_GROUP_NM	varchar(30),
IN _USER_ID varchar(30),
IN _RPT_TYPE_CD varchar(20)
)
ERR_TRAN: BEGIN
  
  
  DECLARE EXIT HANDLER FOR SQLEXCEPTION
	BEGIN
		ROLLBACK;
    
    GET DIAGNOSTICS CONDITION 1 @P1 = MYSQL_ERRNO, @P2 = MESSAGE_TEXT;        
    INSERT INTO NMS_DB.ERROR_DB(ERR_TYPE,ERR_DEST,ERR_CD,ERR_MSG) 
    VALUES('SP','SP_NMS_EVENTACT_MERGE',@P1,@P2);
	END;


  SET @CON_GUBUN = _CON_GUBUN;
  SET @UPPER_GROUP_ID = _UPPER_GROUP_ID;
  SET @UPPER_GROUP_NM = _UPPER_GROUP_NM;
  SET @USER_ID = _USER_ID;    
  SET @RPT_TYPE_CD = _RPT_TYPE_CD;




  IF @CON_GUBUN = 'I' THEN
      WITH RECURSIVE CTE (GROUP_ID, GROUP_CD, GROUP_NM, UPPER_GROUP_ID, UPPER_GROUP_NM) as (
        SELECT
            GROUP_ID,
            GROUP_CD,
            GROUP_NM,
            UPPER_GROUP_ID,
            (SELECT UGI.GROUP_NM FROM GROUP_INFO UGI WHERE UGI.GROUP_ID = GI.UPPER_GROUP_ID) AS UPPER_GROUP_NM
        FROM
            GROUP_INFO GI
        
        WHERE
            UPPER_GROUP_ID = @UPPER_GROUP_ID
        UNION ALL
        SELECT
            P.GROUP_ID,
            P.GROUP_CD,
            P.GROUP_NM,
            P.UPPER_GROUP_ID,
            (SELECT UGI.GROUP_NM FROM GROUP_INFO UGI WHERE UGI.GROUP_ID = P.UPPER_GROUP_ID) AS UPPER_GROUP_NM
        FROM       
            GROUP_INFO P
            INNER JOIN CTE ON P.UPPER_GROUP_ID = CTE.GROUP_ID
      )
      SELECT GROUP_CONCAT(GROUP_ID SEPARATOR ',') INTO @GROUP_ID FROM CTE;
    ELSEIF @CON_GUBUN = 'N' THEN
      WITH RECURSIVE CTE (GROUP_ID, GROUP_CD, GROUP_NM, UPPER_GROUP_ID, UPPER_GROUP_NM) as (
        SELECT
            *
        FROM
            (
            SELECT
                GROUP_ID,
                GROUP_CD,
                GROUP_NM,
                UPPER_GROUP_ID,
                (SELECT UGI.GROUP_NM FROM GROUP_INFO UGI WHERE UGI.GROUP_ID = GI.UPPER_GROUP_ID) AS UPPER_GROUP_NM
            FROM
                GROUP_INFO GI
            ) T
        WHERE
            UPPER_GROUP_NM = @UPPER_GROUP_NM
        UNION ALL
        SELECT
            P.GROUP_ID,
            P.GROUP_CD,
            P.GROUP_NM,
            P.UPPER_GROUP_ID,
            (SELECT UGI.GROUP_NM FROM GROUP_INFO UGI WHERE UGI.GROUP_ID = P.UPPER_GROUP_ID) AS UPPER_GROUP_NM
        FROM       
            GROUP_INFO P
            INNER JOIN CTE ON P.UPPER_GROUP_ID = CTE.GROUP_ID
      )
      SELECT GROUP_CONCAT(GROUP_ID SEPARATOR ',') INTO @GROUP_ID FROM CTE;
    END IF;
    
  
    START TRANSACTION;
    
    INSERT INTO MCH_INNET_CHK_TARGET_NH (
        REG_DT,
        RPT_TYPE_CD,
        GROUP_ID,
        GROUP_CD,
        GROUP_NM,
        UPPER_GROUP_ID,
        UPPER_GROUP_NM,
        ROUTER_CNT,
        SWITCH_CNT,
        CPU_NORMAL_YN,
        CPU_NORMAL_NM,
        MEM_NORMAL_YN,
        MEM_NORMAL_NM
    )
    SELECT
        DATE_FORMAT(NOW(), '%Y-%m-%d %H:00:00'),
        @RPT_TYPE_CD,
        TT1.GROUP_ID,
        TT1.GROUP_CD,
    		TT1.GROUP_NM,
        TT1.UPPER_GROUP_ID,
        TT1.UPPER_GROUP_NM,
        TT1.ROUTER_CNT,
        TT1.SWITCH_CNT,
        TT2.CPU_NORMAL_YN,
        IF(TT2.CPU_NORMAL_YN = 'Y','정상','비정상'),
        TT2.MEM_NORMAL_YN,
        IF(TT2.MEM_NORMAL_YN = 'Y','정상','비정상')
    FROM
    (
        SELECT
            CT.UPPER_GROUP_ID,
            CT.UPPER_GROUP_NM,
            CT.GROUP_ID,
            CT.GROUP_CD,
        		CT.GROUP_NM,
            IFNULL(SUM(CASE WHEN CT.MCH_GB_CD = '0_RUT' THEN IFNULL(CT.MCH_GB_CNT,0) END) ,0) AS ROUTER_CNT,
            IFNULL(SUM(CASE WHEN CT.MCH_GB_CD = '1_SW' THEN IFNULL(CT.MCH_GB_CNT,0) END) ,0) AS SWITCH_CNT
            
        FROM
            (
            SELECT
                C.UPPER_GROUP_ID,
                C.UPPER_GROUP_NM,
                C.GROUP_ID,
                C.GROUP_CD,
        		    C.GROUP_NM,
                M.MCH_GB_CD,
                COUNT(M.MCH_GB_CD) AS MCH_GB_CNT
            FROM      
              (
                  SELECT
                      GROUP_ID,
                      GROUP_NM,
                      GROUP_CD,
                      UPPER_GROUP_ID,
                      (SELECT UGI.GROUP_NM FROM GROUP_INFO UGI WHERE UGI.GROUP_ID = GI.UPPER_GROUP_ID) AS UPPER_GROUP_NM
                  FROM
                    GROUP_INFO GI
                  WHERE
                  FIND_IN_SET(GI.GROUP_ID, @GROUP_ID)
              ) C 
              LEFT OUTER JOIN (
                  SELECT
                    OBJ_ID,
                    GROUP_ID
                  FROM
                    GROUP_OBJ_LIST
                  WHERE
                    GROUP_ID IN (
                        SELECT
                         GROUP_ID
                        FROM
                         GROUP_INFO
                        WHERE               
                         GROUP_CD = 1 AND 
                         FIND_IN_SET(GROUP_ID, @GROUP_ID)
                    )
              )B  ON B.GROUP_ID = C.GROUP_ID AND C.GROUP_CD = 1  
              LEFT OUTER JOIN (
                  SELECT
                      MCH_ID,
                      MCH_GB_CD
                  FROM      
                      V_PRIV_T_MCH_INFO 
                  WHERE
                      MCH_GB_CD IN ('0_RUT', '1_SW')
              ) M ON M.MCH_ID = B.OBJ_ID
            WHERE
                FIND_IN_SET(C.GROUP_ID, @GROUP_ID)
            GROUP BY C.UPPER_GROUP_NM, C.GROUP_NM, M.MCH_GB_CD
            
            ) CT    
        GROUP BY UPPER_GROUP_NM, GROUP_NM
    ) TT1

    JOIN 

    (
        SELECT        
            C.UPPER_GROUP_ID,
            C.UPPER_GROUP_NM,
            C.GROUP_ID,
            C.GROUP_CD,
        		C.GROUP_NM,
        		IF(SUM(T.CPU_NORMAL) > 0, 'N', 'Y') AS CPU_NORMAL_YN,
            IF(SUM(T.MEM_NORMAL) > 0, 'N', 'Y') AS MEM_NORMAL_YN
        FROM
           (
              SELECT
                  GROUP_ID,
                  GROUP_NM,
                  GROUP_CD,
                  UPPER_GROUP_ID,
                  (SELECT UGI.GROUP_NM FROM GROUP_INFO UGI WHERE UGI.GROUP_ID = GI.UPPER_GROUP_ID) AS UPPER_GROUP_NM
              FROM
                GROUP_INFO GI
              WHERE
              FIND_IN_SET(GI.GROUP_ID, @GROUP_ID)
          ) C 
          LEFT OUTER JOIN (
              SELECT
                  OBJ_ID,
                  GROUP_ID
              FROM
                  GROUP_OBJ_LIST
              WHERE
                  GROUP_ID IN (
                      SELECT
                       GROUP_ID
                      FROM
                       GROUP_INFO
                      WHERE               
                       GROUP_CD = 1 AND 
                       FIND_IN_SET(GROUP_ID, @GROUP_ID)
                  )
              GROUP BY OBJ_ID
          )B ON B.GROUP_ID = C.GROUP_ID
          LEFT OUTER JOIN (
              SELECT
                  MCH_ID,
                  IF(CPU_USE_PC > 80, 1, 0) AS CPU_NORMAL,
                  IF(MEMORY_USE_PC > 80, 1, 0) AS MEM_NORMAL,
                  CPU_USE_PC,
                  MEMORY_USE_PC              
             	FROM
              	  PERF_L_MCH
          ) T  ON T.MCH_ID = B.OBJ_ID
          INNER JOIN (        
              SELECT
                  MCH_ID,
                  MCH_GB_CD
              FROM      
                  V_PRIV_T_MCH_INFO 
              WHERE
                  MCH_GB_CD IN ('0_RUT', '1_SW')
          )
          M ON T.MCH_ID = M.MCH_ID
          
        WHERE
          FIND_IN_SET(B.GROUP_ID, @GROUP_ID)
        GROUP BY UPPER_GROUP_NM, GROUP_NM


    ) TT2 ON TT1.GROUP_ID = TT2.GROUP_ID;
    
    COMMIT;
    
    SELECT
        @GROUP_ID AS '** DEBUG:',
        CONCAT("** TABLE COUNT ** :" , (SELECT COUNT(*) FROM MCH_INNET_CHK_TARGET_NH)) AS '** TABLE COUNT :'
    ;
   
  
END ;;
DELIMITER ;
