CREATE DEFINER=`root`@`localhost` PROCEDURE `SP_NMS_RACK_INFO`(
    IN _RACK_OBJ_ID BIGINT,
    OUT _RESULT CHAR(1),
    OUT _ERROR_CD VARCHAR(256)
)
ERR_TRAN:
BEGIN

    DECLARE _UNIT_CNT INT;
    DECLARE _RACK_INDEX INT DEFAULT 1;
    DECLARE _UNIT_LABEL_INDEX INT DEFAULT 0;

    DECLARE _MOUNT_POSITION VARCHAR(10);
    DECLARE _POSITION_L INT;
    DECLARE _POSITION_R INT;
    DECLARE _POSITION_C INT;
    DECLARE _RACK_UNIT_HEIGHT INT DEFAULT -1;

    DECLARE _TEMP_RACK_INFO VARCHAR(50);
	
	DECLARE _CHECK_UNIT_VALUE INT DEFAULT 0;
	DECLARE _CHECK_LOOP_VALUE INT DEFAULT 1;
	DECLARE _UNIT_START VARCHAR(1) DEFAULT 'N';
	DECLARE _UNIT_END VARCHAR(1) DEFAULT 'N';

    
    DECLARE EXIT HANDLER FOR SQLEXCEPTION
        BEGIN
            ROLLBACK;
            DROP TEMPORARY TABLE IF EXISTS _TEMP_RACK_INFO;
            SET _RESULT = 'N';
            SET _ERROR_CD = 'NM20001';

            GET DIAGNOSTICS CONDITION 1 @P1 = MYSQL_ERRNO, @P2 = MESSAGE_TEXT;
            INSERT INTO NMS_DB.ERROR_DB(ERR_TYPE, ERR_DEST, ERR_CD, ERR_MSG)
            VALUES ('SP', 'SP_NMS_RACK_INFO', @P1, @P2);
        END;

    SET _TEMP_RACK_INFO = CONCAT('TEMP_RACK_INFO', DATE_FORMAT(NOW(),'%Y%m%d%H%i%s'));

    CREATE TEMPORARY TABLE _TEMP_RACK_INFO (
	    RACK_INDEX INTEGER NOT NULL AUTO_INCREMENT PRIMARY KEY,
	    UNIT_LABEL_INDEX INT,
	    UNIT_LABEL VARCHAR(10),
	    MOUNT_POSITION VARCHAR(10),
		UNIT_START CHAR(1) NOT NULL DEFAULT 'N' COMMENT '장비의 Unit 시작',
		UNIT_END CHAR(1) NOT NULL DEFAULT 'N' COMMENT '장비의 Unit 종료',
	    UNIT_POSITION_L INT,
	    UNIT_POSITION_R INT,
	    UNIT_POSITION_C INT
    );

    SELECT UNIT_CNT INTO _UNIT_CNT FROM RACK_OBJ WHERE RACK_OBJ_ID = _RACK_OBJ_ID;

    WHILE (_RACK_INDEX <= _UNIT_CNT) DO
        SELECT SUM(RACK_ROWSPAN),
               GROUP_CONCAT(POSITION ORDER BY RACK_MCH_ID ASC) AS POSITION,
               IFNULL(SUM(CASE WHEN POSITION = 'L' THEN RACK_ROWSPAN END), -1) AS POSITION_L,
               IFNULL(SUM(CASE WHEN POSITION = 'R' THEN RACK_ROWSPAN END), -1) AS POSITION_R,
               IFNULL(SUM(CASE WHEN POSITION = 'C' THEN RACK_ROWSPAN END), -1) AS POSITION_C
        INTO  _RACK_UNIT_HEIGHT, _MOUNT_POSITION, _POSITION_L, _POSITION_R, _POSITION_C
        FROM RACK_MCH_OBJ A
        JOIN V_MCH_INFO B ON A.MCH_ID = B.MCH_ID
        WHERE RACK_OBJ_ID = _RACK_OBJ_ID
        AND RACK_UNIT = _RACK_INDEX
        GROUP BY RACK_UNIT;
		
		IF (_POSITION_L > -1 AND _CHECK_UNIT_VALUE != _POSITION_L) THEN
            SET _UNIT_END = 'Y';
			SET _CHECK_UNIT_VALUE = _POSITION_L;
		ELSE
			SET _UNIT_END = 'N';
        END IF;
		
		IF (_POSITION_R > -1 AND _CHECK_UNIT_VALUE != _POSITION_R ) THEN
            SET _UNIT_END = 'Y';
			SET _CHECK_UNIT_VALUE = _POSITION_R;
		ELSE
			SET _UNIT_END = 'N';
        END IF;
		
		IF (_POSITION_C > -1 AND _CHECK_UNIT_VALUE != _POSITION_C ) THEN
            SET _UNIT_END = 'Y';
			SET _CHECK_UNIT_VALUE = _POSITION_C;
		ELSE
			SET _UNIT_END = 'N';
        END IF;
		
		
		IF (_CHECK_UNIT_VALUE <= _CHECK_LOOP_VALUE) THEN
			SET _UNIT_START = 'Y';
			SET _CHECK_LOOP_VALUE = 1;
			SET _CHECK_UNIT_VALUE = -1;
		ELSE
			SET _UNIT_START = 'N';
			SET _CHECK_LOOP_VALUE = _CHECK_LOOP_VALUE + 1;
		END IF;
		
        INSERT INTO _TEMP_RACK_INFO(RACK_INDEX, UNIT_LABEL_INDEX, UNIT_LABEL, MOUNT_POSITION, UNIT_START, UNIT_END,
									UNIT_POSITION_L, UNIT_POSITION_R, UNIT_POSITION_C)
        VALUES (_RACK_INDEX, _RACK_INDEX, CONCAT(_RACK_INDEX, 'U'), IFNULL(_MOUNT_POSITION,'N'), _UNIT_START, _UNIT_END, 
				IFNULL(_POSITION_L, -1), IFNULL(_POSITION_R,-1), IFNULL(_POSITION_C,-1));

        SET _MOUNT_POSITION = NULL;
        SET _POSITION_L = NULL;
        SET _POSITION_R = NULL;
        SET _POSITION_C = NULL;
		
        SET _RACK_INDEX = _RACK_INDEX + 1;
    END WHILE;

    CALL SP_NMS_RACK_MOUNT_HEIGHT_SET(_TEMP_RACK_INFO, @_RESULT, @_ERROR_CD);

    SELECT * FROM _TEMP_RACK_INFO ORDER BY RACK_INDEX DESC;
    SET _RESULT = 'Y';
    SET _ERROR_CD = '';

    DROP TEMPORARY TABLE _TEMP_RACK_INFO;
END ;;
DELIMITER ;
