    IN _RACK_OBJ_ID BIGINT,
    OUT _RESULT CHAR(1),
    OUT _ERROR_CD VARCHAR(256)
)
ERR_TRAN:
BEGIN
    DECLARE _UNIT_CNT INT DEFAULT 0;

    
    DECLARE EXIT HANDLER FOR SQLEXCEPTION
        BEGIN
            ROLLBACK;
            DROP TEMPORARY TABLE IF EXISTS _TEMP_RACK_INFO;
            SET _RESULT = 'N';
            SET _ERROR_CD = 'NM20001';

            
            GET DIAGNOSTICS CONDITION 1 @P1 = MYSQL_ERRNO, @P2 = MESSAGE_TEXT;
            INSERT INTO NMS_DB.ERROR_DB(ERR_TYPE, ERR_DEST, ERR_CD, ERR_MSG)
            VALUES ('SP', 'SP_NMS_RACK_MCH_INFO', @P1, @P2);
        END;

    SELECT UNIT_CNT INTO _UNIT_CNT FROM RACK_OBJ WHERE RACK_OBJ_ID = _RACK_OBJ_ID;

    SELECT RACK_UNIT,
           IF(RACK_ROWSPAN = 0 , 1, RACK_ROWSPAN) AS RACK_ROWSPAN,
           UNIT_POSITION_LABEL,
           POSITION,
           IF(POSITION='C', MCH_IP_C, CONCAT(IF(MCH_IP_L IS NULL, '-', MCH_IP_L), ' / ', IF(MCH_IP_R IS NULL, '-', MCH_IP_R))) AS MCH_IP,
           IF(POSITION='C', MCH_NM_C, CONCAT(IF(MCH_NM_L IS NULL, '-', MCH_NM_L), ' / ', IF(MCH_NM_R IS NULL, '-', MCH_NM_R))) AS MCH_NM,
           IF(POSITION='C', SYS_NAME_C, CONCAT(IF(SYS_NAME_L IS NULL, '-', SYS_NAME_L), ' / ', IF(SYS_NAME_R IS NULL, '-', SYS_NAME_R))) AS SYS_NAME,
           IF(POSITION='C', VENDOR_NM_C, CONCAT(IF(VENDOR_NM_L IS NULL, '-', VENDOR_NM_L), ' / ', IF(VENDOR_NM_R IS NULL, '-', VENDOR_NM_R))) AS VENDOR_NM,
           IF(POSITION='C', MODEL_NM_C, CONCAT(IF(MODEL_NM_L IS NULL, '-', MODEL_NM_L), ' / ', IF(MODEL_NM_R IS NULL, '-', MODEL_NM_R))) AS MODEL_NM
    FROM (
    SELECT A.RACK_UNIT,
           A.RACK_ROWSPAN,
           CASE WHEN POSITION = 'C' THEN CONCAT(RACK_UNIT, 'U')
           ELSE CONCAT(RACK_UNIT, 'U', '(',GROUP_CONCAT(A.POSITION ORDER BY RACK_MCH_ID ASC),')') END AS UNIT_POSITION_LABEL,
           GROUP_CONCAT(A.POSITION ORDER BY RACK_MCH_ID ASC) AS POSITION,
           GROUP_CONCAT(CASE WHEN A.POSITION = 'L' THEN MCH_IP END) AS MCH_IP_L,
           GROUP_CONCAT(CASE WHEN A.POSITION = 'L' THEN MCH_NM END) AS MCH_NM_L,
           GROUP_CONCAT(CASE WHEN A.POSITION = 'L' THEN SYS_NAME END) AS SYS_NAME_L,
           GROUP_CONCAT(CASE WHEN A.POSITION = 'L' THEN VENDOR_NM END) AS VENDOR_NM_L,
           GROUP_CONCAT(CASE WHEN A.POSITION = 'L' THEN MODEL_NM END) AS MODEL_NM_L,
           GROUP_CONCAT(CASE WHEN A.POSITION = 'R' THEN MCH_IP END) AS MCH_IP_R,
           GROUP_CONCAT(CASE WHEN A.POSITION = 'R' THEN MCH_NM END) AS MCH_NM_R,
           GROUP_CONCAT(CASE WHEN A.POSITION = 'R' THEN SYS_NAME END) AS SYS_NAME_R,
           GROUP_CONCAT(CASE WHEN A.POSITION = 'R' THEN VENDOR_NM END) AS VENDOR_NM_R,
           GROUP_CONCAT(CASE WHEN A.POSITION = 'R' THEN MODEL_NM END) AS MODEL_NM_R,
           GROUP_CONCAT(CASE WHEN A.POSITION = 'C' THEN MCH_IP END) AS MCH_IP_C,
           GROUP_CONCAT(CASE WHEN A.POSITION = 'C' THEN MCH_NM END) AS MCH_NM_C,
           GROUP_CONCAT(CASE WHEN A.POSITION = 'C' THEN SYS_NAME END) AS SYS_NAME_C,
           GROUP_CONCAT(CASE WHEN A.POSITION = 'C' THEN VENDOR_NM END) AS VENDOR_NM_C,
           GROUP_CONCAT(CASE WHEN A.POSITION = 'C' THEN MODEL_NM END) AS MODEL_NM_C
    FROM RACK_MCH_OBJ A
             JOIN V_MCH_INFO B ON A.MCH_ID = B.MCH_ID
    WHERE RACK_OBJ_ID = _RACK_OBJ_ID
    GROUP BY RACK_UNIT
    ORDER BY RACK_UNIT DESC
    ) T;
    SET _RESULT = 'Y';
    SET _ERROR_CD = '';
END ;;
