BEGIN

	DECLARE _RACK_INDEX INT DEFAULT 1;
	DECLARE _MOUNT_POSITION VARCHAR(10);
    DECLARE _POSITION_L INT;
    DECLARE _POSITION_R INT;
    DECLARE _POSITION_C INT;
	DECLARE _UNIT_LOOP_INDEX INT DEFAULT 1;

	DECLARE endOfRow  BOOLEAN DEFAULT FALSE;
    DECLARE cursor_l CURSOR FOR SELECT RACK_INDEX, MOUNT_POSITION, UNIT_POSITION_L FROM _TEMP_RACK_INFO WHERE UNIT_POSITION_L > 0;
    DECLARE cursor_r CURSOR FOR SELECT RACK_INDEX, MOUNT_POSITION, UNIT_POSITION_R FROM _TEMP_RACK_INFO WHERE UNIT_POSITION_R > 0;
    DECLARE cursor_c CURSOR FOR SELECT RACK_INDEX, MOUNT_POSITION, UNIT_POSITION_C FROM _TEMP_RACK_INFO WHERE UNIT_POSITION_C > 0;
    DECLARE CONTINUE HANDLER FOR NOT FOUND SET endOfRow = TRUE;

    
    DECLARE EXIT HANDLER FOR SQLEXCEPTION
        BEGIN
            ROLLBACK;
            DROP TEMPORARY TABLE IF EXISTS _TEMP_RACK_INFO;
            SET _RESULT = 'N';
            SET _ERROR_CD = 'NM20001';

            
            GET DIAGNOSTICS CONDITION 1 @P1 = MYSQL_ERRNO, @P2 = MESSAGE_TEXT;
            INSERT INTO NMS_DB.ERROR_DB(ERR_TYPE, ERR_DEST, ERR_CD, ERR_MSG)
            VALUES ('SP', 'SP_NMS_RACK_INFO', @P1, @P2);
        END;

	OPEN cursor_l;
    cursor_l_loop: LOOP
        FETCH cursor_l INTO _RACK_INDEX, _MOUNT_POSITION, _POSITION_L;

        IF endOfRow THEN
            LEAVE cursor_l_loop;
