)
ERR_TRAN : BEGIN

	 DECLARE _MCH_ID INT;
	 DECLARE _MCH_NM VARCHAR(50);
	 DECLARE _MCH_IP VARCHAR(20);
	 DECLARE _RACK_ROCATION VARCHAR(20);
	 DECLARE _RACK_UNIT INT;
