		SUBSTRING_INDEX(VAL_TEXT,'_',-1) AS RACK_SIZE
		FROM 	INVEN_MCH_INFO A
		INNER JOIN (
		SELECT 
		INVEN_ID AS DEST_ID,
		VAL_TEXT
		FROM INVEN_CSF_DATA WHERE INDICATOR_ID = 13 AND VAL_TEXT != ''
				) I ON I.DEST_ID=A.INVEN_MCH_ID
		WHERE A.INVEN_DEL_YN='N' AND (A.MCH_DEL_YN='Y' OR A.MCH_ID=0);
    DECLARE CONTINUE HANDLER FOR NOT FOUND SET done = TRUE;    
    DECLARE EXIT HANDLER FOR SQLEXCEPTION
    BEGIN
        ROLLBACK;
        SET _RESULT = 'N';
        SET _ERROR_CD = 'NM20001';

        
        GET DIAGNOSTICS CONDITION 1 @P1 = MYSQL_ERRNO, @P2 = MESSAGE_TEXT;        
        INSERT INTO NMS_DB.ERROR_DB(ERR_TYPE,ERR_DEST,ERR_CD,ERR_MSG) 
        VALUES('SP','SP_NMS_RACK_OBJ_SAVE',@P1,@P2);
    END;
	
	START TRANSACTION;
    
    OPEN cur;
    loop1: LOOP
        FETCH cur INTO _MCH_ID, _MCH_NM, _MCH_IP, _RACK_ROCATION, _RACK_UNIT, _RACK_SIZE;
        IF done THEN
            LEAVE loop1;
        END IF;
			
			SELECT RACK_OBJ_ID INTO _RACK_OBJ_ID FROM RACK_OBJ WHERE RACK_OBJ_NM = _RACK_ROCATION;
						
			IF _RACK_OBJ_ID IS NOT NULL THEN

				INSERT INTO RACK_MCH_OBJ (RACK_OBJ_ID, MCH_ID, RACK_UNIT, RACK_ROWSPAN, POSITION, REG_DT, UPD_DT, REG_ID, UPD_ID, TEXT_DISP_YN) VALUES
												 (_RACK_OBJ_ID, _MCH_ID, _RACK_UNIT, _RACK_SIZE, 'C', NOW(), NOW(), 'SYS', 'SYS', 'Y');
			END IF; 

    END LOOP;
    CLOSE cur;

    SET _RESULT = 'Y';
    SET _ERROR_CD = 'NM00000';

    COMMIT;

END ;;
