CREATE DEFINER=`root`@`localhost` PROCEDURE `SP_NMS_SPLIT`(IN `tempchar` longtext, IN `splitechar` char(1)
)
BEGIN






	DECLARE spliti int;
  DECLARE tempresult char(255);
  
  CREATE TEMPORARY TABLE IF NOT EXISTS temp_table (
	  splitestr char(255)
	);

  TRUNCATE TABLE temp_table;
    
	SET spliti =1; 
   
	WHILE spliti != 0 DO
  	SET tempresult = (SELECT FC_NMS_SPLIT_STR(tempchar,splitechar,spliti));				

  	IF tempresult != ''
  	THEN
  	  INSERT INTO temp_table
  		(
  		  splitestr
  		)
  		VALUES
      (
  			tempresult
  		);						

  		SET spliti = spliti+1;
  	ELSE
  		SET spliti = 0;
  	END IF;
	END WHILE;  

	SELECT * FROM temp_table WHERE splitestr is not null;       

  drop temporary table temp_table;
END ;;
