CREATE DEFINER=`root`@`localhost` PROCEDURE `SP_NMS_SVR_INFO_ADD`(
IN _SVR_IP VARCHAR(50), 
IN _SVR_TYPE INT        
)
BEGIN















  
  
  SELECT UPPER(IFNULL(LANG, 'ko')) INTO @LANG FROM CM_DB.VERSION_SOLUTION ORDER BY REG_DT DESC LIMIT 1;

  
  IF NOT EXISTS (SELECT SVR_ID FROM NMS_SVR_INFO WHERE SVR_IP = _SVR_IP) THEN

    SET @SVR_ID = 1;
    SET @MCH_ID = 0;
    SET @SVR_NM = '';
    SET @DB_SVR_FLAG = 0;   
    
    IF ( _SVR_TYPE = 1 OR _SVR_TYPE = 4 ) THEN  
      SELECT IFNULL(MAX(SVR_ID)+1, 1) INTO @SVR_ID FROM NMS_SVR_INFO WHERE SVR_ID < 20;
    ELSE 
      SELECT IFNULL(MAX(SVR_ID)+1, 20) INTO @SVR_ID FROM NMS_SVR_INFO WHERE SVR_ID >= 20;
    END IF;
    
    IF( _SVR_TYPE = 1 ) THEN 
      SET @SVR_GB = 0;
      IF( @LANG ='EN' ) THEN
        SET @SVR_NM = 'NMS Server';
      ELSE
        SET @SVR_NM = 'NMS 서버';
      END IF;
      
    ELSEIF( _SVR_TYPE = 2 ) THEN 
      SET @SVR_GB = 1;
      SET @DB_SVR_FLAG = 2;
      IF( @LANG ='EN' ) THEN
        SET @SVR_NM = 'DB Server';
      ELSE
        SET @SVR_NM = 'DB 서버';
      END IF;      
      
      
      SELECT COUNT(*) INTO @DB_CNT FROM NMS_SVR_INFO WHERE DB_SVR_FLAG = 1;
      IF( @DB_CNT = 0 ) THEN
        SET @DB_SVR_FLAG = 1;
      END IF;
      
    ELSEIF( _SVR_TYPE = 3 ) THEN 
      SET @SVR_GB = 3;
      IF( @LANG ='EN' ) THEN
        SET @SVR_NM = 'WAS Server';
      ELSE
        SET @SVR_NM = 'WAS 서버';
      END IF;      
      
    ELSEIF( _SVR_TYPE = 4 ) THEN 
      SET @SVR_GB = 2;
      IF( @LANG ='EN' ) THEN
        SET @SVR_NM = 'NMS Collect';
      ELSE
        SET @SVR_NM = 'NMS 수집 서버';
      END IF;      
      
    ELSEIF( _SVR_TYPE = 5 ) THEN 
      SET @SVR_GB = 4;
      IF( @LANG ='EN' ) THEN
        SET @SVR_NM = 'DB Server';
      ELSE
        SET @SVR_NM = 'Flow 수집 서버';
      END IF;
      
    END IF;    
        
    
    SELECT IFNULL(MCH_ID, 0) INTO @MCH_ID FROM MCH_INFO WHERE MCH_IP = _SVR_IP;
    
    IF ( @MCH_ID = 0 ) THEN
    
      IF @SVR_ID >= 20 THEN
        SET @NMS_SVR_ID = 1;  
      ELSE
        SET @NMS_SVR_ID = @SVR_ID;
      END IF;
      
      CALL SP_NMS_CONFMON_MCH_ADD(
      0,
      @NMS_SVR_ID,  
      _SVR_IP,  
      @SVR_NM,  
      1,        
      0,        
      0,        
      '1',      
      '',       
      '',       
      null,       
      '',       
      '',       
      '',       
      1,        
      0,        
      '',       
      0,        
      0,        
      @MCH_ID); 
      
      
      INSERT INTO NMS_DB.MCH_STATE (MCH_ID, ICMP_YN) VALUES( @MCH_ID, 'Y' );
      
    END IF;
        
    
      
    INSERT INTO NMS_SVR_INFO (SVR_ID, SVR_NM, MCH_ID, SVR_IP, SVR_GB, DB_SVR_FLAG) 
    VALUES(@SVR_ID, @SVR_NM, @MCH_ID, _SVR_IP, @SVR_GB, @DB_SVR_FLAG);
  
  END IF;
  
  
  
  IF NOT EXISTS (SELECT SVR_GRP_ID FROM NMS_SVR_GRP WHERE DEFAULT_YN = 'Y') THEN
    IF( @LANG ='EN' ) THEN
      INSERT INTO NMS_SVR_GRP (SVR_GRP_NM, SVR_GRP_DESC, DEFAULT_YN) VALUES('Default Group','Default Group', 'Y');
    ELSE
      INSERT INTO NMS_SVR_GRP (SVR_GRP_NM, SVR_GRP_DESC, DEFAULT_YN) VALUES('기본 수집그룹','기본 수집그룹', 'Y');
    END IF;
    
  END IF;

  SET @SVR_GRP_ID = 1;
  SET @SVR_ID = 0;
  
  SELECT SVR_GRP_ID INTO @SVR_GRP_ID FROM NMS_SVR_GRP WHERE DEFAULT_YN = 'Y';
  SELECT SVR_ID, SVR_GB INTO @SVR_ID, @SVR_GB FROM NMS_SVR_INFO WHERE SVR_IP = _SVR_IP;
  
  
  IF( @SVR_GB = 0 OR @SVR_GB = 2 ) THEN
  
    IF NOT EXISTS (SELECT SVR_GRP_ID FROM NMS_SVR_GRP_OBJ WHERE SVR_ID = @SVR_ID) THEN
      INSERT INTO NMS_SVR_GRP_OBJ (SVR_GRP_ID, SVR_ID, REG_ID) VALUES(@SVR_GRP_ID, @SVR_ID, 'm_user');
    END IF;
    
  END IF;
  
  
  SELECT SVR_ID FROM NMS_SVR_INFO WHERE SVR_IP = _SVR_IP;

END ;;
