CREATE DEFINER=`root`@`localhost` PROCEDURE `SP_NMS_SVR_INFO_CHG_IP`(
IN _OLD_IP VARCHAR(50),
IN _NEW_IP VARCHAR(50)
)
BEGIN





  SET @SVR_ID = 0;
  SET @MCH_ID = 0;
  
  SELECT SVR_ID, MCH_ID INTO @SVR_ID, @MCH_ID FROM NMS_SVR_INFO WHERE SVR_IP = _OLD_IP;
  
  
  IF @SVR_ID > 0 THEN
    UPDATE NMS_SVR_INFO SET SVR_IP = _NEW_IP, UPD_DT = NOW() WHERE SVR_ID = @SVR_ID;
  END IF;
  
  
  IF @MCH_ID > 0 THEN
    UPDATE MCH_IP_TABLE SET IP_ADDR = _NEW_IP, UPD_DT = NOW() WHERE MCH_ID = @MCH_ID AND IP_ADDR = _OLD_IP;
    UPDATE MCH_INFO SET MCH_IP = _NEW_IP, UPD_DT = NOW() WHERE MCH_ID = @MCH_ID;
  END IF;  
  
END ;;
