CREATE DEFINER=`root`@`localhost` PROCEDURE `SP_NMS_SVR_INFO_DEL`(
IN _SVR_ID INT,             
OUT _RESULT VARCHAR(256)    
)
ERR_TRAN : BEGIN











  IF NOT EXISTS (SELECT SVR_ID FROM NMS_SVR_INFO WHERE SVR_ID = _SVR_ID) THEN
    SET _RESULT = '수집 서버가 없습니다.';
    LEAVE ERR_TRAN;
  END IF;
  
  SET @MCH_ID = (SELECT MCH_ID FROM NMS_SVR_INFO WHERE SVR_ID = _SVR_ID);
    
  IF EXISTS (SELECT MCH_ID FROM MCH_COLLECT_POLICY WHERE NMS_SVR_ID = _SVR_ID AND MCH_ID != @MCH_ID) THEN
    SET _RESULT = '수집 서버를 사용하는 대상 장비가 있습니다.';
    LEAVE ERR_TRAN;
  END IF;
  
  DELETE FROM NMS_SVR_GRP_OBJ WHERE SVR_ID = _SVR_ID;
  DELETE FROM NMS_SVR_INFO WHERE SVR_ID = _SVR_ID;
  
  CALL SP_NMS_INVENTORY_MCH_DEL(@MCH_ID, @RESULT, @ERR_CD);
  
  SET _RESULT = '작업 완료';
  
END ;;
