CREATE DEFINER=`root`@`localhost` PROCEDURE `SP_NMS_TIMEZONE_CHG`(IN _TIMEZONE varchar(125))
    DETERMINISTIC
ERR_TRAN: BEGIN

  DECLARE done INT DEFAULT FALSE;
  DECLARE v_timezone varchar(125);
  DECLARE v_gmt_offset float(10,2);
  DECLARE cal_time float(10,2);
  DECLARE cursor1 CURSOR FOR  
  SELECT TIMEZONE, GMT_OFFSET  FROM timezone;

  DECLARE CONTINUE HANDLER FOR NOT FOUND SET done = TRUE;
  
 DECLARE EXIT HANDLER FOR SQLEXCEPTION
  BEGIN
    ROLLBACK;

    
    GET DIAGNOSTICS CONDITION 1 @P1 = MYSQL_ERRNO, @P2 = MESSAGE_TEXT;        
    INSERT INTO NMS_DB.ERROR_DB(ERR_TYPE,ERR_DEST,ERR_CD,ERR_MSG) 
    VALUES('SP','SP_NMS_TIMEZONE_CHG',@P1,@P2);
  END;

  START TRANSACTION;   
    
  SET @TIMEZONE = _TIMEZONE;
  
  SELECT @ST_TIME := GMT_OFFSET
  FROM timezone
  WHERE TIMEZONE = @TIMEZONE;

 
  OPEN cursor1;

  my_loop: LOOP
  
  FETCH cursor1
  INTO  
  v_timezone,
  v_gmt_offset;
  
  IF done THEN
        LEAVE my_loop;
  END IF;
  
  UPDATE timezone
  SET RAW_OFFSET = (@ST_TIME - v_gmt_offset)   
  WHERE TIMEZONE = v_timezone;
    
  END LOOP;
       
  COMMIT;
  
END ;;
