CREATE DEFINER=`root`@`localhost` PROCEDURE `SP_NMS_TOPOLOGY_COPY_MAP`(
















    IN  _MAP_ID INT,
    OUT _RESULT CHAR(1),
    OUT _ERROR_CD VARCHAR(256)
)
ERR_TRAN : BEGIN

    DECLARE EXIT HANDLER FOR SQLEXCEPTION
    BEGIN
        ROLLBACK;
        SET _RESULT = 'N';
        SET _ERROR_CD = 'NM20001';

        
        GET DIAGNOSTICS CONDITION 1 @P1 = MYSQL_ERRNO, @P2 = MESSAGE_TEXT;        
        INSERT INTO NMS_DB.ERROR_DB(ERR_TYPE,ERR_DEST,ERR_CD,ERR_MSG) 
        VALUES('SP','SP_NMS_TOPOLOGY_COPY_MAP',@P1,@P2);
    END;
    
    IF NOT EXISTS (SELECT * FROM MAP_INFO WHERE MAP_ID = _MAP_ID) THEN
        SET _RESULT = 'N';
        SET _ERROR_CD = 'NM20006';
        LEAVE ERR_TRAN;
    END IF;

    TRUNCATE TABLE MAP_NW_GRP_INFO_MAPPING;
    
    
    
    INSERT INTO MAP_NW_GRP_INFO (MAP_ID, UPPER_NW_GRP_ID, NW_GRP_NM, BACK_IMG_CD, REG_DT, UPD_DT, REF_ID)
    SELECT -99, UPPER_NW_GRP_ID, NW_GRP_NM, BACK_IMG_CD, NOW(), NOW(), NW_GRP_ID
    FROM MAP_NW_GRP_INFO
    WHERE MAP_ID = _MAP_ID;

    
    INSERT INTO MAP_NW_GRP_INFO_MAPPING (NW_GRP_ID, MAP_ID, UPPER_NW_GRP_ID )
    SELECT NW_GRP_ID, MAP_ID, UPPER_NW_GRP_ID
    FROM MAP_NW_GRP_INFO
    WHERE MAP_ID = _MAP_ID;

    
    UPDATE MAP_NW_GRP_INFO_MAPPING M
        JOIN MAP_NW_GRP_INFO S ON M.NW_GRP_ID = S.REF_ID
            AND S.MAP_ID = -99
    SET M.NEW_NW_GRP_ID = S.NW_GRP_ID;

    
    UPDATE MAP_NW_GRP_INFO S
        JOIN MAP_NW_GRP_INFO_MAPPING M ON S.UPPER_NW_GRP_ID <=> M.NW_GRP_ID
            AND S.MAP_ID = -99
    SET S.UPPER_NW_GRP_ID = M.NEW_NW_GRP_ID;


    
    
    
    
    
    
    IF EXISTS (
        SELECT * FROM MAP_NW_GRP_OBJ 
        WHERE NW_GRP_ID IN (
            SELECT NW_GRP_ID FROM MAP_NW_GRP_INFO WHERE MAP_ID = _MAP_ID
        )
    ) THEN
        
        
        INSERT INTO MAP_TEXT_PROP
            (FONT, FONT_COLOR, BOLD_YN, FONT_SIZE, DIRECTION, FILL_COLOR, FILL_COLOR_USE_YN, REF_ID)
        SELECT FONT, FONT_COLOR, BOLD_YN, FONT_SIZE, DIRECTION, FILL_COLOR, FILL_COLOR_USE_YN, TEXT_PROP_ID
        FROM MAP_TEXT_PROP
        WHERE TEXT_PROP_ID IN (
            SELECT TEXT_PROP_ID FROM MAP_NW_GRP_OBJ 
            WHERE NW_GRP_ID IN (
                SELECT NW_GRP_ID FROM MAP_NW_GRP_INFO WHERE MAP_ID = _MAP_ID
            )
        );

        
        INSERT INTO MAP_NW_GRP_OBJ
            (NW_GRP_ID, TEXT_PROP_ID, IMG_CD, POS_X, POS_Y, SCALE_X, SCALE_Y, LAYER_IDX, REG_DT, UPD_DT, REG_ID, UPD_ID, TEXT_DISP_YN)
        SELECT I.NW_GRP_ID, TEXT_PROP_ID, IMG_CD, POS_X, POS_Y, SCALE_X, SCALE_Y, LAYER_IDX, NOW(), NOW(), O.REG_ID, O.UPD_ID, TEXT_DISP_YN
        FROM MAP_NW_GRP_OBJ O JOIN MAP_NW_GRP_INFO I ON O.NW_GRP_ID = I.REF_ID
        WHERE O.NW_GRP_ID IN (SELECT NW_GRP_ID FROM MAP_NW_GRP_INFO WHERE MAP_ID = _MAP_ID);

        
        UPDATE MAP_NW_GRP_OBJ O
                JOIN MAP_TEXT_PROP T ON O.TEXT_PROP_ID = T.REF_ID 
                    AND O.NW_GRP_ID IN (SELECT NW_GRP_ID FROM MAP_NW_GRP_INFO WHERE MAP_ID = -99)
        SET O.TEXT_PROP_ID = T.TEXT_PROP_ID;
    END IF;

    
    
    
    
    
    
    
    IF EXISTS (
        SELECT * FROM MAP_FIGURE_OBJ 
        WHERE NW_GRP_ID IN (
            SELECT NW_GRP_ID FROM MAP_NW_GRP_INFO WHERE MAP_ID = _MAP_ID
        )
    ) THEN
        
        
        INSERT INTO MAP_LINE_PROP
            (LINE_COLOR, DASH_TYPE, THICK, REF_ID)
        SELECT LINE_COLOR, DASH_TYPE, THICK, LINE_PROP_ID
        FROM MAP_LINE_PROP
        WHERE LINE_PROP_ID IN (
            SELECT LINE_PROP_ID FROM MAP_FIGURE_OBJ WHERE NW_GRP_ID IN (
                SELECT NW_GRP_ID FROM MAP_NW_GRP_INFO WHERE MAP_ID = _MAP_ID
            )
        );

        
        
        INSERT INTO MAP_FIGURE_OBJ
            (NW_GRP_ID, LINE_PROP_ID, FIG_TYPE, FILL_COLOR, POS_X, POS_Y, SCALE_X, SCALE_Y, LAYER_IDX, REG_DT, UPD_DT, REG_ID, UPD_ID, REF_ID)
        SELECT I.NW_GRP_ID, LINE_PROP_ID, FIG_TYPE, FILL_COLOR, POS_X, POS_Y, SCALE_X, SCALE_Y, LAYER_IDX, NOW(), NOW(), O.REG_ID, O.UPD_ID, FIG_OBJ_ID
        FROM MAP_FIGURE_OBJ O JOIN MAP_NW_GRP_INFO I ON O.NW_GRP_ID = I.REF_ID
        WHERE O.NW_GRP_ID IN (
            SELECT NW_GRP_ID FROM MAP_NW_GRP_INFO WHERE MAP_ID = _MAP_ID
        );

        
        UPDATE MAP_FIGURE_OBJ O
            JOIN MAP_LINE_PROP L ON O.LINE_PROP_ID = L.REF_ID 
                AND O.NW_GRP_ID IN (SELECT NW_GRP_ID FROM MAP_NW_GRP_INFO WHERE MAP_ID = -99)
        SET O.LINE_PROP_ID = L.LINE_PROP_ID;

        
        
        IF EXISTS (
            SELECT * FROM MAP_RECT WHERE FIG_OBJ_ID IN (
                SELECT FIG_OBJ_ID FROM MAP_FIGURE_OBJ WHERE NW_GRP_ID IN (
                    SELECT NW_GRP_ID FROM MAP_NW_GRP_INFO WHERE MAP_ID = _MAP_ID
                )
            )
        ) THEN
            
            INSERT INTO MAP_RECT (FIG_OBJ_ID, WIDTH, HEIGHT) 
            SELECT O.FIG_OBJ_ID, WIDTH, HEIGHT 
            FROM MAP_RECT R JOIN MAP_FIGURE_OBJ O ON R.FIG_OBJ_ID = O.REF_ID
            WHERE R.FIG_OBJ_ID IN (
                SELECT FIG_OBJ_ID FROM MAP_FIGURE_OBJ 
                WHERE NW_GRP_ID IN (SELECT NW_GRP_ID FROM MAP_NW_GRP_INFO WHERE MAP_ID = _MAP_ID)
            );
        END IF;

        
        IF EXISTS (
            SELECT * FROM MAP_CIRCLE WHERE FIG_OBJ_ID IN (
                SELECT FIG_OBJ_ID FROM MAP_FIGURE_OBJ WHERE NW_GRP_ID IN (
                    SELECT NW_GRP_ID FROM MAP_NW_GRP_INFO WHERE MAP_ID = _MAP_ID
                )
            )
        ) THEN
            
            INSERT INTO MAP_CIRCLE (FIG_OBJ_ID, RADIUS) 
            SELECT O.FIG_OBJ_ID, RADIUS
            FROM MAP_CIRCLE C JOIN MAP_FIGURE_OBJ O ON C.FIG_OBJ_ID = O.REF_ID
            WHERE C.FIG_OBJ_ID IN (
                SELECT FIG_OBJ_ID FROM MAP_FIGURE_OBJ 
                WHERE NW_GRP_ID IN (SELECT NW_GRP_ID FROM MAP_NW_GRP_INFO WHERE MAP_ID = _MAP_ID)
            );
        END IF;

        
        IF EXISTS (
            SELECT * FROM MAP_ELLIPSE WHERE FIG_OBJ_ID IN (
                SELECT FIG_OBJ_ID FROM MAP_FIGURE_OBJ WHERE NW_GRP_ID IN (
                    SELECT NW_GRP_ID FROM MAP_NW_GRP_INFO WHERE MAP_ID = _MAP_ID
                )
            )
        ) THEN
            
            INSERT INTO MAP_ELLIPSE (FIG_OBJ_ID, RADIUS_X, RADIUS_Y) 
            SELECT O.FIG_OBJ_ID, RADIUS_X, RADIUS_Y
            FROM MAP_ELLIPSE E JOIN MAP_FIGURE_OBJ O ON E.FIG_OBJ_ID = O.REF_ID
            WHERE E.FIG_OBJ_ID IN (
                SELECT FIG_OBJ_ID FROM MAP_FIGURE_OBJ 
                WHERE NW_GRP_ID IN (SELECT NW_GRP_ID FROM MAP_NW_GRP_INFO WHERE MAP_ID = _MAP_ID)
            );
        END IF;
        
    END IF;

    
    
    
    IF EXISTS (
        SELECT * FROM MAP_MCH_OBJ 
        WHERE NW_GRP_ID IN (SELECT REF_ID FROM MAP_NW_GRP_INFO WHERE MAP_ID = -99)
    ) THEN
        
        
        INSERT INTO MAP_TEXT_PROP (FONT, FONT_COLOR, BOLD_YN, FONT_SIZE, DIRECTION, FILL_COLOR, FILL_COLOR_USE_YN, REF_ID)
        SELECT FONT, FONT_COLOR, BOLD_YN, FONT_SIZE, DIRECTION, FILL_COLOR, FILL_COLOR_USE_YN, TEXT_PROP_ID
        FROM MAP_TEXT_PROP WHERE TEXT_PROP_ID IN (
            SELECT TEXT_PROP_ID FROM MAP_MCH_OBJ 
            WHERE NW_GRP_ID IN (SELECT REF_ID FROM MAP_NW_GRP_INFO WHERE MAP_ID = -99)
        );

        
        
        
        INSERT INTO MAP_MCH_OBJ 
        (
            NW_GRP_ID, TEXT_PROP_ID, MCH_ID, ACTVN_YN, POS_X, POS_Y, SCALE_X, SCALE_Y, LAYER_IDX, 
            REG_DT, UPD_DT, REG_ID, UPD_ID, TEXT_DISP_YN, VIRTUAL_YN, VIRTUAL_NM, REF_ID
        )
        SELECT 
            I.NW_GRP_ID, MAX(T.TEXT_PROP_ID) AS TEXT_PROP_ID, MCH_ID, ACTVN_YN, POS_X, POS_Y, SCALE_X, SCALE_Y, LAYER_IDX, 
            NOW(), NOW(), O.REG_ID, O.UPD_ID, TEXT_DISP_YN, O.VIRTUAL_YN, VIRTUAL_NM, MCH_OBJ_ID 
        FROM MAP_MCH_OBJ O 
            JOIN MAP_NW_GRP_INFO I ON O.NW_GRP_ID = I.REF_ID
            JOIN MAP_TEXT_PROP T ON O.TEXT_PROP_ID = T.REF_ID
        WHERE O.NW_GRP_ID IN (SELECT REF_ID FROM MAP_NW_GRP_INFO WHERE MAP_ID = -99)
        GROUP BY MCH_OBJ_ID, I.NW_GRP_ID, MCH_ID, REG_ID;

    END IF;


    
    
    
    IF EXISTS (
        SELECT * FROM MAP_IMG_OBJ 
        WHERE NW_GRP_ID IN (SELECT REF_ID FROM MAP_NW_GRP_INFO WHERE MAP_ID = -99)
    ) THEN

        
        
        INSERT INTO MAP_TEXT_PROP (FONT, FONT_COLOR, BOLD_YN, FONT_SIZE, DIRECTION, FILL_COLOR, FILL_COLOR_USE_YN, REF_ID)
        SELECT FONT, FONT_COLOR, BOLD_YN, FONT_SIZE, DIRECTION, FILL_COLOR, FILL_COLOR_USE_YN, TEXT_PROP_ID
        FROM MAP_TEXT_PROP WHERE TEXT_PROP_ID IN (
            SELECT TEXT_PROP_ID FROM MAP_IMG_OBJ
            WHERE NW_GRP_ID IN (SELECT REF_ID FROM MAP_NW_GRP_INFO WHERE MAP_ID = -99)
        );

        
        
        INSERT INTO MAP_IMG_OBJ 
            (NW_GRP_ID, IMG_CD, POS_X, POS_Y, SCALE_X, SCALE_Y, LAYER_IDX, REG_DT, UPD_DT, REF_ID,TEXT_PROP_ID,GRAPHIC_NM,TEXT_DISP_YN)
        SELECT I.NW_GRP_ID, IMG_CD, POS_X, POS_Y, SCALE_X, SCALE_Y, LAYER_IDX, NOW(), NOW(), IMG_OBJ_ID, MAX(T.TEXT_PROP_ID) AS TEXT_PROP_ID,GRAPHIC_NM,TEXT_DISP_YN
        FROM MAP_IMG_OBJ O 
        JOIN MAP_NW_GRP_INFO I ON O.NW_GRP_ID = I.REF_ID
        JOIN MAP_TEXT_PROP T ON O.TEXT_PROP_ID = T.REF_ID
        WHERE O.NW_GRP_ID IN (SELECT REF_ID FROM MAP_NW_GRP_INFO WHERE MAP_ID = -99)
        GROUP BY IMG_OBJ_ID, I.NW_GRP_ID;

    END IF;


    
    
    
    IF EXISTS (
        SELECT * FROM MAP_TEXT_OBJ 
        WHERE NW_GRP_ID IN (SELECT REF_ID FROM MAP_NW_GRP_INFO WHERE MAP_ID = -99)
    ) THEN
        
        
        INSERT INTO MAP_TEXT_PROP
            (FONT, FONT_COLOR, BOLD_YN, FONT_SIZE, DIRECTION, FILL_COLOR, FILL_COLOR_USE_YN, REF_ID)
        SELECT 
            FONT, FONT_COLOR, BOLD_YN, FONT_SIZE, DIRECTION, FILL_COLOR, FILL_COLOR_USE_YN, TEXT_PROP_ID
        FROM MAP_TEXT_PROP WHERE TEXT_PROP_ID IN (
            SELECT TEXT_PROP_ID FROM MAP_TEXT_OBJ 
            WHERE NW_GRP_ID IN (SELECT REF_ID FROM MAP_NW_GRP_INFO WHERE MAP_ID = -99)
        );

        
        
        INSERT INTO MAP_TEXT_OBJ 
            (NW_GRP_ID, TEXT_PROP_ID, OBJ_TEXT, POS_X, POS_Y, SCALE_X, SCALE_Y, LAYER_IDX, REG_DT, UPD_DT, REF_ID)
        SELECT 
            I.NW_GRP_ID, P.TEXT_PROP_ID, OBJ_TEXT, POS_X, POS_Y, SCALE_X, SCALE_Y, LAYER_IDX, NOW(), NOW(), TEXT_OBJ_ID
        FROM MAP_TEXT_OBJ O 
            JOIN MAP_NW_GRP_INFO I ON O.NW_GRP_ID = I.REF_ID
            JOIN MAP_TEXT_PROP P ON O.TEXT_PROP_ID = P.REF_ID
        WHERE O.NW_GRP_ID IN (SELECT REF_ID FROM MAP_NW_GRP_INFO WHERE MAP_ID = -99);

    END IF;


    
    
    
    IF EXISTS (
        SELECT * FROM MAP_LINE_OBJ 
        WHERE NW_GRP_ID IN (SELECT REF_ID FROM MAP_NW_GRP_INFO WHERE MAP_ID = -99)
    ) THEN
        
        INSERT INTO MAP_LINE_PROP (LINE_COLOR, DASH_TYPE, THICK, REF_ID)
        SELECT LINE_COLOR, DASH_TYPE, THICK, LINE_PROP_ID 
        FROM MAP_LINE_PROP WHERE LINE_PROP_ID IN (
            SELECT LINE_PROP_ID FROM MAP_LINE_OBJ 
            WHERE NW_GRP_ID IN (SELECT REF_ID FROM MAP_NW_GRP_INFO WHERE MAP_ID = -99)
        );

        
        INSERT INTO MAP_LINE_OBJ (
            NW_GRP_ID, LINE_PROP_ID, LINE_TYPE, LINE_GB, START_OBJ_ID, END_OBJ_ID, START_RET_X, START_RET_Y, 
            END_RET_X, END_RET_Y, START_IF_ID, END_IF_ID, START_OBJ_TYPE, END_OBJ_TYPE, 
            TIP_BPS_X, TIP_BPS_Y, REF_ID, TIP_FONT, TIP_FONT_COLOR, TIP_FONT_SIZE, TIP_BOLD, TIP_BG_COLOR
        )
        SELECT 
            I.NW_GRP_ID, L.LINE_PROP_ID, LINE_TYPE, LINE_GB,
            FC_NMS_GET_NEW_OBJ_ID(START_OBJ_TYPE, START_OBJ_ID) AS START_OBJ_ID,
            FC_NMS_GET_NEW_OBJ_ID(END_OBJ_TYPE, END_OBJ_ID) AS END_OBJ_ID,
            START_RET_X, START_RET_Y, END_RET_X, END_RET_Y, START_IF_ID, END_IF_ID, 
            START_OBJ_TYPE, END_OBJ_TYPE, TIP_BPS_X, TIP_BPS_Y, O.LINE_ID, TIP_FONT, TIP_FONT_COLOR, TIP_FONT_SIZE, TIP_BOLD, TIP_BG_COLOR
        FROM MAP_LINE_OBJ O 
            JOIN MAP_NW_GRP_INFO I ON O.NW_GRP_ID = I.REF_ID
            JOIN MAP_LINE_PROP L ON O.LINE_PROP_ID = L.REF_ID
        WHERE 
            O.NW_GRP_ID IN (SELECT REF_ID FROM MAP_NW_GRP_INFO WHERE MAP_ID = -99)
            AND (
                FC_NMS_GET_NEW_OBJ_ID(START_OBJ_TYPE, START_OBJ_ID) IS NOT NULL 
                AND FC_NMS_GET_NEW_OBJ_ID(END_OBJ_TYPE, END_OBJ_ID) IS NOT NULL
            );
       
        
        
        INSERT INTO MAP_LINE_PT (LINE_ID, PT_ORDER, POS_X, POS_Y)
        SELECT O.LINE_ID, PT_ORDER, POS_X, POS_Y
        FROM MAP_LINE_OBJ O 
            JOIN MAP_LINE_PT P ON O.REF_ID = P.LINE_ID
        WHERE O.REF_ID IN (
            SELECT LINE_ID FROM MAP_LINE_OBJ 
            WHERE NW_GRP_ID IN (SELECT REF_ID FROM MAP_NW_GRP_INFO WHERE MAP_ID = -99) 
        );

    END IF;








    INSERT INTO MAP_INFO
        (CI_IMG_CD, MAP_NM, MAP_DESC, REG_DT, UPD_DT, TITLE, TITLE_FONT_COLOR, THEME_ID,BASIC_GB)
    SELECT CI_IMG_CD, MAP_NM, CONCAT('(COPY))', MAP_DESC), NOW(), NOW(), TITLE, TITLE_FONT_COLOR, THEME_ID, 'N'
    FROM MAP_INFO
    WHERE MAP_ID = _MAP_ID;

    UPDATE MAP_NW_GRP_INFO SET MAP_ID = LAST_INSERT_ID() WHERE MAP_ID = -99;
    
    UPDATE MAP_NW_GRP_INFO SET REF_ID = null where REF_ID > 0;
    UPDATE MAP_TEXT_PROP SET REF_ID = null where REF_ID > 0;
    UPDATE MAP_LINE_PROP SET REF_ID = null where REF_ID > 0;
    UPDATE MAP_FIGURE_OBJ SET REF_ID = null where REF_ID > 0;
    UPDATE MAP_MCH_OBJ SET REF_ID = null where REF_ID > 0;
    UPDATE MAP_IMG_OBJ SET REF_ID = null where REF_ID > 0;
    UPDATE MAP_TEXT_OBJ SET REF_ID = null where REF_ID > 0;
    UPDATE MAP_LINE_OBJ SET REF_ID = null where REF_ID > 0;
    
    TRUNCATE TABLE MAP_NW_GRP_INFO_MAPPING;

    SET _RESULT = 'Y';
    SET _ERROR_CD = 'NM00000';
    
    COMMIT;

END ;;
