CREATE DEFINER=`root`@`localhost` PROCEDURE `SP_NMS_TOPOLOGY_DEL_NETWORK`(
IN  _NW_GRP_ID INT,
OUT _RESULT CHAR(1),
OUT _ERROR_CD VARCHAR(256))
ERR_TRAN: BEGIN






  
  DECLARE done INT DEFAULT FALSE;
  DECLARE V_NW_GRP_ID INT;
  
  
  DECLARE C_NW_LIST CURSOR FOR WITH recursive USER_NW_GRP_EXT AS (
    SELECT NW_GRP_ID AS NW_GRP_ID
    FROM   MAP_NW_GRP_INFO
    WHERE  UPPER_NW_GRP_ID = _NW_GRP_ID
    UNION  ALL
    SELECT A.NW_GRP_ID  AS NW_GRP_ID 
    FROM   MAP_NW_GRP_INFO A JOIN USER_NW_GRP_EXT B ON A.UPPER_NW_GRP_ID = B.NW_GRP_ID
  )
  SELECT NW_GRP_ID
  FROM   USER_NW_GRP_EXT
  UNION  ALL
  SELECT _NW_GRP_ID AS NW_GRP_ID
  FROM   DUAL
  ;
  
  DECLARE EXIT HANDLER FOR SQLEXCEPTION
  BEGIN
    ROLLBACK;
    SET _RESULT = 'N';
      SET _ERROR_CD = 'NM20001';

      
    GET DIAGNOSTICS CONDITION 1 @P1 = MYSQL_ERRNO, @P2 = MESSAGE_TEXT;        
    INSERT INTO NMS_DB.ERROR_DB(ERR_TYPE,ERR_DEST,ERR_CD,ERR_MSG) 
    VALUES('SP','SP_NMS_TOPOLOGY_DEL_NETWORK',@P1,@P2);
  END;
  
  DECLARE CONTINUE HANDLER FOR NOT FOUND SET done = TRUE;

    
  OPEN C_NW_LIST;

  read_loop:LOOP
  
  FETCH C_NW_LIST INTO V_NW_GRP_ID;















    
    
    
    
    
    
    

    DELETE MLP
    FROM MAP_LINE_PT MLP
                JOIN MAP_LINE_OBJ MLO ON MLP.LINE_ID = MLO.LINE_ID
    WHERE NW_GRP_ID = V_NW_GRP_ID;

    
    
    
    
    
    

    DELETE MLP
    FROM MAP_LINE_PROP MLP
                JOIN MAP_LINE_OBJ MLO ON MLP.LINE_PROP_ID = MLO.LINE_PROP_ID
    WHERE NW_GRP_ID = V_NW_GRP_ID;
     
    DELETE FROM MAP_LINE_OBJ
    WHERE  NW_GRP_ID = V_NW_GRP_ID;
      
    
    
    
    
    
    
    

    DELETE MLP
    FROM MAP_LINE_PROP MLP
                JOIN MAP_FIGURE_OBJ MFO ON MLP.LINE_PROP_ID = MFO.LINE_PROP_ID
    WHERE NW_GRP_ID = V_NW_GRP_ID;
    
    
    
    
    
    
    

    DELETE ME
    FROM MAP_ELLIPSE ME
                JOIN MAP_FIGURE_OBJ MFO ON ME.FIG_OBJ_ID = MFO.FIG_OBJ_ID
    WHERE NW_GRP_ID = V_NW_GRP_ID;
    
    
    
    
    
    
    

    DELETE MR
    FROM MAP_RECT MR
                JOIN MAP_FIGURE_OBJ MFO ON MR.FIG_OBJ_ID = MFO.FIG_OBJ_ID
    WHERE NW_GRP_ID = V_NW_GRP_ID;
    
    
    
    
    
    
    

    DELETE MC
    FROM MAP_CIRCLE MC
                JOIN MAP_FIGURE_OBJ MFO ON MC.FIG_OBJ_ID = MFO.FIG_OBJ_ID
    WHERE NW_GRP_ID = V_NW_GRP_ID;
    
    DELETE FROM MAP_FIGURE_OBJ
    WHERE  NW_GRP_ID = V_NW_GRP_ID;
    
    
    
    
    
    
    
    

    DELETE MTP
    FROM MAP_TEXT_PROP MTP
                JOIN MAP_TEXT_OBJ MTO ON MTP.TEXT_PROP_ID = MTO.TEXT_PROP_ID
    WHERE NW_GRP_ID = V_NW_GRP_ID;
    
    DELETE FROM MAP_TEXT_OBJ
    WHERE  NW_GRP_ID = V_NW_GRP_ID;
     
    
    
    

    DELETE MTP
    FROM MAP_TEXT_PROP MTP
                JOIN MAP_IMG_OBJ MIO ON MTP.TEXT_PROP_ID = MIO.TEXT_PROP_ID
    WHERE NW_GRP_ID = V_NW_GRP_ID;
     
    
    
    
    
    
    
    

    DELETE MTP
    FROM MAP_TEXT_PROP MTP
                JOIN MAP_MCH_OBJ MMO ON MTP.TEXT_PROP_ID = MMO.TEXT_PROP_ID
    WHERE NW_GRP_ID = V_NW_GRP_ID;
    
    DELETE FROM MAP_MCH_OBJ
    WHERE  NW_GRP_ID = V_NW_GRP_ID;

    
    
    
    
    
    
    

    DELETE MTP
    FROM MAP_TEXT_PROP MTP
                JOIN MAP_NW_GRP_OBJ MNGO ON MTP.TEXT_PROP_ID = MNGO.TEXT_PROP_ID
    WHERE NW_GRP_ID = V_NW_GRP_ID;
    
    DELETE FROM MAP_NW_GRP_OBJ
    WHERE  NW_GRP_ID = V_NW_GRP_ID;

    
    DELETE FROM MAP_NW_GRP_INFO
    WHERE  NW_GRP_ID = V_NW_GRP_ID;
    
    
    IF done THEN
      LEAVE read_loop;
    END IF;
  END LOOP read_loop;
  
  CLOSE C_NW_LIST;


  COMMIT;
  
   SET _RESULT = 'Y';
   SET _ERROR_CD = '';
END ;;
DELIMITER ;
