CREATE DEFINER=`root`@`localhost` PROCEDURE `SP_NMS_TOPOLOGY_NETWORK_COPY_MAP`(















    IN  _NW_GRP_ID INT,
    OUT _RESULT CHAR(1),
    OUT _NEW_NW_GRP_ID INT,
    OUT _ERROR_CD VARCHAR(256)
)
ERR_TRAN : BEGIN

    DECLARE EXIT HANDLER FOR SQLEXCEPTION
    BEGIN
        ROLLBACK;
        SET _RESULT = 'N';
        SET _ERROR_CD = 'NM20001';

        
        GET DIAGNOSTICS CONDITION 1 @P1 = MYSQL_ERRNO, @P2 = MESSAGE_TEXT;        
        INSERT INTO NMS_DB.ERROR_DB(ERR_TYPE,ERR_DEST,ERR_CD,ERR_MSG) 
        VALUES('SP','SP_NMS_TOPOLOGY_NETWORK_COPY_MAP',@P1,@P2);
    END;
    

TRUNCATE TABLE MAP_NW_GRP_INFO_MAPPING;
SET @ORI_NW_GRP_ID = _NW_GRP_ID;
SET @COPY_NW_GRP_ID = 0;

INSERT INTO MAP_NW_GRP_INFO (MAP_ID, UPPER_NW_GRP_ID, NW_GRP_NM, BACK_IMG_CD, REG_DT, UPD_DT, REF_ID)
WITH RECURSIVE CTE_0 AS (		
	SELECT 
		NW_GRP_ID
	FROM MAP_NW_GRP_INFO
	WHERE UPPER_NW_GRP_ID = @ORI_NW_GRP_ID
	UNION ALL
	SELECT 
		F.NW_GRP_ID
	FROM MAP_NW_GRP_INFO F
	JOIN CTE_0 B ON F.UPPER_NW_GRP_ID = B.NW_GRP_ID 
)
SELECT -99, UPPER_NW_GRP_ID, CONCAT(NW_GRP_NM,'_COPY') AS NW_GRP_NM, BACK_IMG_CD, NOW(), NOW(), NW_GRP_ID
FROM MAP_NW_GRP_INFO WHERE NW_GRP_ID = @ORI_NW_GRP_ID
UNION ALL
SELECT -99, A.UPPER_NW_GRP_ID, A.NW_GRP_NM, A.BACK_IMG_CD, NOW(), NOW(), A.NW_GRP_ID
FROM MAP_NW_GRP_INFO A INNER JOIN CTE_0 B ON A.NW_GRP_ID = B.NW_GRP_ID;



INSERT INTO MAP_NW_GRP_INFO_MAPPING (NW_GRP_ID, MAP_ID, UPPER_NW_GRP_ID )
WITH RECURSIVE CTE_1 AS (		
	SELECT 
		NW_GRP_ID
	FROM MAP_NW_GRP_INFO
	WHERE UPPER_NW_GRP_ID = @ORI_NW_GRP_ID
	UNION ALL
	SELECT 
		F.NW_GRP_ID
	FROM MAP_NW_GRP_INFO F
	JOIN CTE_1 B ON F.UPPER_NW_GRP_ID = B.NW_GRP_ID 
)
SELECT NW_GRP_ID, MAP_ID, UPPER_NW_GRP_ID
FROM MAP_NW_GRP_INFO WHERE NW_GRP_ID = @ORI_NW_GRP_ID
UNION ALL
SELECT A.NW_GRP_ID, MAP_ID, UPPER_NW_GRP_ID
FROM MAP_NW_GRP_INFO A INNER JOIN CTE_1 B ON A.NW_GRP_ID = B.NW_GRP_ID
WHERE MAP_ID != -99;


UPDATE MAP_NW_GRP_INFO_MAPPING M
  JOIN MAP_NW_GRP_INFO S ON M.NW_GRP_ID = S.REF_ID
      AND S.MAP_ID = -99
SET M.NEW_NW_GRP_ID = S.NW_GRP_ID;



UPDATE MAP_NW_GRP_INFO S
  JOIN MAP_NW_GRP_INFO_MAPPING M ON S.UPPER_NW_GRP_ID <=> M.NW_GRP_ID
      AND S.MAP_ID = -99
SET S.UPPER_NW_GRP_ID = M.NEW_NW_GRP_ID;



IF EXISTS (
  SELECT * FROM MAP_NW_GRP_OBJ 
  WHERE NW_GRP_ID IN (
      SELECT NW_GRP_ID FROM MAP_NW_GRP_INFO 
		WHERE NW_GRP_ID IN (SELECT NW_GRP_ID FROM MAP_NW_GRP_INFO_MAPPING)
  )
) THEN
  
  
  INSERT INTO MAP_TEXT_PROP
      (FONT, FONT_COLOR, BOLD_YN, FONT_SIZE, DIRECTION, FILL_COLOR, FILL_COLOR_USE_YN, REF_ID)
  SELECT FONT, FONT_COLOR, BOLD_YN, FONT_SIZE, DIRECTION, FILL_COLOR, FILL_COLOR_USE_YN, TEXT_PROP_ID
  FROM MAP_TEXT_PROP
  WHERE TEXT_PROP_ID IN (
      SELECT TEXT_PROP_ID FROM MAP_NW_GRP_OBJ 
      WHERE NW_GRP_ID IN (
          SELECT NW_GRP_ID FROM MAP_NW_GRP_INFO_MAPPING
      )
  );

  
  INSERT INTO MAP_NW_GRP_OBJ
      (NW_GRP_ID, TEXT_PROP_ID, IMG_CD, POS_X, POS_Y, SCALE_X, SCALE_Y, LAYER_IDX, REG_DT, UPD_DT, REG_ID, UPD_ID, TEXT_DISP_YN)
  SELECT I.NW_GRP_ID, TEXT_PROP_ID, IMG_CD, POS_X, POS_Y, SCALE_X, SCALE_Y, LAYER_IDX, NOW(), NOW(), O.REG_ID, O.UPD_ID, TEXT_DISP_YN
  FROM MAP_NW_GRP_OBJ O JOIN MAP_NW_GRP_INFO I ON O.NW_GRP_ID = I.REF_ID
  WHERE O.NW_GRP_ID IN (SELECT NW_GRP_ID FROM MAP_NW_GRP_INFO_MAPPING);

  
  UPDATE MAP_NW_GRP_OBJ O
          JOIN MAP_TEXT_PROP T ON O.TEXT_PROP_ID = T.REF_ID 
              AND O.NW_GRP_ID IN (SELECT NW_GRP_ID FROM MAP_NW_GRP_INFO WHERE MAP_ID = -99)
  SET O.TEXT_PROP_ID = T.TEXT_PROP_ID;
END IF;










IF EXISTS (
  SELECT * FROM MAP_FIGURE_OBJ 
  WHERE NW_GRP_ID IN (
		SELECT NW_GRP_ID FROM MAP_NW_GRP_INFO_MAPPING
  )
) THEN
  
  
  INSERT INTO MAP_LINE_PROP
      (LINE_COLOR, DASH_TYPE, THICK, REF_ID)
  SELECT LINE_COLOR, DASH_TYPE, THICK, LINE_PROP_ID
  FROM MAP_LINE_PROP
  WHERE LINE_PROP_ID IN (
      SELECT LINE_PROP_ID FROM MAP_FIGURE_OBJ WHERE NW_GRP_ID IN (
          SELECT NW_GRP_ID FROM MAP_NW_GRP_INFO_MAPPING
      )
  );

  
  
  INSERT INTO MAP_FIGURE_OBJ
      (NW_GRP_ID, LINE_PROP_ID, FIG_TYPE, FILL_COLOR, POS_X, POS_Y, SCALE_X, SCALE_Y, LAYER_IDX, REG_DT, UPD_DT, REG_ID, UPD_ID, REF_ID)
  SELECT I.NW_GRP_ID, LINE_PROP_ID, FIG_TYPE, FILL_COLOR, POS_X, POS_Y, SCALE_X, SCALE_Y, LAYER_IDX, NOW(), NOW(), O.REG_ID, O.UPD_ID, FIG_OBJ_ID
  FROM MAP_FIGURE_OBJ O JOIN MAP_NW_GRP_INFO I ON O.NW_GRP_ID = I.REF_ID
  WHERE O.NW_GRP_ID IN (
      SELECT NW_GRP_ID FROM MAP_NW_GRP_INFO_MAPPING
  );

  
  UPDATE MAP_FIGURE_OBJ O
      JOIN MAP_LINE_PROP L ON O.LINE_PROP_ID = L.REF_ID 
          AND O.NW_GRP_ID IN (SELECT NW_GRP_ID FROM MAP_NW_GRP_INFO WHERE MAP_ID = -99)
  SET O.LINE_PROP_ID = L.LINE_PROP_ID;
   
  
  
  IF EXISTS (
      SELECT * FROM MAP_RECT WHERE FIG_OBJ_ID IN (
          SELECT FIG_OBJ_ID FROM MAP_FIGURE_OBJ WHERE NW_GRP_ID IN (
              SELECT NW_GRP_ID FROM MAP_NW_GRP_INFO_MAPPING
          )
      )
  ) THEN
      
      INSERT INTO MAP_RECT (FIG_OBJ_ID, WIDTH, HEIGHT) 
      SELECT O.FIG_OBJ_ID, WIDTH, HEIGHT 
      FROM MAP_RECT R JOIN MAP_FIGURE_OBJ O ON R.FIG_OBJ_ID = O.REF_ID
      WHERE R.FIG_OBJ_ID IN (
          SELECT FIG_OBJ_ID FROM MAP_FIGURE_OBJ 
          WHERE NW_GRP_ID IN (SELECT NW_GRP_ID FROM MAP_NW_GRP_INFO_MAPPING)
      );
  END IF;

	
	IF EXISTS (
	   SELECT * FROM MAP_CIRCLE WHERE FIG_OBJ_ID IN (
	       SELECT FIG_OBJ_ID FROM MAP_FIGURE_OBJ WHERE NW_GRP_ID IN (
	           SELECT NW_GRP_ID FROM MAP_NW_GRP_INFO_MAPPING
	       )
	   )
	) THEN
	   
	   INSERT INTO MAP_CIRCLE (FIG_OBJ_ID, RADIUS) 
	   SELECT O.FIG_OBJ_ID, RADIUS
	   FROM MAP_CIRCLE C JOIN MAP_FIGURE_OBJ O ON C.FIG_OBJ_ID = O.REF_ID
	   WHERE C.FIG_OBJ_ID IN (
	       SELECT FIG_OBJ_ID FROM MAP_FIGURE_OBJ 
	       WHERE NW_GRP_ID IN (SELECT NW_GRP_ID FROM MAP_NW_GRP_INFO_MAPPING)
	   );
	END IF;
	
	
	IF EXISTS (
	   SELECT * FROM MAP_ELLIPSE WHERE FIG_OBJ_ID IN (
	       SELECT FIG_OBJ_ID FROM MAP_FIGURE_OBJ WHERE NW_GRP_ID IN (
	           SELECT NW_GRP_ID FROM MAP_NW_GRP_INFO_MAPPING
	       )
	   )
	) THEN
	   
