CREATE DEFINER=`root`@`localhost` PROCEDURE `SP_NMS_TOP_VIEW_DEL_NETWORK`(
IN  _NW_GRP_ID INT,
OUT _RESULT CHAR(1),
OUT _ERROR_CD VARCHAR(256))
ERR_TRAN: BEGIN






  
  DECLARE done INT DEFAULT FALSE;
  DECLARE V_NW_GRP_ID INT;
  
  
  DECLARE C_NW_LIST CURSOR FOR WITH recursive USER_NW_GRP_EXT AS (
    SELECT NW_GRP_ID AS NW_GRP_ID
    FROM   TOP_VIEW_NW_GRP_INFO
    WHERE  UPPER_NW_GRP_ID = _NW_GRP_ID
    UNION  ALL
    SELECT A.NW_GRP_ID  AS NW_GRP_ID 
    FROM   TOP_VIEW_NW_GRP_INFO A JOIN USER_NW_GRP_EXT B ON A.UPPER_NW_GRP_ID = B.NW_GRP_ID
  )
  SELECT NW_GRP_ID
  FROM   USER_NW_GRP_EXT
  UNION  ALL
  SELECT _NW_GRP_ID AS NW_GRP_ID
  FROM   DUAL
  ;
  
  DECLARE EXIT HANDLER FOR SQLEXCEPTION
  BEGIN
    ROLLBACK;
    SET _RESULT = 'N';
      SET _ERROR_CD = 'NM20001';
  END;
  
  DECLARE CONTINUE HANDLER FOR NOT FOUND SET done = TRUE;

    
  OPEN C_NW_LIST;

  read_loop:LOOP
  
  FETCH C_NW_LIST INTO V_NW_GRP_ID;


    DELETE FROM TOP_VIEW_ELLIPSE
    WHERE  FIG_OBJ_ID IN (
      SELECT FIG_OBJ_ID
      FROM   TOP_VIEW_FIGURE_OBJ
      WHERE  NW_GRP_ID = V_NW_GRP_ID
    );
    
    DELETE FROM TOP_VIEW_RECT
    WHERE  FIG_OBJ_ID IN (
      SELECT FIG_OBJ_ID
      FROM   TOP_VIEW_FIGURE_OBJ
      WHERE  NW_GRP_ID = V_NW_GRP_ID
    );
    
    DELETE FROM TOP_VIEW_CIRCLE
    WHERE  FIG_OBJ_ID IN (
      SELECT FIG_OBJ_ID
      FROM   TOP_VIEW_FIGURE_OBJ
      WHERE  NW_GRP_ID = V_NW_GRP_ID
    );
    
    DELETE FROM TOP_VIEW_LINE_PROP
    WHERE  LINE_PROP_ID IN (
      SELECT LINE_PROP_ID
      FROM   TOP_VIEW_FIGURE_OBJ
      WHERE  NW_GRP_ID = V_NW_GRP_ID
    );
    
    DELETE FROM TOP_VIEW_FIGURE_OBJ
    WHERE  NW_GRP_ID = V_NW_GRP_ID;
    
    
    DELETE FROM TOP_VIEW_TEXT_PROP
    WHERE  TEXT_PROP_ID IN (
      SELECT TEXT_PROP_ID
      FROM   TOP_VIEW_TEXT_OBJ
      WHERE  NW_GRP_ID = V_NW_GRP_ID
    );
    
    DELETE FROM TOP_VIEW_TEXT_OBJ
    WHERE  NW_GRP_ID = V_NW_GRP_ID;
     
    
    DELETE FROM TOP_VIEW_IMG_OBJ
    WHERE  NW_GRP_ID = V_NW_GRP_ID;
     
      
    DELETE FROM TOP_VIEW_TEXT_PROP
    WHERE  TEXT_PROP_ID IN (
      SELECT TEXT_PROP_ID
      FROM   RACK_OBJ
      WHERE  NW_GRP_ID = V_NW_GRP_ID
    );
    
    DELETE FROM RACK_MCH_OBJ
    WHERE RACK_OBJ_ID IN (
	SELECT 	RACK_OBJ_ID FROM RACK_OBJ WHERE NW_GRP_ID = V_NW_GRP_ID
    );
    
    DELETE FROM RACK_DUMMY_OBJ
    WHERE RACK_OBJ_ID IN (
	SELECT 	RACK_OBJ_ID FROM RACK_OBJ WHERE NW_GRP_ID = V_NW_GRP_ID
    );

    DELETE FROM RACK_OBJ
    WHERE  NW_GRP_ID = V_NW_GRP_ID;
    
    
    DELETE FROM TOP_VIEW_BUILDING_FLOOR
    WHERE BUILDING_ID IN ( SELECT BUILDING_ID FROM TOP_VIEW_BUILDING_INFO WHERE NW_GRP_ID = V_NW_GRP_ID); 

    
    DELETE FROM TOP_VIEW_BUILDING_INFO
    WHERE  NW_GRP_ID = V_NW_GRP_ID;
    
    DELETE FROM TOP_VIEW_NW_GRP_INFO
    WHERE  NW_GRP_ID = V_NW_GRP_ID;

    
    IF done THEN
      LEAVE read_loop;
    END IF;
  END LOOP read_loop;
  
  CLOSE C_NW_LIST;


  COMMIT;
  
   SET _RESULT = 'Y';
   SET _ERROR_CD = '';
END ;;
